INSERT INTO `middle_audiobooks` (`id`, `title`, `description`, `audio_url`, `audio_url2`, `image_url`, `class`) VALUES
(1, 'Русские басни И.А.Крылов', 'Басни И. А. Крылова — стихотворные произведения, в которых часто в качестве главных героев выступают животные и растения. Автор использует образы животных, наделённых отдельными человеческими чертами: смелостью, трудолюбием, трусостью, добротой, красотой, мужеством и другими. ', 'https://nukadeti.ru/audio-basni/krylov', '', 'middleA1.jpg', '5'),
(2, '\"Спящая царевна\" - В.А.Жуковский', '«Спящая царевна» — сказка В. А. Жуковского, которая является поэтическим переложением известного «бродячего» фольклорного сюжета.   \n\nАннотация: в волшебной истории Жуковского о спящей красавице отражается вечная истина: даже в самые тёмные времена важно не сдаваться и верить в счастливый финал. Однако за пеленой волшебства скрывается и другая важная мысль: за каждым нашим действием следует расплата, и от ответственности не уйти.', 'https://mishka-knizhka.ru/audioskazki-dlya-detej/sbornik-audioskazok/sbornik-zarubezhnyh-audioskazok/spjashhaja-carevna-audio/', '', 'middleA2.jpg', '5'),
(3, '\"Сказка о мертвой царевне и о семи богатырях\" - А.С.Пушкин', '«Сказка о мёртвой царевне и семи богатырях» А. С. Пушкина повествует историю любви царевны и королевича Елисея.    \n\nМать царевны умерла сразу после родов, а отец женился на другой. Мачеха завидовала красоте девушки и решила её извести. Однако царевна встретила верных друзей. Благодаря семи богатырям, королевичу Елисею и счастливому случаю, царевна смогла преодолеть все козни мачехи и обрести счастье.', 'https://nukadeti.ru/audioskazki/pushkin_skazka_o_mjortvoj_carevne_i_o_semi_bogatyryakh', 'nukadeti.ru_-_skazka_o_mertvoj_carevne (1).mp3', 'middleA3.jpg', '5'),
(4, '\"Черная курица, или Подземные жители\" - А.Погорельский', '«Чёрная курица, или Подземные жители» — волшебная повесть для детей Антония Погорельского, написанная в 1829 году. Это первое авторское произведение литературы для детей на русском языке.    \n\nАннотация: сказка рассказывает о мальчике Алёше, который спас от смерти свою любимую курицу и благодаря этому поступку оказался в подземном городе. ', 'https://nukadeti.ru/audioskazki/pogorelskij_chernaya_kurica_ili_podzemnye_zhiteli', '', 'middleA4.jpg', '5'),
(5, '\"Заколдованное место\" - Н.В.Гоголь', '«Заколдованное место» — повесть Николая Гоголя, входящая в сборник «Вечера на хуторе близ Диканьки».  34\n\nАннотация: мистическая сказка о том, как простому сельскому жителю довелось встретиться с нечистой силой и стать её жертвой.', 'https://nukadeti.ru/audioskazki/zakoldovannoe-mesto', 'nukadeti.ru_-_zakoldovannoe-mesto.mp3', 'middleA5.jpg', '5'),
(6, '\"Муму\" - И.С.Тургенев', '«Муму» — рассказ Ивана Сергеевича Тургенева, написанный в 1852 году. Главная тема произведения — жизнь крепостных, во времена которых помещик распоряжался людьми по своему усмотрению, а у крепостных не было никаких прав.', 'https://nukadeti.ru/audiorasskazy/mumu', '', 'middleA6.jpg', '5'),
(7, '\"Кавказский пленник\" - Л.Н.Толстой', '«Кавказский пленник» — рассказ (иногда жанр этого произведения определяют как повесть или быль) Льва Толстого, повествующий о русском офицере в плену у горцев. Впервые опубликован в 1872 году в журнале «Заря». ', 'https://nukadeti.ru/audiorasskazy/tolstoj-kavkazskij-plennik', '', 'middleA7.jpg', '5'),
(8, '\"Хирургия\" - А.П.Чехов', '«Хирургия» — юмористический рассказ Антона Чехова, написанный в 1884 году.  12\n\nАннотация: фельдшер Курятин подменяет врача в земской больнице, к нему приходит дьячок с больным зубом. Справиться с зубом — не такая простая задача, как представлялось фельдшеру поначалу. ', 'https://nukadeti.ru/audiorasskazy/chekhov-khirurgiya', 'nukadeti.ru_-_chekhov-khirurgiya.mp3', 'middleA8.jpg', '5'),
(9, '\"В деревне\" - И.А.Бунин', '«В деревне» — рассказ И. А. Бунина о любви к малой родине, природе и простых человеческих радостях. \n\nАннотация:\nПриближение весны по-настоящему можно почувствовать только в деревне. Рассказчик вспоминает детство и каникулы, которые он проводил там, непременно замечая признаки наступления весны. Подобные воспоминания навсегда остаются в памяти и позволяют вновь и вновь переживать те далёкие ощущения, переноситься в прошлое. ', 'https://knigavuhe.org/book/v-derevne/', '', 'middleA9.jpg', '5'),
(10, '\"В дурном обществе\" - В.Г.Короленко', '«В дурном обществе» — повесть Владимира Короленко, написанная в 1881–1884 годах и впервые опубликованная в 1885 году.    \n\nАннотация: повесть посвящена процессу становления человека в детскую пору и проблемам его взаимоотношений с обществом. Произведение рассказывает о тяжёлой и полной лишений жизни сообщества нищих, о сиротстве и голоде, об относительности понятий «хорошо» и «плохо», о сочувствии и дружбе социально неравных людей.', 'https://nukadeti.ru/audiorasskazy/v-durnom-obshhestve', '', 'middleA10.jpg', '5'),
(11, '\"Медной горы хозяйка\" - П.П.Бажов', '«Хозяйка Медной горы» — сказ Павла Бажова из сборника «Малахитовая шкатулка», созданный на основе уральского фольклора.    \n\nАннотация: «Она может обернуться прекрасной женщиной, а может и шустрой ящеркой, она хранит в своих каменных чертогах невиданные красоты, может поведать мастеру все секреты мастерства». Это Хозяйка Медной горы, хранительница Уральских гор. Но вся красота гор не заменит ей человеческого тепла и любви. ', 'https://music.mts.ru/album/20074863', '', 'middleA11.jpg', '5'),
(12, '\"Тёплый хлеб\" - К.Г.Паустовский', '«Тёплый хлеб» — добрая и поучительная сказка Константина Паустовского, написанная в 1954 году.    \n\nАннотация: в произведении рассказывается о мальчике Фильке, который обидел раненого коня, за что пострадала вся деревня. Однако тяжёлые времена отступили, как только Филька понял и исправил свою оплошность. ', 'https://nukadeti.ru/audioskazki/paustovskij-teplyj-khleb', 'nukadeti.ru_-_paustovskij-teplyj-khleb.mp3', 'middleA12.jpg', '5'),
(13, '\"Заячьи лапы\" - К.Г.Паустовский', '«Заячьи лапы» — рассказ Константина Паустовского, написанный в 1937 году.      \n\nАннотация: трогательная сказка, в которой люди и животные протягивают друг другу руку помощи, и доброта, сопереживание всегда одерживают верх над равнодушием. ', 'https://nukadeti.ru/audioskazki/paustovskij-zayachi-lapy', '', 'middleA13.jpg', '5'),
(14, '\"Двенадцать месяцев\" - С.Я.Маршак', '«Двенадцать месяцев» — сказка Самуила Маршака, написанная в 1943 году.      \r\n\r\nАннотация: сказка о том, что нельзя изменить ход времени и каждому явлению в жизни полагается свой черёд. Чудеса случаются, если верить в них всем сердцем. ', 'https://nukadeti.ru/audioskazki/marshak_dvenadcat_mesyacev', 'nukadeti.ru_-_12_mesjacev.mp3', 'middleA14.jpg', '5'),
(15, '\"Никита\" - А.П.Платонов', '«Никита» — рассказ Андрея Платонова, написанный в 1945 году.     \r\n\r\nАннотация: история о маленьком мальчике, который с помощью своего воображения оживлял все предметы вокруг него. Детство Никиты пришлось на тяжёлые военные и послевоенные годы, когда взрослые были вынуждены либо воевать, либо много работать.  ', 'https://nukadeti.ru/audiorasskazy/platonov-nikita', 'nukadeti.ru_-_platonov-nikita.mp3', 'middleA15.jpg', '5'),
(16, '\"Васюткино озеро\" - В.П.Астафьев', '«Васюткино озеро» — рассказ Виктора Астафьева (1924–2001).     \r\n\r\nАннотация: история о мальчике Васютке, который заблудился в тайге и нашёл богатое рыбой озеро, названное потом его именем.  ', 'https://nukadeti.ru/audiorasskazy/vasyutkino-ozero', '', 'middleA16.jpg', '5'),
(17, '\"Робинзон Крузо\" - Д.Дефо', '«Жизнь и удивительные приключения Робинзона Крузо» — роман английского писателя Даниэля Дефо, написанный как вымышленная автобиография.    \r\n\r\nАннотация: герою романа, Робинзону Крузо, предстоит спастись в кораблекрушении и затем прожить в одиночестве на острове более 20 лет. За время своей жизни на острове он сталкивается с различными трудностями и опасностями как природного происхождения, так и исходящими от дикарей-каннибалов и пиратов.', 'https://akniga.org/defo-daniel-priklyucheniya-robinzona-kruzo-1', '', 'middleA17.jpg', '5'),
(18, '\"Снежная королева\" - Г.Х.Андерсен', '«Снежная королева» Ганса Христиана Андерсена — сказка о настоящей дружбе и о том, что никакие препятствия не страшны человеку с любящим сердцем.      \r\n\r\nАннотация: однажды в тихий уютный домик, где жили названные брат и сестра Кай и Герда со своей старой бабушкой, обманом проникает коварная и жестокая женщина — Снежная Королева. Она заманивает Кая в хитрую ловушку и уносит в своё королевство, где царит вечный холод.', 'https://nukadeti.ru/audioskazki/andersen_snezhnaya_koroleva', '', 'middleA18.jpg', '5'),
(19, '\"Приключения Тома Сойера\" - М.Твен', '«Приключения Тома Сойера» Марка Твена — роман о приключениях мальчика, растущего в небольшом американском городке Сент-Питерсберг в штате Миссури. Действие происходит до событий Гражданской войны в США.\r\n\r\nСюжет: Том Сойер — мальчик примерно двенадцатилетнего возраста, живёт у тёти Полли, сестры своей покойной матери. Он тяготеет к свободной жизни и приключениям, постоянно втягивая в них своего приятеля Гекльберри Финна. ', 'https://nukadeti.ru/audiorasskazy/priklyucheniya-toma-sojera', '', 'middleA19.jpg', '5'),
(20, '\"Сказание о Кише\" - Д.Лондон', '«Сказание о Кише» Джека Лондона — стилизованная под легенду история взросления мальчика из северного индейского племени.     \r\n\r\nАннотация: после смерти Бока, великого охотника на медведей, его жена и сын остались без кормильца и жили впроголодь. Другие мужчины племени не спешили делиться своей добычей с вдовой и её сыном. Но когда Киш подрос, он стал добывать мяса для всего племени, деля поровну долю каждого, тем самым доказав, что он такой же великий охотник, как и его отец. ', 'https://mishka-knizhka.ru/audio-rasskazy-dlya-detej/audio-rasskazy-dzheka-londona/skazanie-o-kishe-audio/', '', 'middleA20.jpg', '5'),
(21, '\"Арно\" - Э.Сетон-Томпсон', '«Арно» — рассказ Эрнеста Сетона-Томпсона о жизни почтового голубя. Произведение написано в жанре рассказа о животных и относится к циклу «Животные-герои».     \r\n\r\nАннотация: в рассказе рассказывается о «возвратных» голубях, которых сначала учат при помощи соревнований, а затем используют для доставки писем и других задач. Автор описывает культуру приручения таких голубей и возлагаемые на них надежды.', 'https://deti-online.com/audioskazki/seton-tompson/arno/', '', 'middleA21.jpg', '5'),
(22, '\"Умеешь ли ты слушать, Йоханна?\" - У.Старк', '«Умеешь ли ты свистеть, Йоханна?» — рассказ шведского детского писателя Ульфа Старка.         \r\n\r\nАннотация: у семилетнего Ульфа есть дедушка, а у его друга Берры — нет. Берра грустит, что никто не пригласит его в гости, не угостит кофе, не возьмёт порыбачить на озеро, не научит запускать воздушного змея и красиво свистеть. ', 'https://akniga.org/umeesh-li-ty-svistet-yohanna', '', 'middleA22.jpg', '5'),
(23, '\"Сказание о Белгородском киселе\"', '«Сказание о белгородском киселе» — отрывок из древнерусской летописи «Повесть временных лет» Нестора Летописца. Это рассказ о чудесном избавлении жителей Белгорода от осады печенегов.      \r\n\r\nМесто и время действия: события происходят в 997 году в Киевской Руси. ', 'https://knigavuhe.org/book/18577-skazanie-o-belgorodskom-kisele/', '', 'middleA23.jpg', '6'),
(24, '\"Дубровский\" - А.С,Пушкин', '«Дубровский» — произведение А. С. Пушкина, в котором главным героем выступает благородный разбойник-дворянин, похожий на Робин Гуда.    \r\n\r\nАннотация: богатый самодур Троекуров в пылу пустячной ссоры разорил соседа Дубровского. Старик не вынес удара и умер. Его сын решил отомстить обидчику, но у Троекурова была красавица-дочь.    \r\n\r\nОписание сюжета: предположительно действие романа происходит в 30-х годах XIX века, в двух соседних поместьях — Покровском и Кистенёвке, а также в их окрестностях. ', 'https://rutube.ru/play/embed/5981f41256aabdcb1db4b5aab3be3cf0', '', 'middleA24.jpg', '6'),
(25, '\"Бежин луг\" - И.С.Тургенев', '«Бежин луг» — рассказ Ивана Сергеевича Тургенева, входящий в цикл «Записки охотника».     \r\n\r\nАннотация: автор рассказывает о возвращении с охоты на тетеревов, о том, как он заблудился в лесу. События происходят в Тульской губернии. Охотник оказывается на Бежином лугу — невероятно красивом месте с нетронутой природой. Рассказчик встречает крестьянских мальчишек и, проведя ночь у их костра, с интересом слушает мистические истории. С рассветом охотник удаляется восвояси, а мальчики гонят табун домой. ', 'https://nukadeti.ru/audiorasskazy/bezhin-lug', '', 'middleA25.jpg', '6'),
(26, '\"Левша\" - Н.С.Лесков', '«Левша» — повесть-сказ Н. С. Лескова, написанная в 1881 году.    \r\n\r\nАннотация: история о простом русском человеке, талантливом мастере Левше, которому удалось доказать превосходство русских мастеров над заграничными и тем самым принести славу своему Отечеству. ', 'https://nukadeti.ru/audiorasskazy/leskov-levsha', '', 'middleA26.jpg', '6'),
(27, '\"Толстый и тонкий\" - А.П.Чехов', '«Толстый и тонкий» — сатирический рассказ Антона Павловича Чехова, написанный в 1883 году. Впервые опубликован юмористическим журналом «Осколки» 1 октября 1883 года.                           \r\n\r\nАннотация: автор высмеивает пороки, свойственные любому социуму: лицемерие и низкопоклонство перед чинами. ', 'https://nukadeti.ru/audiorasskazy/chekhov-tolstyj-i-tonkij', 'nukadeti.ru_-_tolstyi-i-tonkii.mp3', 'middleA27.jpg', '6'),
(28, '\"Чудесный доктор\" - А.И.Куприн', '«Чудесный доктор» — рассказ Александра Куприна, опубликованный в 1897 году.       \r\n\r\nАннотация: глава семьи Мерцаловых, доведённый до отчаяния, уже готов был свести счёты с жизнью, но встреча с «чудесным доктором» Николаем Ивановичем Пироговым, участие и своевременная помощь спасли жизнь ребёнка и положили конец бедам семьи Мерцаловых.', 'https://akniga.org/kuprin-aleksandr-chudesnyy-doktor-1', '', 'middleA28.jpg', '6'),
(29, '\"Алые паруса\" - А.Грин', '«Алые паруса» Александра Грина — повесть-феерия о возвышенной, всепобеждающей мечте, дающей силы жить и верить в лучшее. Это история о том, что истинно любящий человек способен сотворить чудо своими руками. ', 'https://deti-online.com/audioskazki/povesti-i-romany-mp3/alye-parusa/', '', 'middleA29.jpg', '6'),
(30, '\"Неизвестный цветок\" - А.П.Палтонов', '«Неизвестный цветок» — сказка-быль Андрея Платонова, написанная в 1950 году. Произведение заставляет задуматься о сложностях человеческой жизни и о важности помощи и заботы окружающих людей.      \r\n\r\nАннотация: история об удивительно стойком скромном цветке, которому удалось выжить и расцвести вопреки всем жизненным невзгодам. ', 'https://nukadeti.ru/audioskazki/platonov-neizvestnyj-cvetok', 'nukadeti.ru_-_platonov-neizvestnyj-cvetok.mp3', 'middleA30.jpg', '6'),
(31, '\"Конь с розовой гривой\" - В.П.Астафьев', '«Конь с розовой гривой» Виктора Астафьева — добрый, немного печальный, поучительный рассказ о маленьком мальчике-сироте, который совершил неблаговидный поступок, но вовремя осознал свою вину и искренне раскаялся. ', 'https://nukadeti.ru/audiorasskazy/kon-s-rozovoj-grivoj', '', 'middleA31.jpg', '6'),
(32, '\"Уроки фрнцузского\" - В.Г.Распутин', '«Уроки французского» — рассказ Валентина Распутина, впервые опубликованный в 1973 году.      \r\n\r\nАннотация: история о взаимоотношениях учительницы и ученика, благодаря которым мальчик получает жизненно важные уроки доброты, милосердия и человеческого участия. ', 'https://nukadeti.ru/audiorasskazy/uroki-francuzskogo', '', 'middleA32.jpg', '6'),
(33, '\"Критики\" - В.М.Шукшин', 'Аннотация к рассказу Василия Шукшина «Критики»:    \r\n\r\nПетька и его дед Тимофей обожали смотреть кино в сельском клубе, с жаром обсуждая сюжет каждого фильма после сеанса. Тимофей Макарыч, старый деревенский житель, легко отличал постановочную фальшь от правды и непоколебимо стоял на своей точке зрения.', 'https://nukadeti.ru/audiorasskazy/shukshin-kritiki', '', 'middleA33.jpg', '6'),
(34, '\"Тринадцатый подвиг Геракла\" - Ф.Искандер', '«Тринадцатый подвиг Геракла» — книга Фазиля Искандера, в которую вошли рассказ «Тринадцатый подвиг Геракла» и цикл рассказов о Чике.     \r\n\r\nАннотация:\r\n\r\nРассказ «Тринадцатый подвиг Геракла» рассказывает о пятикласснике, который не решил трудную задачу по математике, заданную на дом. Чтобы избежать насмешки учителя, он решился на обман. Учитель назвал поступок ученика подвигом трусости.', 'https://nukadeti.ru/audiorasskazy/trinadcatyj-podvig-gerakla', '', 'middleA34.jpg', '6'),
(35, '\"Илиада\" - Гомер', '«Илиада» — древнейший из сохранившихся памятников древнегреческой литературы, эпическая поэма, приписываемая Гомеру.', 'https://mishka-knizhka.ru/audioskazki-dlya-detej/sbornik-audioskazok/klassicheskie-shedevry-audioskazok/iliada-audio/', '', 'middleA35.jpg', '6'),
(36, '\"Одиссея\" - Гомер', '«Одиссея» — вторая после «Илиады» классическая поэма, приписываемая древнегреческому поэту Гомеру. Создана в VIII веке до н. э. или несколько позже.                  \r\n\r\nАннотация: «Одиссея» — эпическая поэма, которая рассказывает о приключениях мифического героя по имени Одиссей во время его возвращения на родину по окончании Троянской войны, а также о приключениях его жены Пенелопы, ожидавшей Одиссея на Итаке.', 'https://akniga.org/gomer-iliada-odisseya', '', 'middleA36.jpg', '6'),
(37, '\"Маленький принц\" - А.де Сент-Экзюпери', '«Маленький принц» — аллегорическая повесть-сказка Антуана де Сент-Экзюпери. Впервые опубликована 6 апреля 1943 года в Нью-Йорке.                     \r\n\r\nАннотация: «Все взрослые сначала были детьми, только мало кто из них об этом помнит». Маленький мальчик оказывается в мире взрослых, где больше никто не способен оценить любовь или поэзию. Это мир, где могущественные баобабы разрушают планету и мало кто знает, как быть по‑настоящему счастливым.  ', 'https://nukadeti.ru/audioskazki/antuan_de_sent_ehkzyuperi_malenkij_princ', '', 'middleA37.jpg', '6'),
(38, '\"Воцарение Ивана Грозного\"', '«Воцарение Ивана Грозного» — предание устного народного творчества, которое относится к жанру легенды. Произведение датируется XVII веком.     \r\n\r\nАннотация: предание рассказывает о необычном и мистическом способе избрания царя в древней Руси. В центре сюжета — судьба крепостного Ивана, ставшего правителем, и история исполнения обещания. ', 'https://mp3song.cc/mp3/воцарение-ивана-грозного.html', '', 'middleA38.jpg', '7'),
(39, '\"Сороки-ведьмы\"', '«Сороки-ведьмы» — предание о царствовании Ивана Грозного в период опричнины.    \r\n\r\nАннотация: история о том, как царь решил очистить Русь от ведьм и колдунов, обратив их хитрость против них самих. ', 'https://mishka-knizhka.ru/audioskazki-dlya-detej/russkie-narodnye-audioskazki/audio-predanija/soroki-vedmy-audio/', '', 'middleA39.jpg', '7'),
(40, '\"Пётр и плотник\"', '«Пётр и плотник» — русское народное предание.       \r\n\r\nАннотация: история о скромности и трудолюбии Петра Великого, который, скрыв свою царскую роль, работал бок о бок с простыми мастерами. ', 'https://audioskazki-online.ru/podborki/raznoe/pjotr-i-plotnik', '', 'middleA40.jpg', '7'),
(41, '\"Вольга и Микула Сергеевич\"', '«Вольга и Микула Селянинович» — старинная русская былина, впервые записанная в 1860 году.    \r\n\r\nАннотация: в произведении образ крестьянина-пахаря Микулы Селяниновича противопоставлен образу князя Вольги Святославовича, племянника киевского князя Владимира. Есть предположения, что в былине воссозданы образы не просто крестьянина и князя, а двух языческих богов: бога земледелия — Микулы и бога охоты — Вольги. ', 'https://nukadeti.ru/audioskazki/volga-i-mikula-selyaninovich', '', 'middleA41.jpg', '7'),
(42, '\"Садко\"', 'Былина «Садко» повествует об искусном гусляре из Новгорода, который повстречал Морского царя. За хорошую игру на гуслях царь помог Садко разбогатеть.', 'https://mishka-knizhka.ru/audioskazki-dlya-detej/russkie-narodnye-audioskazki/russkie-narodnye-volshebnye-audioskazki/sadko-audioskazka/', '', 'middleA42.jpg', '7'),
(43, '\"Песнь о Роланде\"', '«Песнь о Роланде» — одна из самых известных и значительных героических поэм в жанре старофранцузской жесты, древнейшее крупное произведение французской литературы.      \r\n\r\nАннотация: в основе поэмы лежит историческое событие, связанное с неудачным походом Карла Великого в Испанию в 778 году. Из-за предательства Ганелона бретонский граф Роланд, его верный друг Оливье, мудрый архиепископ Турпин в составе небольшого отряда-арьергарда попадают в засаду в Ронсевальском ущелье. Храбрые воины оказываются отрезанными от войска Карла и вынуждены вступить в кровавый бой с многократно превосходящими силами противников.', 'https://knigavuhe.org/book/pesn-o-rolande/', '', 'middleA43.jpg', '7'),
(44, '\"Похвала Ярославу и книгам\"', '«Похвала Ярославу и книгам» — фрагмент «Повести временных лет», в котором летописец Нестор рассказывает о делах Ярослава Мудрого.       \r\n\r\nАннотация: летописец хвалит Ярослава Мудрого за заботу о просвещении русского народа, о развитии культуры на Руси. ', 'https://mp3song.cc/mp3/из-похвалы-князю-ярославу-и-книгам.html', '', 'middleA44.jpg', '7'),
(45, '\"Поучение Владимира Мономаха\"', '«Поучение Владимира Мономаха» — один из самых значимых литературных памятников XII века, написанный великим князем киевским Владимиром Мономахом. Это ярчайший образец древнерусского красноречия, своеобразная светская проповедь правителя, ставшего первым светским писателем Руси.      \r\n\r\nАннотация: Мономах начал писать «Поучение» в эпоху губительных междоусобиц, поэтому смысл его сводится к необходимости объединения Руси. Князь даёт советы подрастающему поколению о том, как надлежит организовывать свою жизнь, каким правилам стоит следовать в тех или иных ситуациях, каких нравственных принципов придерживаться. ', 'https://my.mail.ru/music/songs/а-н-ужанков-поучение-владимира-мономаха-cc0a537cab2b90d0458f5a4aa445e43d', '', 'middleA45.jpg', '7'),
(46, 'Повесть о Петре и Февронии Муромских', '«Повесть о Петре и Февронии Муромских» — памятник древнерусской агиографической литературы, созданный в конце 1540-х годов Ермолаем-Еразмом на основе муромских устных преданий.        \r\n\r\nАннотация: произведение сочетает в себе жанровые признаки жития, сказки и исторической повести. Тема повести — жизнь и духовно-нравственный подвиг муромских святых. ', 'https://mishka-knizhka.ru/audio-rasskazy-dlya-detej/sbornik-audio-rasskazov/povest-o-petre-i-fevronii-muromskih-audio/', '', 'middleA46.jpg', '7'),
(47, '\"Медный всадник\" - А.С.Пушкин', '«Медный всадник» — последняя поэма Александра Сергеевича Пушкина, написанная в 1833 году. Произведение посвящено городу Санкт-Петербургу и является итогом размышлений автора о власти и личности Петра I.', 'https://akniga.org/pushkin-aleksandr-mednyy-vsadnik-3', '', 'middleA47.jpg', '7'),
(48, '\"Песнь о вещем Олеге\" - А.С.Пушкин', '«Песнь о вещем Олеге» — историческая баллада Александра Сергеевича Пушкина, написанная в 1822 году.      \r\n\r\nАннотация: произведение основано на легенде о смерти киевского князя Олега, записанной в «Повести временных лет». В основе сюжета — военный поход князя Олега против хазар, где автор прославляет его достижения и стремление сохранить и расширить земли Древней Руси.', 'https://nukadeti.ru/audioskazki/pesn-o-veshhem-olege', '', 'middleA48.jpg', '7'),
(49, '\"Борис Годунов\" - А.С.Пушкин', '«Борис Годунов» — историческая драма Александра Пушкина о годах правления русского царя Бориса Годунова, его свержении и приходе к власти самозванца Лжедмитрия.  \r\n\r\nВремя и место действия: Россия в конце XVI — начале XVII столетия. Произведение описывает период царствования Бориса Годунова с 1598 по 1605 год. ', 'https://akniga.org/pushkin-as-boris-godunov-mhat', '', 'middleA49.jpg', '7'),
(50, '\"Станционный смотритель\" - А.С.Пушкин', '«Станционный смотритель» — повесть А. С. Пушкина из цикла «Повестей покойного Ивана Петровича Белкина», написанная в 1830 году.      \r\n\r\nАннотация: в произведении описана драматическая история простой семьи, где центральная тема — трагедия простого человека, который теряет всё, что ему дорого.', 'https://akniga.org/pushkin-aleksandr-stancionnyy-smotritel-6', '', 'middleA50.jpg', '7'),
(51, '\"Тарас Бульба\" - Н.В.Гоголь', '«Тарас Бульба» — историческая повесть Николая Гоголя, написанная в 1835–1842 годах.     \r\n\r\nАннотация: произведение о любви и предательстве, о долге и чести, а также о том, ради чего настоящему казаку и жизнь не жалко отдать — о любви к своей родной земле.', 'https://akniga.org/gogol-nv-taras-bulba', '', 'middleA51.jpg', '7'),
(52, '\"Бирюк\" - С.И.Тургенев', '«Бирюк» — рассказ Ивана Тургенева из цикла «Записки охотника». Написан в 1847 году, впервые опубликован в 1848 году в журнале «Современник».     \r\n\r\nАннотация: произведение представляет собой глубокое исследование человеческой природы и социальной несправедливости. В центре рассказа — конфликт между обязанностью и человечностью. ', 'https://akniga.org/turgenev-ivan-biryuk', '', 'middleA52.jpg', '7'),
(53, '\"Русские женщины\" - Н.А.Некрасов', '«Русские женщины» — поэма Николая Некрасова, которая рассказывает о жёнах декабристов, последовавших за мужьями в Сибирь.     \r\n\r\nАннотация: произведение посвящено трагическому подвигу жён декабристов, которые отказались от комфорта и тепла, чтобы оказаться в суровом крае в ужасных условиях. ', 'https://knigavuhe.org/book/knjaginja-trubeckaja/', '', 'middleA53.jpg', '7'),
(54, '\"Князь Михайло Репнин\" - А.К.Толстой', '«Князь Михайло Репнин» — историческая баллада Алексея Константиновича Толстого, написанная в 1860-х годах.    \r\n\r\nАннотация: произведение основано на исторических событиях периода правления Ивана Грозного и отражает социальные и политические реалии того времени. ', 'https://audioskazki-online.ru/podborki/raznoe/knyaz-mikhajlo-repnin', '', 'middleA54.jpg', '7'),
(55, '\"Повесть о том, как один мужик двух генералов прокормил\" - М.Салтыков-Щедрин', '«Повесть о том, как один мужик двух генералов прокормил» — сатирическая сказка Михаила Салтыкова-Щедрина, в которой автор высмеивает пороки современного ему общества.', 'https://mishka-knizhka.ru/audioskazki-dlya-detej/russkie-audioskazki/audioskazki-saltykova-shchedrina/povest-o-tom-kak-odin-muzhik-dvuh-generalov-prokormil-audio/', '', 'middleA55.jpg', '7'),
(56, '\"Детство\" - Л.Н.Толстой', '«Детство» — первая часть автобиографической трилогии Л. Н. Толстого, в которой описаны важные вехи в развитии и переживания, характерные для большинства детей.    \r\n\r\nАннотация: Николеньке Иртеньеву недавно исполнилось десять лет, он совершенно счастлив, беззаботен и окружён любовью. Но, похоже, жизнь его вскоре переменится — ведь отец принял решение увезти мальчиков (Николеньку и старшего брата Володю) с собой в Москву для дальнейшего обучения.', 'https://mishka-knizhka.ru/audio-rasskazy-dlya-detej/audio-rasskazy-tolstogo-l-n/detstvo-audio/', '', 'middleA56.jpg', '7'),
(57, '\"История одного города\" - М.Е.Салтыков-Щедрин', '«История одного города» — сатирический роман-хроника Михаила Евграфовича Салтыкова-Щедрина, написанный в 1869–1870 годах.      \r\n\r\nАннотация: произведение представляет собой иносказательную историю российского государства. Действие романа происходит в вымышленном городе Глупове в период с 1731 по 1825 годы. В истории города прослеживается аналогия с историей Российской империи, включая правителей, государственные перевороты и прочие важные исторические события.', 'https://akniga.org/saltykov-schedrin-mihail-evgrafovich-istoriya-odnogo-goroda', '', 'middleA57.jpg', '8'),
(58, '\"После бала\" - Л.Н.Толстой', '«После бала» — рассказ Л. Н. Толстого, в котором описано, как одно событие способно оказать сильнейшее влияние на всю последующую жизнь человека.', 'https://akniga.org/tolstoy-lev-posle-bala-3', '', 'middleA58.jpg', '8'),
(59, '\"Старый гений\" - Н.С.Лесков', '«Старый гений» — рассказ Николая Лескова, написанный в 1884 году.     \r\n\r\nАннотация: мелкая помещица из далёкой провинции приезжает в столицу за правдой. Одолжила большую сумму денег одному франту, а теперь никак не может получить их обратно. ', 'https://akniga.org/leskov-nikolay-staryy-geniy-1', '', 'middleA59.jpg', '8'),
(60, '\"О любви\" - А.П.Чехов', 'Аннотация рассказа Антона Чехова «О любви»: любовь — это не всегда страсть и буря эмоций, не всегда ухаживания и свадьба. Иногда любовь — это тихо смотреть на любимого человека, вздыхать украдкой в одиночестве, не смея и боясь порушить счастье любимой. ', 'https://akniga.org/chehov-anton-o-lyubvi-5', '', 'middleA60.jpg', '8'),
(61, '\"Кавказ\" - И.А.Бунин', '«Кавказ» — рассказ Ивана Бунина, написанный в 1937 году и входящий в цикл «Тёмные аллеи».     \r\n\r\nАннотация: «Кавказ» — печальная, трагическая история обманутой любви, не пожелавшей мириться с ложью и предательством. В произведении затронуты вечные проблемы любви и ненависти, жизни и смерти, смысла жизни.', 'https://akniga.org/bunin-ivan-kavkaz-1', '', 'middleA61.jpg', '8'),
(62, '\"Куст сирени\" - А.И.Куприн', '«Куст сирени» — романтический рассказ Александра Куприна, написанный в 1894 году. Действие происходит в конце XIX века в Петербурге.      \r\n\r\nАннотация: история о настоящей любви, которая невозможна без жертвенности. Главные герои произведения, Николай Алмазов и Вера, на своём примере показывают, что любовь — великая, созидающая сила, способная преодолеть любые невзгоды.  ', 'https://akniga.org/kuprin-aleksandr-kust-sireni-2', '', 'middleA62.jpg', '8'),
(63, '\"Как я стал писателем\" - И.С.Шмелев', '«Как я стал писателем» — автобиографический рассказ Ивана Сергеевича Шмелёва, написанный в 1895 году. В нём автор рассказывает о своём пути к писательству, начиная с детства и заканчивая публикацией первого рассказа.     \r\n\r\nАннотация: рассказчик делится воспоминаниями о том, как он стал писателем. По его словам, всё произошло нечаянно, и ему казалось, что он всегда был писателем, хотя ещё не имел опубликованных работ.', 'https://my.mail.ru/music/songs/и-с-шмелёв-как-я-стал-писателем-1-925ab776629dcd2a793e1a7f9abfe7ce', '', 'middleA63.jpg', '8'),
(64, '\"Пенсне\" - М.А.Осоргин', '«Пенсне» — рассказ Михаила Осоргина, написанный в 1929 году во время эмиграции писателя.     \r\n\r\nАннотация: в произведении рассказывается о том, как привычные людям вещи способны жить собственной жизнью и даже покидать хозяев ради увлекательных путешествий. ', 'https://nukadeti.ru/audiorasskazy/pensne', '', 'middleA64.jpg', '8'),
(65, '\"Жизнь и воротник\" - Н.Тэффи', '«Жизнь и воротник» — юмористический поучительный рассказ Надежды Тэффи (Лохвицкой). Произведение написано в 1910 году, действие происходит в Москве в конце XIX — начале ХХ века.     \r\n\r\nАннотация: автор показывает, как внешний фактор — белый накрахмаленный воротничок — изменяет внутренний мир Олечки Розовой. Скромная и счастливая в браке девушка становится зависимой от моды, что разрушает её отношения с мужем и приводит к внутреннему кризису.', 'https://akniga.org/teffi-nadezhda-zhizn-i-vorotnik-4', '', 'middleA65.jpg', '8'),
(66, '\"История болезни\" - М.М.Зощенко', '«История болезни» Михаила Зощенко — сатирический рассказ о системе здравоохранения, где главный герой переживает ряд абсурдных и унизительных ситуаций в больнице.      \r\n\r\nАннотация: в произведении рассказывается о привередливом пациенте, который сталкивается с непрофессионализмом и недостатками медицинского обслуживания. ', 'https://nukadeti.ru/audioskazki/zoshhenko-istoriya-bolezni', '', 'middleA66.jpg', '8'),
(67, '\"Василий Теркин\" - А.Твардовский', '«Василий Тёркин. Книга про бойца» — поэма Александра Твардовского, одно из главных произведений в творчестве поэта, получившее всенародное признание.     \r\n\r\nАннотация: герой поэмы, Василий Тёркин, — собирательный образ русского солдата, олицетворяющего воина-победителя, которому присущи воинская отвага, умение воевать и не унывать ни при каких обстоятельствах. ', 'https://my.mail.ru/video/embed/4988871592550350027', '', 'middleA67.jpg', '8'),
(68, '\"Фотография, на которой меня нет\" - В.П.Астафьев', '«Фотография, на которой меня нет» — автобиографический рассказ Виктора Астафьева, который входит в книгу «Последний поклон».     \r\n\r\nАннотация: в основу произведения легла реальная история из жизни автора, где он всю жизнь бережно хранил школьную фотографию, несмотря на то что из-за болезни не смог прийти в школу фотографироваться.', 'https://nukadeti.ru/audiorasskazy/fotografiya-na-kotoroj-menya-net', '', 'middleA68.jpg', '8'),
(69, '\"Ромео и Джульетта\" - У.Шекспир', '«Ромео и Джульетта» — трагедия Уильяма Шекспира о трагической судьбе юных влюблённых.     \r\n\r\nАннотация: два семейства города Верона — Монтекки и Капулетти — враждуют между собой. Но Ромео Монтекки и Джульетта Капулетти полюбили друг друга и решили сочетаться браком. ', 'https://knigavuhe.org/book/romeo-i-dzhuletta-2/', '', 'middleA69.jpg', '8'),
(70, '\"Мещанин во дворянстве\" - Ж.Мольер', '«Мещанин во дворянстве» — комедия-балет в пяти актах Жана-Батиста Мольера, написанная в 1670 году.     \r\n\r\nАннотация: произведение соответствует правилам классицизма, включает в действие балет, что добавляет зрелищности. Автор создаёт пародийную атмосферу мещанского и дворянского быта с помощью простых изобразительных средств.', 'https://akniga.org/zhan-batist-moler-meschanin-vo-dvoryanstve', '', 'middleA70.jpg', '8'),
(71, '\"Айвенго\" - В.Скотт', '«Айвенго» Вальтера Скотта — классика приключенческой исторической литературы, в которой Средневековье предстаёт романтической эпохой легендарных сказаний о верности, доблести и чести, крестовых походах, прекрасных дамах и их рыцарях.', 'https://akniga.org/valter-skott-ayvengo', '', 'middleA71.jpg', '8'),
(72, '\"Шемякин суд\"', '«Шемякин суд» — древнерусская сказочная повесть, в которой в сатирическом ключе показана продажность суда и всех тех, кто наделён властью.     \r\n\r\nАннотация: в произведении высмеиваются судебные нормы и постановления, обличается продажность и лицемерие судей. Автор использует абсурдные ситуации и нелепые решения судьи Шемяки, чтобы подчеркнуть проблемы правосудия того времени. ', 'https://nukadeti.ru/audioskazki/shemyakin_sud', '', 'middleA72.jpg', '8'),
(73, '\"Недоросль\" - Д.Фонвизин', '«Недоросль» Дениса Фонвизина — комедия, которая стала классикой русской литературы. В ней автор создал образы помещиков-крепостников, Скотининых, Простаковых и невежественного недоросля Митрофанушки. Эти имена стали нарицательными, а образы помещиков и их невежественность актуальны и сегодня.', 'https://smotrim.ru/audio/2682498', '', 'middleA73.jpg', '8'),
(74, '\"Капитанская дочка\" - А.С.Пушкин', '«Капитанская дочка» — исторический роман Александра Сергеевича Пушкина, посвящённый событиям Крестьянской войны 1773–1775 годов под предводительством Емельяна Пугачёва.     \r\n\r\nАннотация: «Ни война, ни голод, ни забывшийся в самолюбии самозванец не способны остановить горячее чувство любви». Молодой офицер Пётр Андреевич Гринёв готов пойти на риск, чтобы спасти любимую Марию.', 'https://akniga.org/pushkin-aleksandr-kapitanskaya-dochka', '', 'middleA74.jpg', '8'),
(75, '\"Шинель\" - Н.В.Гоголь', '«Шинель» — повесть Николая Гоголя, которая входит в цикл «Петербургские повести».     \r\n\r\nАннотация: произведение рассказывает о жизни «маленького человека», чиновника Акакия Акакиевича Башмачкина. Он замкнутый человек, который беспрекословно терпит издевательства сослуживцев и тяжёлые условия жизни. ', 'https://akniga.org/gogol-nikolay-shinel-5', '', 'middleA75.jpg', '8'),
(76, '\"Ревизор\" - Н.В.Гоголь', '«Ревизор» — комедия в пяти действиях Николая Васильевича Гоголя, написанная в 1835 году (окончательные правки автор внёс в 1842 году).     \r\n\r\nАннотация: «Ревизор» — образец русской сатиры, в котором Гоголь решил «собрать в одну кучу всё дурное в России и за одним разом посмеяться над всем». Безымянный уездный город, в котором разворачивается действие, стал моделью всей России, и на сцену вышли все типы русской действительности, все характеры русского национального духа, все душевные движения и все пороки, присущие человеку.', 'https://deti-online.com/audioknigi/revizor/', '', 'middleA76.jpg', '8'),
(77, '\"Слово о полку Игореве\"', '«Слово о полку Игореве» — выдающееся произведение древнерусской поэзии, рассказывающее о неудачном походе новгород-северского князя Игоря Святославича против половцев в 1185 году.       \r\n\r\nАннотация: неизвестный автор призывает русских князей прекратить усобицы и объединиться для совместной защиты Русской земли.', 'https://nukadeti.ru/audioskazki/slovo-o-polku-igoreve', '', 'middleA77.jpg', '9'),
(78, '\"Бедная Лиза\" - Н.М.Карамзин', '«Бедная Лиза» — сентиментальная повесть Николая Михайловича Карамзина, написанная в 1792 году.     \r\n\r\nАннотация: история о роковой любви доверчивой крестьянской девушки Лизы и богатого дворянина Эраста. Автор впечатляюще передаёт, как всепоглощающая страсть постепенно оборачивается ложью, холодностью и приводит к трагическому финалу. ', 'https://akniga.org/karamzin-nikolay-bednaya-liza', '', 'middleA78.jpg', '9'),
(79, '\"Горе от ума\" - А.С.Грибоедов', '«Горе от ума» — комедия в стихах А. С. Грибоедова, сатира на аристократическое московское общество первой половины XIX века. Произведение сделало своего создателя классиком русской литературы. ', 'https://akniga.org/griboedov-aleksandr-gore-ot-uma-spektakl', '', 'middleA79.jpg', '9'),
(80, '\"Евгений Онегин\" - А.С.Пушкин', '«Евгений Онегин» — роман в стихах Александра Сергеевича Пушкина, начат 9 мая 1823 года и закончен 5 октября 1831 года.     \r\n\r\nАннотация: по меткому выражению автора, «Евгений Онегин» показывает «жизнь во всей её прозаической действительности». Это произведение Пушкина обрело мировую славу и считается образцом изящной словесности, а также «энциклопедией русской жизни» первой четверти XIX века. ', 'https://rutube.ru/play/embed/7c5c9d57a3900d824f970df043109f2a', '', 'middleA80.jpg', '9'),
(81, '\"Герой нашего времени\" - М.Ю.Лермонтов', '«Герой нашего времени» Михаила Лермонтова — лирико-психологический роман, который считается первым в русской прозе.       \r\n\r\nАннотация: в основе повествования — история жизни главного героя, Григория Печорина. Произведение состоит из пяти частей, расположенных не в хронологическом порядке. Автор постепенно погружает читателя во внутренний мир героя, циника, прожигателя жизни, противоречивого и неоднозначного. ', 'https://akniga.org/lermontov-mihail-geroy-nashego-vremeni-3', '', 'middleA81.jpg', '9'),
(82, '\"Мертвые души\" - Н.В.Гоголь', '«Мёртвые души» Николая Гоголя — одно из самых масштабных и значимых произведений русской литературы, пример русского реализма.     \r\n\r\nАннотация: автор описывает пороки и слабости русского человека, живо и правдиво отражает важнейшие для России проблемы. В произведении нет ни одного положительного персонажа: помещики преувеличенно смешны и уродливы, простые люди забиты и глупы.', 'https://akniga.org/gogol-nikolay-mertvye-dushi-2', '', 'middleA82.jpg', '9'),
(83, '\"Белые ночи\" - Ф.М.Достоевский', '«Белые ночи» — одна из ранних повестей Фёдора Достоевского, по мнению критика Аполлона Григорьева, одно из лучших произведений школы «сентиментального натурализма».     \r\n\r\nАннотация: Санкт-Петербург, лето, белые ночи. Одинокий человек совершает привычную прогулку и не находит знакомые лица. Петербург пустеет — господа разъезжаются по дачам. Предаваясь грустным мыслям и мечтам, главный герой неожиданно встречает девушку. Так в повествовании появляется Настенька. Молодые люди сближаются. Они встречаются белыми ночами, беседуют, затрагивая историю своей жизни. ', 'https://radiovera.ru/belyie-nochi-f-m-dostoevskiy.html', '', 'middleA83.jpg', '9');
INSERT INTO `middle_movies` (`id`, `title`, `description`, `video_url`, `image_url`, `class`) VALUES
(1, '\"Черная курица, или Подземные жители\" - А.Погорельский', '«Чёрная курица, или Подземные жители» — волшебная повесть для детей Антония Погорельского, написанная в 1829 году. Это первое авторское произведение литературы для детей на русском языке.    \n\nАннотация: сказка рассказывает о мальчике Алёше, который спас от смерти свою любимую курицу и благодаря этому поступку оказался в подземном городе. ', 'https://my.mail.ru/video/embed/765627630463484429', 'middleM1.jpg', '5'),
(2, '\"Муму\" - И.С.Тургенев', '«Муму» — рассказ Ивана Сергеевича Тургенева, написанный в 1852 году. Главная тема произведения — жизнь крепостных, во времена которых помещик распоряжался людьми по своему усмотрению, а у крепостных не было никаких прав.', 'https://vk.com/video_ext.php?oid=-126048376&id=456239420&hash=248f1c97ddfcb16e', 'middleM2.jpg', '5'),
(3, '\"Кавказский пленник\" - Л.Н.Толстой', '«Кавказский пленник» — рассказ (иногда жанр этого произведения определяют как повесть или быль) Льва Толстого, повествующий о русском офицере в плену у горцев. Впервые опубликован в 1872 году в журнале «Заря». ', 'https://vk.com/video_ext.php?oid=-107000463&id=456239397&hash=fbf24285fd5bb8f1', 'middleM3.jpg', '5'),
(4, '\"В дурном обществе\" - В.Г.Короленко', '«В дурном обществе» — повесть Владимира Короленко, написанная в 1881–1884 годах и впервые опубликованная в 1885 году.    \n\nАннотация: повесть посвящена процессу становления человека в детскую пору и проблемам его взаимоотношений с обществом. Произведение рассказывает о тяжёлой и полной лишений жизни сообщества нищих, о сиротстве и голоде, об относительности понятий «хорошо» и «плохо», о сочувствии и дружбе социально неравных людей.', 'https://vk.com/video_ext.php?oid=246993784&id=170898783&hash=0cf99c05f9b8371c', 'middleM4.jpg', '5'),
(5, '\"Тёплый хлеб\" - К.Г.Паустовский', '«Тёплый хлеб» — добрая и поучительная сказка Константина Паустовского, написанная в 1954 году.    \n\nАннотация: в произведении рассказывается о мальчике Фильке, который обидел раненого коня, за что пострадала вся деревня. Однако тяжёлые времена отступили, как только Филька понял и исправил свою оплошность. ', 'https://vk.com/video_ext.php?oid=392004026&id=456243575&hash=586cebbd0549aed6', 'middleM5.jpg', '5'),
(6, '\"Двенадцать месяцев\" - С.Я.Маршак', '«Двенадцать месяцев» — сказка Самуила Маршака, написанная в 1943 году.      \n\nАннотация: сказка о том, что нельзя изменить ход времени и каждому явлению в жизни полагается свой черёд. Чудеса случаются, если верить в них всем сердцем. ', 'https://rutube.ru/play/embed/f34fecb946672acad184480042af0d82', 'middleM6.jpg', '5'),
(7, '\"Жизнь и удивительные приключения Робинзона Крузо\" - Д.Дефо', '«Жизнь и удивительные приключения Робинзона Крузо» — роман английского писателя Даниэля Дефо, написанный как вымышленная автобиография.    \n\nАннотация: герою романа, Робинзону Крузо, предстоит спастись в кораблекрушении и затем прожить в одиночестве на острове более 20 лет. За время своей жизни на острове он сталкивается с различными трудностями и опасностями как природного происхождения, так и исходящими от дикарей-каннибалов и пиратов.', 'https://vk.com/video_ext.php?oid=-178765883&id=456243050&hash=badd1717f0e3c30f', 'middleM7.jpg', '5'),
(8, '\"Снежная королева\" - Г.Х.Андерсен', '«Снежная королева» Ганса Христиана Андерсена — сказка о настоящей дружбе и о том, что никакие препятствия не страшны человеку с любящим сердцем.      \n\nАннотация: однажды в тихий уютный домик, где жили названные брат и сестра Кай и Герда со своей старой бабушкой, обманом проникает коварная и жестокая женщина — Снежная Королева. Она заманивает Кая в хитрую ловушку и уносит в своё королевство, где царит вечный холод.', 'https://vk.com/video_ext.php?oid=896133275&id=456239779&hash=37eeb88bde5ba51d', 'middleM8.jpg', '5'),
(9, '\"Приключения Тома Сойера и Гекльберри Фина\" - М.Твен', '«Приключения Тома Сойера» Марка Твена — роман о приключениях мальчика, растущего в небольшом американском городке Сент-Питерсберг в штате Миссури. Действие происходит до событий Гражданской войны в США.\n\nСюжет: Том Сойер — мальчик примерно двенадцатилетнего возраста, живёт у тёти Полли, сестры своей покойной матери. Он тяготеет к свободной жизни и приключениям, постоянно втягивая в них своего приятеля Гекльберри Финна. ', 'https://vk.com/video_ext.php?oid=389888742&id=456239652&hash=840ede732e90decc', 'middleM9.jpg', '5'),
(10, '\"Рыба-кит\" - Ю.Ким', '«Рыба-кит» — шуточное стихотворение Юлия Кима, написанное в 1959 году.      \n\nСюжет: рыба-кит очень добрый и безобидный, но все обитатели моря его боятся из-за больших размеров. Поэтому он чувствует себя одиноким и несчастным. Однажды рыба-меч рассказывает киту о брате, с которым они однажды разлучились. Кит отправляется в далёкое плавание, чтобы разыскать своего брата.', 'https://vk.com/video_ext.php?oid=-19890251&id=456247351&hash=dcb0fd445adf4fc3', 'middleM10.jpg', '5'),
(11, '\"Дубровский\" - А.С.Пушкин', '«Дубровский» — произведение А. С. Пушкина, в котором главным героем выступает благородный разбойник-дворянин, похожий на Робин Гуда.    \n\nАннотация: богатый самодур Троекуров в пылу пустячной ссоры разорил соседа Дубровского. Старик не вынес удара и умер. Его сын решил отомстить обидчику, но у Троекурова была красавица-дочь.    \n\nОписание сюжета: предположительно действие романа происходит в 30-х годах XIX века, в двух соседних поместьях — Покровском и Кистенёвке, а также в их окрестностях. ', 'https://my.mail.ru/video/embed/1062798002516659217', 'middleM11.jpg', '6'),
(12, '\"Левша\" - Н.С.Лесков', '«Левша» — повесть-сказ Н. С. Лескова, написанная в 1881 году.    \n\nАннотация: история о простом русском человеке, талантливом мастере Левше, которому удалось доказать превосходство русских мастеров над заграничными и тем самым принести славу своему Отечеству. ', 'https://vk.com/video_ext.php?oid=-64953478&id=170095487&hash=2e58e1ac5b90f6dd', 'middleM12.jpg', '6'),
(13, '\"Толстый и тонкий\" - А.П.Чехов', '«Толстый и тонкий» — сатирический рассказ Антона Павловича Чехова, написанный в 1883 году. Впервые опубликован юмористическим журналом «Осколки» 1 октября 1883 года.                           \n\nАннотация: автор высмеивает пороки, свойственные любому социуму: лицемерие и низкопоклонство перед чинами. ', 'https://ok.ru/videoembed/3266479262248?autoplay=1', 'middleM13.jpg', '6'),
(14, '\"Алые паруса\" - А.Грин', '«Алые паруса» Александра Грина — повесть-феерия о возвышенной, всепобеждающей мечте, дающей силы жить и верить в лучшее. Это история о том, что истинно любящий человек способен сотворить чудо своими руками. ', 'https://vk.com/video_ext.php?oid=-229204001&id=456239320&hash=1653e8344114f206', 'middleM14.jpg', '6'),
(15, '\"Уроки фрнцузского\" - В.Г.Распутин', '«Уроки французского» — рассказ Валентина Распутина, впервые опубликованный в 1973 году.      \n\nАннотация: история о взаимоотношениях учительницы и ученика, благодаря которым мальчик получает жизненно важные уроки доброты, милосердия и человеческого участия. ', 'https://vk.com/video_ext.php?oid=-2698666&id=456239592&hash=f2611bbdc7aa2e48', 'middleM15.jpg', '6'),
(16, '\"Садко\"', 'Былина «Садко» повествует об искусном гусляре из Новгорода, который повстречал Морского царя. За хорошую игру на гуслях царь помог Садко разбогатеть.', 'https://rutube.ru/play/embed/9bbd26ba057af4682c521d88556d86df', 'middleM16.jpg', '7'),
(17, '\"Борис Годунов\" - А.С.Пушкин', '«Борис Годунов» — историческая драма Александра Пушкина о годах правления русского царя Бориса Годунова, его свержении и приходе к власти самозванца Лжедмитрия.     \n\nВремя и место действия: Россия в конце XVI — начале XVII столетия. Произведение описывает период царствования Бориса Годунова с 1598 по 1605 год. ', 'https://my.mail.ru/video/embed/-16713513045065518', 'middleM17.jpg', '7'),
(18, '\"Станционный смотритель\" - А.С.Пушкин', '«Станционный смотритель» — повесть А. С. Пушкина из цикла «Повестей покойного Ивана Петровича Белкина», написанная в 1830 году.      \r\n\r\nАннотация: в произведении описана драматическая история простой семьи, где центральная тема — трагедия простого человека, который теряет всё, что ему дорого.', 'https://vk.com/video_ext.php?oid=-181368273&id=456239076&hash=6f1295131dd8169b', 'middleM18.jpg', '7'),
(19, '\"Тарас Бульба\" - Н.В.Гоголь', '«Тарас Бульба» — историческая повесть Николая Гоголя, написанная в 1835–1842 годах.     \r\n\r\nАннотация: произведение о любви и предательстве, о долге и чести, а также о том, ради чего настоящему казаку и жизнь не жалко отдать — о любви к своей родной земле.', 'https://rutube.ru/play/embed/0c73e6facac2c53bec1c970454229fca\r\n    ', 'middleM19.jpg', '7'),
(20, '\"Бирюк\" - С.И.Тургенев', '«Бирюк» — рассказ Ивана Тургенева из цикла «Записки охотника». Написан в 1847 году, впервые опубликован в 1848 году в журнале «Современник».     \r\n\r\nАннотация: произведение представляет собой глубокое исследование человеческой природы и социальной несправедливости. В центре рассказа — конфликт между обязанностью и человечностью. ', 'https://my.mail.ru/video/embed/1340307861124785717', 'middleM20.jpg', '7'),
(21, '\"История одного города\" - М.Е.Салтыков-Щедрин', '«История одного города» — сатирический роман-хроника Михаила Евграфовича Салтыкова-Щедрина, написанный в 1869–1870 годах.        Аннотация: произведение представляет собой иносказательную историю российского государства. Действие романа происходит в вымышленном городе Глупове в период с 1731 по 1825 годы. В истории города прослеживается аналогия с историей Российской империи, включая правителей, государственные перевороты и прочие важные исторические события.', 'https://vk.com/video_ext.php?oid=-43542068&id=163507282&hash=9008b280e0e7cda9', 'middleM21.jpg', '8'),
(22, '\"О любви\" - А.П.Чехов', 'Аннотация рассказа Антона Чехова «О любви»: любовь — это не всегда страсть и буря эмоций, не всегда ухаживания и свадьба. Иногда любовь — это тихо смотреть на любимого человека, вздыхать украдкой в одиночестве, не смея и боясь порушить счастье любимой. ', 'https://my.mail.ru/video/embed/3506992095996977860', 'middleM22.jpg', '8'),
(23, '\"Василий Теркин\" - А.Твардовский', 'Моноспектакль «Василий Тёркин» 1979 года — образец произведения, созданного в жанре «театра одного актёра».  23\r\n\r\nАннотация: Олег Табаков читает поэму Александра Твардовского, передавая чувства и мысли простого бойца. В спектакле не стихает бодрый ритм, каждый отрывок — законченная сцена. ', 'https://rutube.ru/play/embed/508156ac445d410cd29e6a22fbbb6617', 'middleM23.jpg', '8'),
(24, '\"Ромео и Джульетта\" - У.Шекспир', '«Ромео и Джульетта» — трагедия Уильяма Шекспира о трагической судьбе юных влюблённых.     \r\n\r\nАннотация: два семейства города Верона — Монтекки и Капулетти — враждуют между собой. Но Ромео Монтекки и Джульетта Капулетти полюбили друг друга и решили сочетаться браком. ', 'https://vk.com/video_ext.php?oid=209564480&id=168212140&hash=b62369ce033acb6f', 'middleM24.jpg', '8'),
(25, '\"Баллада о доблестном рыцаре Айвенго\" - по мотивам романа В.Скотта \"Айвенго\" ', '«Баллада о доблестном рыцаре Айвенго» — советский художественный фильм 1982 года, снятый по мотивам романа Вальтера Скотта «Айвенго».', 'https://my.mail.ru/video/embed/1306402654850680074', 'middleM25.jpg', '8'),
(26, '\"Капитанская дочка\" - А.С.Пушкин', '«Капитанская дочка» — исторический роман Александра Сергеевича Пушкина, посвящённый событиям Крестьянской войны 1773–1775 годов под предводительством Емельяна Пугачёва.     \r\n\r\nАннотация: «Ни война, ни голод, ни забывшийся в самолюбии самозванец не способны остановить горячее чувство любви». Молодой офицер Пётр Андреевич Гринёв готов пойти на риск, чтобы спасти любимую Марию.', 'https://rutube.ru/play/embed/6904e5923d814104e07797f2091f45a8', 'middleM26.jpg', '8'),
(27, '\"Шинель\" - Н.В.Гоголь', '«Шинель» — повесть Николая Гоголя, которая входит в цикл «Петербургские повести».     \r\n\r\nАннотация: произведение рассказывает о жизни «маленького человека», чиновника Акакия Акакиевича Башмачкина. Он замкнутый человек, который беспрекословно терпит издевательства сослуживцев и тяжёлые условия жизни. ', 'https://rutube.ru/play/embed/48a7621d6c13bd9e969e27582e39b5df', 'middleM27.jpg', '8'),
(28, '\"Князь Игорь\"(по мотивам \"Слово о полку Игореве\") ', '«Князь Игорь» (1969) — музыкальный фильм, драма, исторический фильм, мелодрама. В основу картины легла одноимённая опера Александра Бородина, написанная по мотивам русского эпоса «Слово о полку Игореве». ', 'https://ok.ru/videoembed/272833251926?autoplay=1&ya=1', 'middleM28.jpg', '9'),
(29, '\"Бедная Лиза\" Н.М.Карамзин', '«Бедная Лиза» (1967) — телеспектакль по одноимённой повести Н. Карамзина из цикла «Литературный театр».     \r\n\r\nОписание: злодей Эраст полюбил прекрасную селянку Лизу, обесчестил её, предал и сбежал. Трагическая развязка не за горами.', 'https://rutube.ru/play/embed/487643ff44f38ca9481b342399a4dd8b', 'middleM29.jpg', '9'),
(30, '\"Евгений Онегин\" - А.С.Пушкин', '«Евгений Онегин» — роман в стихах Александра Сергеевича Пушкина, начат 9 мая 1823 года и закончен 5 октября 1831 года.     \r\n\r\nАннотация: по меткому выражению автора, «Евгений Онегин» показывает «жизнь во всей её прозаической действительности». Это произведение Пушкина обрело мировую славу и считается образцом изящной словесности, а также «энциклопедией русской жизни» первой четверти XIX века. ', 'https://vk.com/video_ext.php?oid=8015781&id=164238674&hash=b872c28bd7dc1240', 'middleM30.jpg', '9'),
(31, '\"Герой нашего времени\" - М.Ю.Лермонтов', '«Герой нашего времени» Михаила Лермонтова — лирико-психологический роман, который считается первым в русской прозе.       \r\n\r\nАннотация: в основе повествования — история жизни главного героя, Григория Печорина. Произведение состоит из пяти частей, расположенных не в хронологическом порядке. Автор постепенно погружает читателя во внутренний мир героя, циника, прожигателя жизни, противоречивого и неоднозначного. ', 'https://vk.com/video_ext.php?oid=679961310&id=456239322&hash=3e1b3570b2c89e5f', 'middleM31.jpg', '9'),
(32, '\"Белые ночи\" - Ф.М.Достоевский', '«Белые ночи» (1959) — романтическая драма, снятая режиссёром Иваном Пырьевым по одноимённой повести Ф. М. Достоевского. \r\n\r\nАннотация: Санкт-Петербург, лето, белые ночи. Одинокий человек совершает привычную прогулку и не находит знакомые лица. Петербург пустеет — господа разъезжаются по дачам. Предаваясь грустным мыслям и мечтам, главный герой неожиданно встречает девушку. Так в повествовании появляется Настенька. Молодые люди сближаются. Они встречаются белыми ночами, беседуют, затрагивая историю своей жизни. ', 'https://rutube.ru/play/embed/2737d6a5810542eca3cd7c9e74af9a6a', 'middleM32.jpg', '9');
INSERT INTO `middle_performances` (`id`, `title`, `description`, `video_url`, `image_url`, `class`) VALUES
(1, '\"Мещанин во дворянстве\" - Ж.Мольер', '«Мещанин во дворянстве» — комедия-балет в пяти актах Жана-Батиста Мольера, написанная в 1670 году.     \n\nАннотация: произведение соответствует правилам классицизма, включает в действие балет, что добавляет зрелищности. Автор создаёт пародийную атмосферу мещанского и дворянского быта с помощью простых изобразительных средств.', 'https://vk.com/video_ext.php?oid=-210628103&id=456246551&hash=bdf886946a366cc6', 'middlePF1.jpg', '8'),
(2, '\"Недоросль\" - Д.И.Фонвизин', '«Недоросль» Дениса Фонвизина — комедия, которая стала классикой русской литературы. В ней автор создал образы помещиков-крепостников, Скотининых, Простаковых и невежественного недоросля Митрофанушки. Эти имена стали нарицательными, а образы помещиков и их невежественность актуальны и сегодня.', 'https://rutube.ru/play/embed/62a4aa36b2dca829bbfb310d4ac6f1b6', 'middlePF2.jpg', '8'),
(3, '\"Ревизор\" - Н.В.Гоголь', '«Ревизор» — комедия в пяти действиях Николая Васильевича Гоголя, написанная в 1835 году (окончательные правки автор внёс в 1842 году).     \n\nАннотация: «Ревизор» — образец русской сатиры, в котором Гоголь решил «собрать в одну кучу всё дурное в России и за одним разом посмеяться над всем». Безымянный уездный город, в котором разворачивается действие, стал моделью всей России, и на сцену вышли все типы русской действительности, все характеры русского национального духа, все душевные движения и все пороки, присущие человеку.', 'https://vk.com/video_ext.php?oid=-205515763&id=456239544&hash=99d7e259af344bf7', 'middlePF3.jpg', '8'),
(4, '\"Горе от ума\" - А.С.Грибоедов', 'Телеспектакль «Горе от ума» — постановка Государственного академического Малого театра по одноимённой комедии А. С. Грибоедова. ', 'https://vk.com/video_ext.php?oid=-165903&id=456251494&hash=b7902c75b0b7efb0', 'middlePF4.jpg', '9'),
(5, '\"Мертвые души\" - Н.В.Гоголь', '«Мёртвые души» (1979) — сатирическая комедия по одноимённой поэме Н. В. Гоголя, исполненная артистами Московского Художественного академического театра им. М. Горького.', 'https://vk.com/video_ext.php?oid=-165903&id=456251051&hash=6e79c017411355d8', 'middlePF5.jpg', '9'),
(6, '\"Детство\" - Л.Н.Толстой', '«Детство. Отрочество. Юность» — телеспектакль 1973 года из трёх частей, созданный Петром Фоменко по одноимённой трилогии Льва Толстого. \r\n\r\nЧасть 1 «Детство»:  \r\n\r\nНиколенька Иртеньев — чувствительный и мечтательный мальчик, который, как и многие дети, грезит о той чудесной поре, когда он станет взрослым, когда не нужно будет зубрить уроки и рано ложиться спать. Он счастлив, и все вокруг кажутся ему счастливыми.', 'https://ok.ru/videoembed/1547910056508?autoplay=1', 'primaryPF1.jpg', '7');
INSERT INTO `middle_poems` (`id`, `title`, `description`, `pdf_url`, `epub_url`, `fb2_url`, `txt_url`, `image_url`, `class`) VALUES
(1, '\"Случились вместе два Астронома в пиру...\" - М.В.Ломоносов', '«Случились вместе два Астронома в пиру…» — стихотворение М. В. Ломоносова, написанное в 1761 году в форме притчи. \n\nСюжет: два астронома, Коперник и Птолемей, спорят о строении мира. Коперник утверждал, что Земля вращается вокруг Солнца, а Птолемей считал, что Солнце и все остальные планеты вращаются вокруг Земли. В реальности спор этих людей не мог происходить, так как они жили в разные эпохи: Коперник — в Средневековье, а Птолемей — в античности.', 'mixail-lomonosov-sluchilis-vmeste-dva-astronoma-v-piru.pdf', 'mixail-lomonosov-sluchilis-vmeste-dva-astronoma-v-piru.epub', 'mixail-lomonosov-sluchilis-vmeste-dva-astronoma-v-piru.fb2', 'mixail-lomonosov-sluchilis-vmeste-dva-astronoma-v-piru.txt', 'middleP1.jpg', '5'),
(2, '\"Кубок\" - В.А.Жуковский', '«Кубок» — баллада Василия Жуковского, перевод баллады немецкого поэта Фридриха Шиллера «Водолаз».   \r\n\r\nСюжет: царь бросает золотой кубок со скалы в пучину моря и призывает рыцарей или латников прыгнуть за кубком и достать его. Призыв звучит трижды, и никто не решается на такой поступок, ведь там можно погибнуть.', 'kubok-zhukovskiy.pdf', '', '', '', 'middleP2.jpg', '5'),
(3, '\"Няне\" - А.С.Пушкин', '«Няне» — незавершённое стихотворение Александра Сергеевича Пушкина, написанное в 1826 году и посвящённое няне поэта, Арине Родионовне. Впервые опубликовано в 1855 году в собрании сочинений поэта. ', '487cbb92.pdf', '', '', '', 'middleP3.jpg', '5'),
(4, '\"У лукоморья дуб зеленый...\" - А.С.Пушкин', '«У лукоморья дуб зелёный» — это отрывок из начала поэмы А. С. Пушкина «Руслан и Людмила».    \r\n\r\nОсновная тема стихотворения — описание волшебного мира сказок, фантастических персонажей и магических мест. Лирический герой, выступающий от первого лица, делится впечатлениями о сказочной стране, где происходит множество чудес. ', 'u-lukomorja-dub-zelenyi.pdf', '', '', '', 'middleP4.jpg', '5'),
(5, '\"Бородино\" - М.Ю.Лермонтов', '«Бородино» — одно из наиболее известных стихотворений Михаила Лермонтова. В нём от лица русского солдата рассказывается о важнейшем и кровопролитном сражении Отечественной войны 1812 года между русской армией и армией Наполеона Бонапарта. Оно состоялось 26 августа (7 сентября по новому стилю) у села Бородино, что в 125 км на запад от Москвы. ', 'М. Лермонтов  Бородино.pdf', '', '', '', 'middleP5.jpg', '5'),
(6, '\"Мороз, Красный нос\" - Н.А.Некрасов', '«Мороз, Красный нос» — поэма Николая Некрасова, написанная в 1863 году и посвящённая образу русской крестьянки. Произведение является одним из самых ярких образцов крестьянской тематики в русской литературе XIX века.', 'Nekrasov_Moroz_krasnyi_nos.pdf', '', '', '', 'middleP6.jpg', '5'),
(7, '\"Крестьянские дети\" - Н.А.Некрасов', '«Крестьянские дети» — поэма Николая Некрасова, написанная в 1861 году. В произведении автор описывает жизнь и быт крестьянской детворы России XIX века. \r\n\r\nНекрасов в своём творчестве часто обращался к теме тяжёлой жизни простого русского народа. В поэме «Крестьянские дети» он показывает, насколько рано приходилось взрослеть крестьянским детям и как многим из них с шестилетнего возраста приходилось на равных со взрослыми заниматься тяжёлым трудом, помогая родителям.', 'Крестьянские дети.pdf', '', '', '', 'middleP7.jpg', '5'),
(8, '\"Чудная картина\" - А.А.Фет', '«Чудная картина» — стихотворение А. А. Фета, написанное в 1842 году. Произведение относится к лирическому жанру и пейзажной лирике.    \r\n\r\nОсновная тема стихотворения — красота природы зимой и любовь к родному краю, параллель между природой и человеком, их неделимое единство.', 'Чудная картина.pdf', '', '', '', 'middleP8.jpg', '5'),
(9, '\"Весенний дождь\" - А.А.Фет', '«Весенний дождь» А. А. Фета — стихотворение пейзажной лирики, в котором природа выступает не только фоном, но и основным героем. Произведение написано в 1857 году и опубликовано в 1859 году в журнале «Русское слово». ', 'afanasij-fet-vesennij-dozhd.pdf', 'afanasij-fet-vesennij-dozhd.epub', 'afanasij-fet-vesennij-dozhd.fb2', 'afanasij-fet-vesennij-dozhd.txt', 'middleP9.jpg', '5'),
(10, '\"Задрожали листы, облетая\" - А.А.Фет', '«Задрожали листы, облетая» — лирическое стихотворение А. А. Фета, написанное в июле 1887 года. Произведение относится к пейзажной лирике и состоит из трёх строф.', 'afanasij-fet-zadrozhali-listy-obletaya.pdf', 'afanasij-fet-zadrozhali-listy-obletaya.epub', 'afanasij-fet-zadrozhali-listy-obletaya.fb2', 'afanasij-fet-zadrozhali-listy-obletaya.txt', 'middleP10.jpg', '5'),
(11, 'Ф.И.Тютчев - Полное собрание стихотворений', '«Полное собрание стихотворений» Ф. И. Тютчева — книга, которая находится в общественном достоянии (Public Domain).    \r\n\r\nПроизведение относится к жанрам литературы XIX века, русской классики, стихов и поэзии. \r\n\r\nНекоторые стихотворения из сборника: «Зима недаром злится...»(151 страница), «Весенние воды»(105 страница), «Как весел грохот летних бурь»(232 страница), «Есть в осени первоначальной»(262 страница) и другие.', 'Ф. Тютчев Полное собрание стихотворений.pdf', '', '', '', 'middleP11.jpg', '5'),
(12, '\"Весна\" - А.Н.Плещеев', 'Стихотворение Алексея Николаевича Плещеева «Весна» посвящено теме возрождения и обновления, которое весна приносит в природу и в жизнь человека.', 'Плещеев А. Н. Весна.pdf', '', '', '', 'middleP12.jpg', '5'),
(13, '\"Утро\" - И.С.Никитин', '«Утро» — стихотворение Ивана Саввича Никитина, написанное в 1854 году. Произведение относится к пейзажной лирике и описывает природу и мирный крестьянский труд.     \r\n\r\nТема стихотворения — пробуждение всего живого с наступлением утра: птичек, солнца, леса. В середине произведения в пейзаж впервые вписываются люди — рыбаки, проснувшиеся на рассвете и уже начавшие собирать снасти. ', 'Утро - Иван Никитин.pdf', '', '', '', 'middleP13.jpg', '5'),
(14, '\"Зимняя ночь в деревне\" - И.С.Никитин', '«Зимняя ночь в деревне» — стихотворение Ивана Саввича Никитина, написанное в 1853 году. Произведение относится к пейзажно-философской лирике и написано в жанре элегии.     \r\n\r\nВ стихотворении переплетаются две темы: красота зимней поры и предсмертные мысли старушки-крестьянки. Раскрываются они в монологе лирического героя, который, кажется, наблюдает за происходящим со стороны. ', 'Зимняя ночь в деревне - Иван Никитин.pdf', '', '', '', 'middleP14.jpg', '5'),
(15, '\"Ласточки\" - А.П.Майков', 'Стихотворение Аполлона Николаевича Майкова «Ласточки» представляет собой лирическое размышление о красоте природы и быстротечности жизни.', 'maikov.pdf', '', '', '', 'middleP15.jpg', '5'),
(16, '\"Зима\" - И.З.Суриков', 'В стихотворении И. З. Сурикова «Зима» описывается красота зимней природы и подготовка крестьян к зиме. ', 'zima-2.pdf', '', '', '', 'middleP16.jpg', '5'),
(17, '\"Помню - долгий зимний вечер\" - И.А.Бунин', '«Помню – долгий зимний вечер...» — стихотворение И. А. Бунина, написанное в 1887 году. Произведение относится к лирической поэзии с элементами пейзажной и психологической лирики. ', '4c51c469.pdf', '', '', '', 'middleP17.jpg', '5'),
(18, '\"Рассказ танкиста\" - А.Т.Твардовский', '«Рассказ танкиста» — стихотворение А. Т. Твардовского, написанное в 1942 году на основе реальных событий.     \r\n\r\nОсновная тема произведения — подвиг и самопожертвование на войне. Лирический герой, танкист, вспоминает о мальчике, который помогал в бою, рискуя собственной жизнью. ', 'Рассказ танкиста.pdf', '', '', '', 'middleP18.jpg', '5'),
(19, 'К.М.Симонов - стихи', '«Майор привёз мальчишку на лафете» — стихотворение К. Симонова, написанное в 1941 году.(28 страница)     \r\n\r\nГлавная тема произведения — судьба детей во времена Великой Отечественной войны. Автор поднимает проблемы сиротства, взаимопомощи, долга перед родиной. ', 'Библиотека ѴОгонекѵ, 1946, ѣ43-44. _Симонов К.М._ Стихи.(1946).pdf', '', '', '', 'middleP19.jpg', '5'),
(20, '\"Родная деревня\" - Н.М.Рубцов', '«Родная деревня» — лирическое стихотворение Н. М. Рубцова, написанное в 1970 году и посвящённое малой родине поэта — селу Никольское, в котором прошло его детство и юность.     \r\n\r\nОсновная тема произведения — любовь к родной деревне и осознание её ценности спустя годы. Лирический герой вспоминает своё детство и места, с которыми связаны его юношеские годы. Он осознаёт, что, несмотря на желание уехать в город, его душа всё равно остаётся в деревне.', 'rubcov-rodnaia-derevnia.pdf', '', '', '', 'middleP20.jpg', '5'),
(21, '\"Города и годы\" - Дон-Аминадо', '«Города и годы» — лирическое стихотворение Дона-Аминадо, написанное в 1927 году.     \r\n\r\nТема произведения — любовь к Родине, которая наиболее остро ощущается после того, как много путешествуешь по миру, нигде не найдя настоящего дома. Автор показывает многообразие и красоту мира, но при этом подчёркивает контраст между ними и Родиной. ', '', '', 'donaminado-text_0050.fb2', '', 'middleP21.jpg', '5'),
(22, '\"Зимнее утро\" - А.С.Пушкин', '«Зимнее утро» — стихотворение А. С. Пушкина, написанное в 1829 году. Произведение относится к пейзажной лирике и пронизано трепетной радостью от созерцания прекрасной русской зимы.     \r\n\r\nОсновная тема стихотворения — зимняя природа и пробуждение к активной жизни. Поэт показывает, как после мрачной и бурной ночи наступает ясное, светлое утро, пробуждающее радость и восторг. ', 'Зимнее утро.pdf', '', '', '', 'middleP22.jpg', '6'),
(23, '\"Три пальмы\" - М.Ю.Лермонтов', '«Три пальмы» — стихотворение Михаила Юрьевича Лермонтова, написанное в 1839 году и опубликованное в том же году в «Отечественных записках». ', 'tri-palmy_27685.pdf', 'tri-palmy_27685.epub', 'tri-palmy_27685.fb2', '', 'middleP23.jpg', '6'),
(24, '\"Тучи\" - М.Ю.Лермонтов', '«Тучи» — стихотворение Михаила Юрьевича Лермонтова, написанное в 1840 году. Произведение относится к руслу романтизма и жанру медитативной элегии.    \r\n\r\nТема стихотворения — одиночество и изгнание (оторванность от родины). Обращение к тучам можно трактовать как поиск сочувствия и понимания у тех, кто тоже переживает гонение. ', 'tuchi_27673.pdf', 'tuchi_27673.epub', 'tuchi_27673.fb2', '', 'middleP24.jpg', '6'),
(25, '\"Неохотно и несмело\" - Ф.И.Тютчев', 'Стихотворение Ф. И. Тютчева «Неохотно и несмело…» раскрывает тему красоты природы, объятой громом и молниями.', '482da770.pdf', '', '', '', 'middleP25.jpg', '6'),
(26, '\"Листья\" - Ф.И.Тютчев', '«Листья» — лирическое стихотворение Ф. И. Тютчева, написанное в 1830 году. Впервые опубликовано в 1879 году.     \r\n\r\nТема произведения — молодость и старость, изменчивость жизни. Автор сравнивает жизнь листьев с жизнью человека. Листья олицетворяют собой идею изменчивости, подвижности, им противопоставлена вечная зелень сосен и елей, которым свойственно постоянство. ', 'Листья.pdf', '', '', '', 'middleP26.jpg', '6'),
(27, '\"Ель рукавом мне тропинку завесила...\" - А.А.Фет', '«Ель рукавом мне тропинку завесила» — лирическое стихотворение А. А. Фета, написанное в 1891 году и вошедшее в сборник «Вечерние огни». ', 'fet_el_rukavom_mne_tropinku_zavesila.pdf', '', '', '', 'middleP27.jpg', '6'),
(28, '\"Железная дорога\" - Н.А.Некрасов', '«Железная дорога» — стихотворение Николая Некрасова, написанное в 1864 году. Произведение посвящено строительству железной дороги между Москвой и Санкт-Петербургом в период с 1842 по 1852 годы.', 'Железная дорога.pdf', '', '', '', 'middleP28.jpg', '6'),
(29, '\"Весна, весна! Как воздух чист\"\" - Е.А.Баратынский', '«Весна, весна! Как воздух чист!» — стихотворение Евгения Баратынского, написанное в 1832 году. Это лирическое произведение, относящееся к пейзажной лирике.', 'print.pdf', '', '', '', 'middleP29.jpg', '6'),
(30, '\"По горам две хмурых тучи\" - Я.П.Полонский', '«По горам две хмурых тучи…» — стихотворение Якова Петровича Полонского, написанное в 1859 году.', 'polonskiy-po-goram-dve-hmurih-tuchi.pdf', '', '', '', 'middleP30.jpg', '6'),
(31, '\"Где гнутся над омутом лозы...\" - А.К.Толстой', '«Где гнутся над омутом лозы…» — небольшая баллада Алексея Константиновича Толстого, написанная в 1840-х годах. ', 'aleksej-tolstoj-gde-gnutsya-nad-omutom-lozy.pdf', 'aleksej-tolstoj-gde-gnutsya-nad-omutom-lozy.epub', 'aleksej-tolstoj-gde-gnutsya-nad-omutom-lozy.fb2', '', 'middleP31.jpg', '6'),
(32, 'Пословицы и поговорки по направлениям', 'Патриотическое направление воспитания: Родина, природа. Духовно-нравственное направление: жизнь, милосердие, добро. Социальное направление воспитания: человек, семья, дружба, сотрудничество. Трудовое направление воспитания: труд. Физическое и оздоровительное направление: жизнь, здоровье. Эстетическое направление: культура, красота. Познавательно направление: познание.', 'Сборник-пословиц-и-поговорок.pdf', '', '', '', 'middleP32.jpg', '6'),
(33, '\"Жираф\" - Н.С.Гумилёв ', '«Жираф» — стихотворение Николая Гумилёва, написанное в 1907 году и впервые опубликованное в книге «Романтические цветы» (1908).    \r\n\r\nТема стихотворения — грусть женщины и рассказы о таинственных экзотических странах.', '475b802b.pdf', '', '', '', 'middleP33.jpg', '6'),
(34, '\"Я покинул родимый дом...\" - С.А.Есенин', '«Я покинул родимый дом» — лирическое стихотворение Сергея Есенина, написанное в 1918 году. В нём отражена тоска поэта по малой родине, привязанность к дому и семье. ', '439eff97.pdf', '', '', '', 'middleP34.jpg', '6'),
(35, '\"Низкий дом с голубыми ставнями\" - С.А.Есенин', '«Низкий дом с голубыми ставнями» — стихотворение Сергея Есенина, написанное в 1924 году.   \r\n\r\nТема произведения — искренняя любовь к малой родине, вечная память о родительском доме. В контексте неё развивается идея, что память об отчем доме неподвластна времени. ', '43a0142c.pdf', '', '', '', 'middleP35.jpg', '6'),
(36, '\"Ты помнишь, Алёша, дороги Смоленщины\" - К.М.Симонов', '«Ты помнишь, Алёша, дороги Смоленщины» — стихотворение Константина Симонова, написанное в 1941 году.', 'konstantin-simonov-ty-pomnish-alesha-dorogi-smolenshhiny.pdf', '', '', '', 'middleP36.jpg', '6'),
(37, '\"Сороковые\" - Д.Самойлов', 'konstantin-simonov-ty-pomnish-alesha-dorogi-smolenshhiny.pdf', 'david-samojlov-sorokovye.pdf', '', '', '', 'middleP37.jpg', '6'),
(38, '\"Родная деревня\" - Г.Тукай', '«Родная деревня» — стихотворение Габдуллы Тукая, написанное в 1909 году. Произведение посвящено малой родине поэта, небольшой деревушке Кырлай, где прошло его детство.      \r\n\r\nТема стихотворения — любовь к родной деревне, месту, где прошли детские и юношеские годы.  ', 'Тукай-Родная деревня.pdf', '', '', '', 'middleP38.jpg', '6'),
(39, '\"Книга\" - Г.Тукай', 'Стихотворение Габдуллы Тукая «Книга», написанное в 1909 году на татарском языке.       \r\n\r\nТема произведения — ценность знаний, полученных из книг. Поэт говорит о том, что книги направляют и «исцеляют» человека, избавляют от беды, приносят моральное облегчение, заставляют поверить в себя.', 'Тукай-Книга.pdf', '', '', '', 'middleP39.jpg', '6'),
(40, '\"Когда на меня навалилась беда\" - К.Кулиев', '«Когда на меня навалилась беда» — стихотворение балкарского поэта Кайсына Кулиева, написанное в 1967 году.     \r\n\r\nТема произведения — любовь к родине, родному краю. Основная мысль — родная земля способна залечивать душевные раны, на Родине всегда легче переносятся все тяготы. ', '1101-2011-12-13-08-43-54.pdf', '', '', '', 'middleP40.jpg', '6'),
(41, '\"Каким бы малым не был мой народ\" - К.Кулиев', '«Каким бы малым ни был мой народ…» — стихотворение Кайсына Кулиева, в котором автор выражает любовь к своей родине, народу и языку. ', 'Кулиев-Каким бы малым ни был мой народ.pdf', '', '', '', 'middleP41.jpg', '6'),
(42, '\"Признание\" - Г.Р.Державин', '«Признание» — автобиографическое стихотворение Г. Р. Державина, написанное в 1807 году. В нём автор подводит итоги своей жизни и творчества, размышляет о собственных достижениях и промахах.', 'gavriil-derzhavin-priznanie.pdf', 'gavriil-derzhavin-priznanie.epub', 'gavriil-derzhavin-priznanie.fb2', 'gavriil-derzhavin-priznanie.txt', 'middleP42.jpg', '7'),
(43, '\"Молитва\" - М.Ю.Лермонтов', '«Молитва» — лирическое стихотворение Михаила Юрьевича Лермонтова, написанное в 1839 году и опубликованное в 1842 году, после смерти поэта.', 'molitva-lermontov-mixail_5825.pdf', '', '', '', 'middleP43.jpg', '7'),
(44, '\"Ангел\" - М.Ю.Лермонтов', '«Ангел» — лирическое стихотворение М. Ю. Лермонтова, написанное в 1831 году. Произведение считается одним из самых значимых в раннем периоде творчества автора.', '4960f1ce.pdf', '', '', '', 'middleP44.jpg', '7'),
(45, '\"Русский язык\" - И.С.Тургенев', '«Русский язык» — стихотворение в прозе И. С. Тургенева, написанное в 1882 году. Произведение появилось в период, когда Россия переживала сложные времена — политическую нестабильность, общественные кризисы. ', 'ivan-turgenev-russkij-yazyk.pdf', 'ivan-turgenev-russkij-yazyk.epub', 'ivan-turgenev-russkij-yazyk.fb2', 'ivan-turgenev-russkij-yazyk.txt', 'middleP45.jpg', '7'),
(46, '\"Два богача\" - И.С.Тургенев (Сборник \"Стихотворения в прозе\")', '«Два богача» — стихотворение в прозе И. С. Тургенева, написанное в июле 1878 года и опубликованное в 1882 году в рамках сборника «Стихотворения в прозе».', 'Turgenev1.pdf', '', '', '', 'middleP46.jpg', '7'),
(47, '\"Близнецы\" - И.С.Тургенев', '«Близнецы» — стихотворение И. С. Тургенева, написанное в 1878 году и вошедшее в сборник «Стихотворения в прозе». ', 'ivan-turgenev-bliznecy.pdf', 'ivan-turgenev-bliznecy.epub', 'ivan-turgenev-bliznecy.fb2', 'ivan-turgenev-bliznecy.txt', 'middleP47.jpg', '7'),
(48, '\"Русские женщины\" - Н.А.Некрасов', '«Русские женщины» — поэма Николая Некрасова, которая рассказывает о жёнах декабристов, последовавших за мужьями в Сибирь.     \r\n\r\nАннотация: произведение посвящено трагическому подвигу жён декабристов, которые отказались от комфорта и тепла, чтобы оказаться в суровом крае в ужасных условиях. ', 'Н. Некрасов Русские женщины.pdf', '', '', '', 'middleP48.jpg', '7'),
(49, '\"Вчерашний день, часу в шестом...\" - Н.А.Некрасов', '«Вчерашний день, часу в шестом…» — стихотворение Н. А. Некрасова, написанное в 1848 году.  13\r\n\r\nОсновная тема произведения — жестокое обращение с крестьянками, несправедливость и страдания народа. Автор описывает сцену, когда молодую крестьянку публично били кнутом на Сенной площади в Петербурге. ', '4e894fcf.pdf', '', '', '', 'middleP49.jpg', '7'),
(50, '\"Князь Михайло Репнин\" - А.К.Толстой', '«Князь Михайло Репнин» — историческая баллада Алексея Константиновича Толстого, написанная в 1860-х годах.    \r\n\r\nАннотация: произведение основано на исторических событиях периода правления Ивана Грозного и отражает социальные и политические реалии того времени. ', 'aleksej-tolstoj-knyaz-mixajlo-repnin.pdf', 'aleksej-tolstoj-knyaz-mixajlo-repnin.epub', 'aleksej-tolstoj-knyaz-mixajlo-repnin.fb2', 'aleksej-tolstoj-knyaz-mixajlo-repnin.txt', 'middleP50.jpg', '7'),
(51, '\"Цветы последние милей...\" - А.С.Пушкин', '«Цветы последние милей…» — стихотворение А. С. Пушкина, написанное 16 октября 1825 года и посвящённое близкой подруге поэта — Прасковье Александровне Осиповой. При жизни автора произведение не публиковали, впервые его напечатали в 1838 году в журнале «Современник». ', 'aleksandr-pushkin-cvety-poslednie-milej.pdf', 'aleksandr-pushkin-cvety-poslednie-milej.epub', 'aleksandr-pushkin-cvety-poslednie-milej.fb2', 'aleksandr-pushkin-cvety-poslednie-milej.txt', 'middleP51.jpg', '8'),
(52, 'М.Ю.Лермонтов - Полное собрание стихотворений', 'Лермонтов М. Ю. \r\nПолное собрание стихотворений  /  М. Ю. Лермонтов —  «Public\r\nDomain»,   — (Список школьной литературы 5-6 класс) Стихотворение \"Осень\" - 17 страница', 'М. Лермонтов Полное собрание стихотворений.pdf', '', '', '', 'middleP52.jpg', '8'),
(53, 'Ф.И.Тютчев - Полное собрание стихотворений', 'Тютчев Ф. И.\r\nПолное собрание стихотворений  /  Ф. И. Тютчев —  «Public\r\nDomain», \r\n«Уже великое небесное светило,Лиюще с высоты обилие и свет,Начертанным\r\nпутем годичный круг свершилоИ в ново поприще в величии грядет! —И\r\nсе! Одеянный блистательной зарею,Пронзив эфирных стран белеющийся\r\nсвод,Слетает с урной роковоюМладый сын Солнца – Новый год!..»\r\n\r\n\"Осенний вечер\" - 114 страница', 'Ф. Тютчев Полное собрание стихотворений.pdf', '', '', '', 'middleP53.jpg', '8'),
(54, '\"Первый ландыш\" - А.А.Фет', '«Первый ландыш» — стихотворение Афанасия Фета, написанное в 1854 году.    \r\n\r\nАннотация: произведение посвящено теме пробуждения природы весной и её связи с внутренним миром человека. Ландыш, первый весенний цветок, символизирует чистоту, невинность и обновление.', 'afanasij-fet-pervyj-landysh.pdf', 'afanasij-fet-pervyj-landysh.epub', 'afanasij-fet-pervyj-landysh.fb2', 'afanasij-fet-pervyj-landysh.txt', 'middleP54.jpg', '8'),
(55, '\"На поле Куликовом\" - А.А.Блок', '«На поле Куликовом» — стихотворение А. А. Блока, написанное в 1908 году и входящее в цикл «Родина». ', 'aleksandr-blok-na-pole-kulikovom.pdf', 'aleksandr-blok-na-pole-kulikovom.epub', 'aleksandr-blok-na-pole-kulikovom.fb2', 'aleksandr-blok-na-pole-kulikovom.txt', 'middleP55.jpg', '8'),
(56, '\"Пугачев\" - С.А.Есенин', 'Аннотация к поэме Сергея Есенина «Пугачёв»:   \r\n\r\nВ 1921 году Сергей Есенин создал драматическую поэму «Пугачёв», которая вызвала неоднозначное отношение современников. Сам поэт считал, что революционность произведения состоит не столько в историческом материале, сколько в способах его художественного воплощения. ', 's.a.-esenin-poemapugachyov.pdf', '', '', '', 'middleP56.jpg', '8'),
(57, '\"Василий Теркин\" - А.Твардовский', '«Василий Тёркин. Книга про бойца» — поэма Александра Твардовского, одно из главных произведений в творчестве поэта, получившее всенародное признание.     \r\n\r\nАннотация: герой поэмы, Василий Тёркин, — собирательный образ русского солдата, олицетворяющего воина-победителя, которому присущи воинская отвага, умение воевать и не унывать ни при каких обстоятельствах. ', 'Tvardovsky_Vasilij_Terkin.pdf', '', '', '', 'middleP57.jpg', '8'),
(58, '\"Катюша\" - М.В.Исаковский', '«Катюша» — лирическая композиция, созданная поэтом Михаилом Исаковским и композитором Матвеем Блантером. Впервые песня зазвучала в исполнении Валентины Батищевой 27 ноября 1938 года в Колонном зале Дома Союзов. ', 'slova_pesni_Katyusha.pdf', '', '', '', 'middleP58.jpg', '8'),
(59, '\"Враги сожгли родную хату...\" - М.В.Исаковский', '«Враги сожгли родную хату…» — стихотворение Михаила Васильевича Исаковского, написанное в 1945 году и опубликованное в 1946 году в журнале «Знамя». ', 'mixail-isakovskij-vragi-sozhgli-rodnuyu-xatu.pdf', 'mixail-isakovskij-vragi-sozhgli-rodnuyu-xatu.epub', 'mixail-isakovskij-vragi-sozhgli-rodnuyu-xatu.fb2', 'mixail-isakovskij-vragi-sozhgli-rodnuyu-xatu.txt', 'middleP59.jpg', '8'),
(60, '\"Песенка о пехоте\" - Б.Окуджава', '«Песенка о пехоте» — стихотворение Булата Окуджавы, написанное в 1961 году. Жанр — военная лирика, тема — война, долг перед Родиной, самоотверженность солдата. ', 'bulat-okudzhava-pesenka-o-pexote.pdf', 'bulat-okudzhava-pesenka-o-pexote.epub', 'bulat-okudzhava-pesenka-o-pexote.fb2', '', 'middleP60.jpg', '8'),
(61, '\"Здесь птицы не поют\"(\"Нам нужна одна победа\") - Б.Окуджава', '«Здесь птицы не поют…» (также «Нам нужна одна победа» или «Десятый наш десантный батальон») — песня Булата Окуджавы, написанная в 1970 году для художественного фильма Андрея Смирнова «Белорусский вокзал». ', '74-q--q--q-q.pdf', '', '', '', 'middleP61.jpg', '8'),
(62, '\"Мне трудно без России\" - Н.А.Оцуп', 'Стихотворение «Мне трудно без России» было написано Николаем Александровичем Оцупом в 1933 году. Это произведение создано в контексте эмиграции, когда поэт ощущал свою связь с родиной, находясь вдали от неё. Основная тема стихотворения — ностальгия по родине и трудности, связанные с жизнью вдали от неё. Лирический герой выражает свою тоску по России, размышляет о своей роли как поэта и о том, как тяжело быть без родной земли. ', 'ocup-mne-trudno-bez-rossii.pdf', '', '', '', 'middleP62.jpg', '8'),
(63, '\"Бабье лето\" - Дон Аминадо', '«Бабье лето» — стихотворение Дона Аминадо (настоящее имя — Аминад Шполянский), написанное в 1926 году. Произведение посвящено России, которую поэту пришлось покинуть. ', 'minado-babye-leto.pdf', '', '', '', 'middleP63.jpg', '8'),
(64, '\"У птицы есть гнездо...\" - И.А.Бунин', '«У птицы есть гнездо...» — стихотворение И. А. Бунина, написанное в 1922 году в эмиграции. ', 'bunin-u-ptici-est-gnezdo.pdf', '', '', '', 'middleP64.jpg', '8'),
(65, '\"Обоз\" - И.А.Крылов', '«Обоз» — басня Ивана Андреевича Крылова, написанная осенью 1812 года и впервые опубликованная в том же году в журнале «Сын отечества». ', 'i.a.-krylov-oboz.pdf', '', '', '', 'middleP65.jpg', '8'),
(66, '\"Смерть Ермака\" - К.Ф.Рылеев', '«Смерть Ермака» — стихотворение (дума) К. Ф. Рылеева, написанное в 1821 году. Произведение посвящено последним часам жизни казачьего атамана Ермака, который внёс значительный вклад в завоевание Сибири для Русского государства. ', 'k.f.-ryleev-smert-ermaka.pdf', '', '', '', 'middleP66.jpg', '8'),
(67, '\"Туча\" - А.С,Пушкин', '«Туча» — лирическое стихотворение Александра Сергеевича Пушкина, написанное в 1835 году. Произведение относится к пейзажной лирике и построено на антитезе: недавняя буря противопоставляется наступившему покою. ', 'tucha_27661.pdf', 'tucha_27661.epub', 'tucha_27661.fb2', '', 'middleP67.jpg', '8'),
(68, '\"Мцыри\" - М.Ю.Лермонтов', '«Мцыри» — поэма М. Ю. Лермонтова, часть позднего творчества, входит в число последних образцов классики русского романтизма. Произведение опубликовано в 1840 году в сборнике «Стихотворения М. Лермонтова».     \r\n\r\nАннотация: в основе поэмы — трагическая история мальчика-горца, который был пленён русским генералом и вырос в монастыре. Мцыри всё время тосковал по родине, но постепенно как будто привык к «плену», выучил чужой язык и уже собирался принять монашеский обет. Однако мощный душевный порыв заставляет его решиться на побег. ', 'mcyri_26690.pdf', 'mcyri_26690.epub', 'mcyri_26690.fb2', '', 'middleP68.jpg', '8'),
(69, '\"Памятник\" - Г.Р.Державин', '«Памятник» — стихотворение Гавриила Романовича Державина, написанное в 1795 году. Первоначально оно называлось «К музе».', 'g.r.-derzhavin_stihi674.pdf', '', '', '', 'middleP69.jpg', '9'),
(70, '\"Осень\" - Н.М.Карамзин', '«Осень» — стихотворение Н. М. Карамзина, написанное в Женеве в 1789 году. По жанру произведение относится к пейзажной лирике, содержащей философские размышления.', 'nikolaj-karamzin-osen.pdf', 'nikolaj-karamzin-osen.epub', 'nikolaj-karamzin-osen.fb2', 'nikolaj-karamzin-osen.txt', 'middleP70.jpg', '9'),
(71, '\"К Чаадееву\" - А.С.Пушкин', '«К Чаадаеву» — стихотворение А. С. Пушкина, написанное в 1818 году и адресованное другу поэта, Петру Чаадаеву. Произведение относится к гражданской лирике и отражает ранние патриотические взгляды автора. ', 'k-chaadaevu.pdf', '', '', '', 'middleP71.jpg', '9'),
(72, '\"На холмах Грузии лежит ночная мгла...\" - А.С.Пушкин', '«На холмах Грузии лежит ночная мгла...» — стихотворение А. С. Пушкина, написанное в 1829 году во время пребывания поэта на Кавказе.  ', '487c8b24.pdf', '', '', '', 'middleP72.jpg', '9'),
(73, '\"Я вас любил: любовь ещё быть может...\" - А.С.Пушкин', '«Я вас любил: любовь ещё, быть может...» — стихотворение Александра Сергеевича Пушкина, написанное в 1829 году. Это лирическая миниатюра, созданная, по мнению исследователей, как отклик на реальные события в жизни поэта.  ', '487c8f22.pdf', '', '', '', 'middleP73.jpg', '9'),
(74, '\"Я памятник себе воздвиг нерукотворный...\" - А.С.Пушкин', '«Я памятник себе воздвиг нерукотворный...» — стихотворение Александра Сергеевича Пушкина, написанное в 1836 году, в котором автор подводит итог своей творческой деятельности и раскрывает тему памяти потомков о личности и творчестве поэта. ', 'ya-pamyatnik-sebe-vozdvig-nerukotvornyj.pdf', '', '', '', 'middleP74.jpg', '9'),
(75, '\"Смерть поэта\" - М.Ю.Лермонтов', '«Смерть поэта» («На смерть Пушкина») — стихотворение Михаила Лермонтова о трагической гибели великого поэта Александра Сергеевича Пушкина и вине высшего общества в смерти поэта.  ', 'Лермонтов М_Смерть.pdf', '', '', '', 'middleP75.jpg', '9');
INSERT INTO `primary_audiobooks` (`id`, `title`, `description`, `audio_url`, `audio_url2`, `image_url`, `class`) VALUES
(1, ' \"Аля, Кляксич и буква «А»\" - И.П.Токмакова', 'Маленькая Аля писала письмо маме, когда на середину страницы выбежала буква А и рассказала, что отвратительный Кляксич пробрался в Букварь и хочет заменить все буквы своими родственницами — кляксами. ', 'https://deti-online.com/audioskazki/sbornik-skazok-mp3/alya-klyaksich-i-bukva-a/', 'deti-online.com_-_alya-klyaksich-i-bukva-a.zip', 'primaryA1.jpg', '1'),
(2, '\"Гусь и журавль\" - К.Д.Ушинский', '«Гусь и журавль» — поучительная миниатюра, написанная Константином Ушинским. В ней рассказывается о самонадеянном и хвастливом гусе, который возомнил себя царём птиц. Глупый гусак гордится тем, что владеет сразу несколькими умениями — ходит по земле, летает по воздуху и плавает в пруду. \n\nЖуравль метко осаживает хвастуна, подчёркивая, что тому никогда не покорить орлиных высот, не обогнать быстроногого оленя и не научиться нырять на такую глубину, как щука. \n\nСказка учит тому, что лучше быть мастером в одном деле, чем посредственностью в нескольких.', 'https://podster.fm/podcasts/vaukniga/e/347628/kushinskiy-gus-i-zhuravl', 'kushinskiy-gus-i-zhuravl.mp3', 'primaryA2.jpg', '1'),
(3, '\"Волк\" - Я.М.Тайц', '«Волк» — рассказ Якова Тайца о том, как девочка Маша пришла в зоопарк и увидела там серого хищника. Узнав в нём антагониста популярных сказок, малышка пристыдила зверя — ведь нехорошо преследовать Красную Шапочку и охотиться за тремя поросятами.\n\nТеперь волка поймали и заперли в клетке, а значит впереди у него много времени, чтобы подумать над своим поведением. \n\nИстория Маши и одинокого волчка развеселит ребят, напомнит им сюжеты зарубежных сказок и донесёт до них мысль, что маленьких и беззащитных обижать нельзя.', 'https://nukadeti.ru/audiorasskazy/tajc-volk', 'nukadeti.ru_-_tajc-volk.mp3', 'primaryA3.jpg', '1'),
(4, '\"Саша-дразнилка\" - Н.М.Артюхова', '«Саша-дразнилка» — забавная миниатюра Нины Артюховой. Рассказ про мальчика Сашу, которому нравилось дразнить свою маленькую сестрёнку Лялю. Ляля обижалась и плакала, но папа научил её, как исправить ситуацию.\n\nКогда Ляля пожаловалась, что её постоянно дразнит старший брат, папа дал дочке мудрый совет. Девочка последовала ему и перестала бурно реагировать на слова Саши. Вместо привычных слёз и обид насмешник получал в ответ спокойствие и невозмутимость. Вскоре мальчик сам не выдержал и разрыдался: он так старался довести сестру — и всё впустую! \n\nРассказ будет полезно прочитать детям, которые легко обижаются и болезненно воспринимают слова окружающих.', 'https://mishka-knizhka.ru/audio-rasskazy-dlya-detej/audio-rasskazy-artjuhovoj-n-m/sasha-draznilka-audio-rasskaz-artjuhovoj-n-m/', 'mishka-knizhka.ru__Саша-дразнилка.mp3', 'primaryA4.jpg', '1'),
(5, '\"Ворон и сорока\" - К.Д.Ушинский', 'Рассказ Константина Ушинского «Ворон и сорока»: притча о говорливой сороке и молчаливом вороне, которая рассказывает о том, что нельзя верить тому, кто много болтает.', 'https://mishka-knizhka.ru/audio-rasskazy-dlya-detej/skazki-ushinskogo/voron-i-soroka-audio/', 'mishka-knizhka.ru__Ворон_и_сорока.mp3', 'primaryA5.jpg', '1'),
(6, '\"Что хорошо и что дурно?\" - К.Д.Ушинский', 'Рассказ К. Д. Ушинского «Что хорошо и что дурно» посвящён теме нравственных понятий и включает в себя описание таких качеств, как трудолюбие, гордость, леность, прилежание, милосердие, зависть, ложь, доброта, злость, кротость, упрямство, скупость, щедрость, честность, благодарность, скука и радость.', 'https://rutube.ru/play/embed/c8e3acd13fcd2d870633d370f44a981c', '', 'primaryA6.jpg', '1'),
(7, '\"Худо тому, кто добра не делает никому\" - К.Д.Ушинский', '«Худо тому, кто добра не делает никому» — это маленький рассказ К. Д. Ушинского о мальчике Грише, который отказывается помогать своим школьным товарищам.\n\nВ конце рассказа писатель оставляет вопрос: «Любили ли Гришу товарищи?». Наверняка товарищи не любили Гришу, потому что он был грубым и жадным, думал только о себе, отвечал невежливо.\n\nСвоим рассказом К. Д. Ушинский хотел показать, как нельзя вести себя с товарищами. Рассказ учит быть добрыми, отзывчивыми, вежливыми, помогать в трудную минуту.\n\nВ название рассказа вынесена пословица «Худо тому, кто добра не делает никому». Худо — значит плохо. Получается, что тому, кто не делает добра, самому же плохо.', 'https://yandex.ru/video/preview/14657334295821793604', 'Худо тому, кто не делает добра никому. К.Ушинский.mp3', 'primaryA7.jpg', '1'),
(8, '\"Находка\" - Д.Тихомиров', 'Рассказ Дмитрия Тихомирова «Находка» рассказывает о мальчике, который во время прогулки мимо оврага услышал жалобный скулёж. Он спустился в овраг и увидел в траве крошечного щенка.\n\nМальчик взял щенка на руки. Он был ещё слепенький, кожа на нём сморщилась. Щенок дрожал всем телом и всё плакал жалобно.\n\nМальчик принёс щенка домой и выпросил у матери молочка. Налил в черепок и поставил щенку. Но щенок не видел молока и тыкался во все стороны головой. Мать сунула щенка мордочкой в молоко и подержала немного. Щенок стал лакать и перестал плакать.\n\nС тех пор мальчик подружился с собачкой, поил и кормил её. Они дали собачке кличку — Находка. Находка жила у них долго и очень любила мальчика.', 'https://altube.ru/video/WGrIqR1FKoHf', 'Д.Тихомиров _Находка_.mp3', 'primaryA8.jpg', '1'),
(9, '\"Мальчики и лягушки\" - Д.Тихомиров', 'Мальчики играли у пруда. Увидели они в воде лягушек. Дети набрали камней и принялись бросать в лягушек. Детям было весело. Одна лягушка высунулась из воды и говорит шалунам: — Перестаньте камни бросать: вам забава, а нам — смерть!\nЗлая забава. Разумная речь. И лягушке жить хочется.\n\n\nРассказ учит тому, что бессмысленное уничтожение окружающего мира может привести к негативным последствиям для его обитателей. ', '', '', 'primaryA9.jpg', '1'),
(10, '\"Храбрый ёж\" - Д.Хармс', '«Храбрый ёж» — сказка Даниила Хармса, в которой рассказывается о храбром еже, который не побоялся змеи, выскочившей из ящика. ', 'https://soundstream.media/clip/malen-kiye-istorii-daniil-kharms-khrabryy-yezh', 'Д. Хармс - Храбрый еж.mp3', 'primaryA10.jpg', '1'),
(11, '\"Лучший друг\" - Ю.И.Ермолаев ', 'Рассказ Ю. И. Ермолаева «Лучший друг» рассказывает о том, как мальчик Коля оставил свой самокат во дворе, а его друг Вова стал без спроса кататься на нём.  4\n\nКоля рассердился и побежал во двор, чтобы отлупить Вову, чтобы тот не брал чужие вещи без спроса. Когда Вова увидел его и сказал стоявшему рядом мальчику: «Знакомься, Игорёк, это мой лучший друг Коля», — мальчик смутился, разжал кулаки и неожиданно для себя спросил: «Что же ты на самокате-то не катаешься?..». 45\n\nРассказ называется так, потому что именно фраза «лучший друг» — главная в нём, она меняет настрой Коли. ', 'https://babushkini-skazki.ru/audioskazki/skazki-po-multfilmam/7773-luchshij-drug.html', 'Ю.Ермолаев _Лучший друг_.mp3', 'primaryA11.jpg', '1'),
(12, '\"Подарок\" - Е.Благинина', 'Стихотворение Елены Благининой «Подарок» рассказывает о том, как к девочке пришла подружка и ей приглянулась одна из игрушек — заводная лягушка. Героиня была расстроена тем, что приходится отдавать любимую вещь, но всё же отдала её подружке.', '', 'Е Благинина - Подарок.mp3', 'primaryA12.jpg', '1'),
(13, '\"Телефон\" - К.И.Чуковский', 'Однажды у писателя зазвонил телефон. К его удивлению, на другом конце провода он услышал слона, крокодила, зайчаток и медведя. И каждый что-нибудь просил, и просьбы все были необычными.\n\nЧуковский решил написать весёлую сказку о своих разговорах. ', 'https://deti-online.com/audioskazki/skazki-chukovskogo-mp3/telefon/', 'deti-online.com_-_telefon.mp3', 'primaryA13.jpg', '1'),
(14, '\"Помощник\" - М.С.Пляцковский ', '«Помощник» — сказка Михаила Пляцковского. \r\n\r\nГлавные герои: кролик Кочерыжка и ослик Алфавит. Кролик попросил ослика о помощи в постройке дома, но Алфавит отказался. Ему было лень помогать, а признаться в своей лени было стыдно, поэтому ослик притворился, что у него болит голова. А вот покушать вместе Алфавит был не прочь. Кролик сразу понял, что ослик его обманул, поэтому ничего ему на отказ и не ответил. Но сумел проучить ослика за враньё: отказался от «помощи», когда надо было есть суп. ', 'https://nukadeti.ru/audioskazki/plyackovskij-pomoshhnik', 'nukadeti.ru_-_plyackovskij-pomoshhnik.mp3', 'primaryA14.jpg', '1'),
(15, '\"Бумага-победительница\" - М.Ильин ', 'Рассказ М. Ильина «Бумага-победительница» рассказывает об истории создания и распространения бумаги.', 'https://rutube.ru/play/embed/5d73346646708183733b2616316ede9d', '', 'primaryA15.jpg', '2'),
(16, '\"Музыкант\" - В.В.Бианки ', 'В рассказе Виталия Бианки «Музыкант» автор сравнивает человека и животное. Старый медвежатник любит играть на скрипке и старается сам научиться этому. ', 'https://nukadeti.ru/audiorasskazy/vitalij-bianki-muzykant', 'nukadeti.ru_-_vitalij-bianki-muzykant.mp3', 'primaryA16.jpg', '2'),
(17, '\"Страшный рассказ\" - Е.И.Чарушин', 'Краткое описание рассказа Евгения Чарушина «Страшный рассказ»:\r\n\r\nМальчики Шура и Петя остались одни на даче у леса, так как родители ушли в гости. В темноте они услышали шуршание и начали храбриться, утверждая, что ничего не боятся. Вдруг за дверью раздались шаги, и Петя переполз к Шуре, они спрятались под одеялом. Шум продолжался, но в этот момент вернулись родители и зажгли свет.\r\n\r\nОни осмотрели дом и обнаружили, что шумел ёжик, забравшийся из леса. Родители аккуратно переместили ёжика в чулан, дали ему молока и мяса. С тех пор ёжик жил с мальчиками всё лето, и они больше его не боялись.', 'https://nukadeti.ru/audiorasskazy/charushin-strashnyj-rasskaz', 'nukadeti.ru_-_charushin-strashnyj-rasskaz.mp3', 'primaryA17.jpg', '2'),
(18, '\"Ребята и утята\" - Пришвин ', 'Рассказ Михаила Пришвина «Ребята и утята» повествует о путешествии утиного семейства из болота на озеро.', 'https://nukadeti.ru/audioskazki/prishvin_rebyata_i_utyata', 'nukadeti.ru_-_rebyata_i_utyata.mp3', 'primaryA18.jpg', '2'),
(19, '\"Новогодние былины\" - С.Михалков ', '«Новогодняя быль» — сказка Сергея Михалкова, в которой рассказывается история одной ёлочки, растущей недалеко от дома лесничего.\r\n\r\nОднажды незнакомая сорока, прилетевшая летом неизвестно откуда, сказала ёлочке, что зимой её срубят люди. Ёлочка очень испугалась. Но малютке повезло — её облюбовал местный лесник. Он не стал спиливать ёлку ради Нового года, а просто красиво нарядил её, чтобы порадовать своих детишек. Повзрослев, ребята продолжили эту традицию.', 'https://nukadeti.ru/audioskazki/novogodnyaya-byl', 'nukadeti.ru_-_novogodnyaya-byl.mp3', 'primaryA19.jpg', '2'),
(20, '\"Сказка о потерянном времени\" - Е.Л.Шварц', '«Сказка о потерянном времени» Евгения Шварца — поучительная история о том, как ленивые дети, впустую тратившие своё время, превратились в старичков.\r\n\r\nМальчик Петя Зубов любил убивать время — бездельничать, шалить и опаздывать в школу. И вот потерянное Петей время утекло от него к жуликам-колдунам. Мальчик в одно мгновение превратился в старичка, и теперь его не узнают друзья и родные.', 'https://nukadeti.ru/audioskazki/skazka-o-poteryannom-vremeni', 'nukadeti.ru_-_skazka-o-poteryannom-vremeni.mp3', 'primaryA20.jpg', '4'),
(21, '\"Главные реки\" - В.Драгунский', 'Рассказ Виктора Драгунского «Главные реки» повествует о девятилетнем Денисе Кораблеве, который накануне заигрался с летающим змеем и забыл выучить уроки. Он едва не опоздал в школу, чуть не сбил возле класса учительницу, и она сразу вызвала его к доске. Денис не знал, какое стихотворение читать, и начал рассказывать Пушкина. Мишка пытался ему подсказать, но Дениска не понял друга. Он насмешил весь класс своим «мужичком с ноготком».', 'https://nukadeti.ru/audioskazki/glavnye_reki_ameriki', 'nukadeti.ru_-_glavnye-reki-ameriki.mp3', 'primaryA21.jpg', '4'),
(22, '\"Как я ловил человечков\" - Б.Житков', 'Рассказ Бориса Житкова «Как я ловил человечков» рассказывает о любопытном мальчике Боре, который, придумав человечков, которые живут на макете парусника, ослушался бабушку и чуть не испортил важную для неё вещь.', 'https://nukadeti.ru/audioskazki/zhitkov-kak-ya-lovil-chelovechkov', 'nukadeti.ru_-_zhitkov-kak-ya-lovil-chelovechkov.mp3', 'primaryA22.jpg', '4'),
(23, '\"Корзина с еловыми шишками\" - К.Г.Паустовский', '«Корзина с еловыми шишками» — рассказ советского писателя Константина Паустовского о том, как композитор Эдвард Григ написал музыкальную пьесу для дочери лесника. ', 'https://nukadeti.ru/audioskazki/korzina-s-elovymi-shishkami', 'nukadeti.ru_-_korzina-s-elovymi-shishkami.mp3', 'primaryA23.jpg', '4'),
(24, '\"Ёлка\" - М.Зощенко', 'Рассказ М. М. Зощенко «Ёлка» повествует о воспоминании уже взрослого мужчины о том, как много лет назад они с сестрой испортили Новый год себе и гостям.', 'https://nukadeti.ru/audioskazki/mikhail-zoshhenko-jolka', 'nukadeti.ru_-_mikhail-zoshhenko-jolka.mp3', 'primaryA24.jpg', '4'),
(25, '\"Приемыш\" - Д.Мамин-Сибиряк', 'Рассказ Дмитрия Мамина-Сибиряка «Приёмыш» повествует историю дружбы старого сторожа Тараса и лебедя, у которого охотники застрелили родителей.', 'https://nukadeti.ru/audioskazki/mamin-sibiryak-priemysh', 'nukadeti.ru_-_mamin-sibiryak-priemysh.mp3', 'primaryA25.jpg', '4'),
(26, '\"Барбос и Жулька\" - А.И.Куприн', '«Барбос и Жулька» — рассказ Александра Ивановича Куприна, в котором рассказывается о дружбе двух собак: боевитого Барбоса и маленькой, деликатной Жульки.', 'https://nukadeti.ru/audiorasskazy/barbos-i-zhulka', 'nukadeti.ru_-_barbos-i-zhulka.mp3', 'primaryA26.jpg', '4'),
(27, '\"Стрижонок Скрип\" - В.Астафьев', '«Стрижонок Скрип» Виктора Астафьева — добрая и поучительная история о крошечном птенчике, который остался без мамы, но выжил и научился летать благодаря птицам своей стаи.', 'https://nukadeti.ru/audiorasskazy/strizhonok-skrip', 'nukadeti.ru_-_strizhonok-skrip.mp3', 'primaryA27.jpg', '4'),
(28, '\"Приключение Алисы\" - К.Булычев', 'Аннотация к книге Кира Булычёва «Приключения Алисы Селезнёвой»: 2\r\n\r\nВ книгу вошли пять историй про Алису, написанные специально для младших школьников. По первой фантастической повести, вошедшей в книгу, был снят мультфильм «Тайна Третьей планеты». После успеха мультфильма Кир Булычёв переписал несколько историй про Алису Селезнёву, и они вошли в книгу.\r\n\r\nСюжет: Алиса отправляется в путешествия на разные планеты, побывает даже в параллельных мирах и разгадывает замыслы коварных космических пиратов. Алиса Селезнёва очень сообразительная девочка, и смекалка выручает её в разных ситуациях. В самых опасных приключениях важнее всего оказывается доброе сердце и верные друзья.', 'https://deti-online.com/audioskazki/povesti-i-romany-mp3/priklyucheniya-alisy-alisa-selezneva/', '', 'primaryA28.jpg', '4'),
(29, '\"Русалочка\" - Х.К.Андерсен ', '«Русалочка» (дат. Den Lille Havfrue, в дословном переводе — «Маленькая морская дева» или «Маленькая Русалка») — всемирно известная сказка датского писателя Ханса Кристиана Андерсена, повествующая о юной морской деве, которая готова отказаться от своей жизни в море ради того, чтобы получить человеческую душу и любовь принца.', 'https://nukadeti.ru/audioskazki/andersen_rusalochka', 'nukadeti.ru_-_rusalochka.mp3', 'primaryA29.jpg', '4'),
(30, '\"Приключения Тома Сойера\" - М.Твен', '«Приключения Тома Сойера» Марка Твена — роман о приключениях мальчика, растущего в небольшом американском городке Сент-Питерсберг в штате Миссури. Действие происходит до событий Гражданской войны в США.\r\n\r\nСюжет: Том Сойер — мальчик примерно двенадцатилетнего возраста, живёт у тёти Полли, сестры своей покойной матери. Он тяготеет к свободной жизни и приключениям, постоянно втягивая в них своего приятеля Гекльберри Финна. ', 'https://nukadeti.ru/audiorasskazy/priklyucheniya-toma-sojera', '', 'primaryA30.jpg', '4'),
(31, '\"Путешествие Гулливера\" - Д.Свифт', '«Путешествие Гулливера» Джонатана Свифта — сатирико-фантастическая книга, в которой высмеиваются человеческие и общественные пороки.  \n\nАннотация: по воле судьбы корабельный врач Лемюэль Гулливер оказывается на судне, которое терпит крушение. Так начинаются 16-летние странствия героя: от Лилипутии до страны лошадей с разумом человека — гуингнгмов.  \n\nВ каждом путешествии писатель высмеивает разные пороки человека и общества. На примере Гулливера Свифт показывает, как человек приспосабливается к самым разнообразным ситуациям.  \n\nКнига сочетает фантастический роман-путешествие, памфлет и антиутопию. Это многогранный философский роман, адресованный людям всех возрастов. ', 'https://akniga.org/svift-dzhonatan-puteshestviya-gullivera-1', '', 'primaryA31.jpg', '4'),
(32, '\"Сказка о мертвой царевне и о семи богатырях\" - А.С.Пушкин', '«Сказка о мёртвой царевне и семи богатырях» Александра Пушкина рассказывает историю любви царевны и королевича Елисея. ', 'https://nukadeti.ru/audioskazki/pushkin_skazka_o_mjortvoj_carevne_i_o_semi_bogatyryakh', 'nukadeti.ru_-_skazka_o_mertvoj_carevne.mp3', 'primaryA32.jpg', '4'),
(33, '\"Детство\" - Л.Н.Толстой', '«Детство» — первая часть автобиографической трилогии, в которой писатель смешивает факты из собственной жизни с художественным вымыслом.  \r\n\r\nСюжет: 10-летний Николенька Иртеньев живёт в усадьбе с родителями. Он совершенно счастлив, беззаботен и окружён любовью. Но, похоже, жизнь его вскоре переменится — ведь отец принял решение увезти мальчиков (Николеньку и старшего брата Володю) с собой в Москву для дальнейшего обучения.', 'https://mishka-knizhka.ru/audio-rasskazy-dlya-detej/audio-rasskazy-tolstogo-l-n/detstvo-audio/', '', 'primaryA33.jpg', '4'),
(34, '\"Четыре желания\" - К.Д.Ушинский', '«Четыре желания» — это короткая и добрая история о мальчике Мите, который получает удовольствие, проживая каждый сезон: зиму, весну, лето и осень.', 'https://nukadeti.ru/audioskazki/ushinskij-chetyre-zhelaniya', 'nukadeti.ru_-_ushinskij-chetyre-zhelaniya.mp3', 'primaryA34.jpg', '4'),
(35, '\"Городок в табакерке\" - В.Ф.Одоевский', '«Городок в табакерке» — сказка Владимира Одоевского, в которой в сказочной форме рассказывается об устройстве музыкальной шкатулки («табакерки»). Впервые опубликована отдельным изданием в 1834 году, затем вошла в сборник «Сказки дедушки Иринея», вышедший в 1841 году. ', 'https://nukadeti.ru/audioskazki/gorodok_v_tabakerke', 'nukadeti.ru_-_gorodok_v_tabakerke.mp3', 'primaryA35.jpg', '4'),
(36, '\"Серебряное копытце\" - П.П.Бажов', '«Серебряное копытце» — сказ Павла Бажова из его сборника «Малахитовая шкатулка», созданный на основе уральского фольклора. Произведение написано в 1938 году.  \r\n\r\nАннотация: «Серебряное копытце» — сказка о доброте, милосердии и душевной щедрости. Это история удивительной дружбы маленькой девочки, её кошки Мурёнки и волшебного козлика Серебряное копытце.', 'https://nukadeti.ru/audioskazki/bazhov-serebryanoe-kopytce', 'nukadeti.ru_-_bazhov-serebryanoe-kopytce.mp3', 'primaryA36.jpg', '4'),
(37, '\"Аленький цветочек\" - С.Т.Аксаков', '«Аленький цветочек» — сказка Сергея Аксакова о любви, которая побеждает зло.  \r\n\r\nСюжет: купец отправляется в деловую поездку и спрашивает у дочек, что им привезти в качестве гостинца. Младшая дочь Настенька просит отца привезти ей аленький цветочек. Когда купец отыскал и сорвал заветный цветок, его настигло ужасное чудовище. Чудище отпустило похитителя цветка лишь с тем условием, что одна из дочек купца по доброй воле приедет жить в его дворец. Настенька вызывается ехать к чудищу, чтобы избавить от гибели любимого отца.', 'https://nukadeti.ru/audioskazki/aksakov_alenkij_cvetochek', 'nukadeti.ru_-_alenkij_cvetochek.mp3', 'primaryA37.jpg', '4');
INSERT INTO `primary_movies` (`id`, `title`, `description`, `video_url`, `image_url`, `class`) VALUES
(1, ' \"Аля, Кляксич и буква «А»\" - И.П.Токмакова', 'Маленькая Аля писала письмо маме, когда на середину страницы выбежала буква А и рассказала, что отвратительный Кляксич пробрался в Букварь и хочет заменить все буквы своими родственницами — кляксами. \n\nАля решает отправиться в волшебную страну Азбуку и прогнать злодея, но ей никак не справиться без помощи юных читателей. \n\nГероям книги понадобится помощь маленьких читателей: необходимо выполнить несложные, но интересные задания. С такой поддержкой Аля и её помощники добьются своей цели: спасут буквы и выгонят Кляксича из Азбуки. \n\nЖители Азбуки — сказочные буквы и знаки препинания — встретят своих читателей в образах весёлых человечков, птиц и животных.', 'https://rutube.ru/play/embed/09a35ae93217251a4e9fe5e1c01cdc98\n    ', 'primaryM1.jpg', '1'),
(2, '\"Сказка о рыбаке и рыбке\" - А.С.Пушкин', '«Сказка о рыбаке и рыбке» — поучительная сказка А. С. Пушкина, в которой в лёгкой, увлекательной форме показаны печальные последствия излишней жадности и слабоволия.', 'https://rutube.ru/play/embed/2f3b7f50862775c419eada3222c77905', 'primaryM2.jpg', '2'),
(5, '\"У Лукоморья дуб зеленый...\" - А.С.Пушкин', '«У лукоморья дуб зелёный» — это отрывок из поэмы Александра Пушкина «Руслан и Людмила». В нём описывается чудесное место, где живут сказочные персонажи: учёный кот на золотой цепи, леший, русалка, 30 морских витязей, царевна и серый волк, Баба Яга, Кащей и другие. ', 'https://my.mail.ru/video/embed/1998052879549648887', 'primaryM3.jpg', '2'),
(6, '\"Стрекоза и муравей\" - И.А.Крылов', 'Тёплое и щедрое лето — благодатная пора для песен и танцев, и не хочется Стрекозе думать о нужде и голоде зимы. Её сосед Муравей о лишениях холодной поры беспокоится всё лето. А кто окажется прав?', 'https://vk.com/video_ext.php?oid=484208578&id=456240375&hash=14413633b55cf151', 'primaryM4.jpg', '2'),
(7, '\"Лебедь, Щука и Рак\" - И.А.Крылов', '«Лебедь, Щука и Рак» — басня Ивана Андреевича Крылова, написанная в 1814 году.', 'https://rutube.ru/play/embed/3a7c2a56500bc08b2e504e864eab03f6', 'primaryM5.jpg', '2'),
(8, '\"Котёнок\" - Л.Н.Толстой', 'Рассказ Л. Н. Толстого «Котёнок» повествует о брате и сестре, которые заботились о маленьком котёнке. У них была кошка, и по весне она пропала. Дети пошли её искать и нашли под крышей амбара с пятью котятами. Мама котят раздала, а одного оставила детям. Дети заботились о котёнке: кормили его, играли, клали с собой спать.', 'https://rutube.ru/play/embed/e6590144259cf44f40ac76eb7674ebcd', 'primaryM6.jpg', '2'),
(9, '\"Музыкант\" - В.В.Бианки ', 'В рассказе Виталия Бианки «Музыкант» автор сравнивает человека и животное. Старый медвежатник любит играть на скрипке и старается сам научиться этому. ', 'https://ok.ru/videoembed/6925663603371?autoplay=1&ya=1', 'primaryM7.jpg', '2'),
(10, '\"Страшный рассказ\" - Е.И.Чарушин', 'Краткое описание рассказа Евгения Чарушина «Страшный рассказ»:\r\n\r\nМальчики Шура и Петя остались одни на даче у леса, так как родители ушли в гости. В темноте они услышали шуршание и начали храбриться, утверждая, что ничего не боятся. Вдруг за дверью раздались шаги, и Петя переполз к Шуре, они спрятались под одеялом. Шум продолжался, но в этот момент вернулись родители и зажгли свет.\r\n\r\nОни осмотрели дом и обнаружили, что шумел ёжик, забравшийся из леса. Родители аккуратно переместили ёжика в чулан, дали ему молока и мяса. С тех пор ёжик жил с мальчиками всё лето, и они больше его не боялись.', 'https://rutube.ru/play/embed/d6f149afc605d9be1c76e0171bc5abab', 'primaryM8.jpg', '2'),
(11, '\"Сказка о потерянном времени\" - Е.Л.Шварц', '«Сказка о потерянном времени» Евгения Шварца — поучительная история о том, как ленивые дети, впустую тратившие своё время, превратились в старичков.\r\n\r\nМальчик Петя Зубов любил убивать время — бездельничать, шалить и опаздывать в школу. И вот потерянное Петей время утекло от него к жуликам-колдунам. Мальчик в одно мгновение превратился в старичка, и теперь его не узнают друзья и родные.', 'https://vk.com/video_ext.php?oid=-173967414&id=456244588&hash=1df0f465599de66d', 'primaryM9.jpg', '4'),
(12, '\"Как я ловил человечков\" - Б.Житков', 'Рассказ Бориса Житкова «Как я ловил человечков» рассказывает о любопытном мальчике Боре, который, придумав человечков, которые живут на макете парусника, ослушался бабушку и чуть не испортил важную для неё вещь.', 'https://rutube.ru/play/embed/a26845bd6e5ff88895e63a383b0b6529', 'primaryM10.jpg', '4'),
(13, '\"Корзина с еловыми шишками\" - К.Г.Паустовский', '«Корзина с еловыми шишками» — рассказ советского писателя Константина Паустовского о том, как композитор Эдвард Григ написал музыкальную пьесу для дочери лесника. ', 'https://vkvideo.ru/video_ext.php?oid=-202346699&id=456240019&hash=596d7a12b208472d', 'primaryM11.jpg', '4'),
(14, '\"Ёлка\" - М.Зощенко', 'Рассказ М. М. Зощенко «Ёлка» повествует о воспоминании уже взрослого мужчины о том, как много лет назад они с сестрой испортили Новый год себе и гостям.', 'https://rutube.ru/play/embed/a241c05b0964a1c99186dfb373696f8b', 'primaryM12.jpg', '4'),
(15, '\"Тайна третьей планеты\" - по повести К.Булычева \"Путешествие Алисы\"', '«Тайна третьей планеты» — советский полнометражный научно-фантастический[1] мультфильм Романа Качанова, экранизация повести Кира Булычёва «Путешествие Алисы» (другой вариант названия — «Алиса и три капитана») из цикла «Приключения Алисы». Для режиссёра, мультфильм стал его четвёртой режиссёрской работой, выполненной в технике традиционной рисованной мультипликации.', 'https://my.mail.ru/video/embed/2543467619667716502', 'primaryM13.jpg', '4'),
(16, '\"Русалочка\" - Х.К.Андерсен ', '«Русалочка» (дат. Den Lille Havfrue, в дословном переводе — «Маленькая морская дева» или «Маленькая Русалка») — всемирно известная сказка датского писателя Ханса Кристиана Андерсена, повествующая о юной морской деве, которая готова отказаться от своей жизни в море ради того, чтобы получить человеческую душу и любовь принца.', 'https://vk.com/video_ext.php?oid=-220394706&id=456241086&hash=35a672b26283fc9d', 'primaryM14.jpg', '4'),
(17, '\"Приключения Тома Сойера\" - М.Твен', '«Приключения Тома Сойера» Марка Твена — роман о приключениях мальчика, растущего в небольшом американском городке Сент-Питерсберг в штате Миссури. Действие происходит до событий Гражданской войны в США.\n\nСюжет: Том Сойер — мальчик примерно двенадцатилетнего возраста, живёт у тёти Полли, сестры своей покойной матери. Он тяготеет к свободной жизни и приключениям, постоянно втягивая в них своего приятеля Гекльберри Финна. ', 'https://vk.com/video_ext.php?oid=389888742&id=456239652&hash=840ede732e90decc', 'primaryM15.jpg', '4'),
(18, '\"Путешествие Гулливера\" - Д.Свифт', '«Путешествие Гулливера» (Gulliver\'s Travels) — полнометражный цветной мультфильм 1939 года, экранизация первой части одноимённой тетралогии Джонатана Свифта.  \r\n\r\nСюжет: в результате кораблекрушения путешественник Гулливер оказывается в стране Лилипутии, а её обитатели, крохотные человечки, берут «великана» в плен. Постепенно чужак начинает постигать особенности уклада и узнаёт, что Лилипутия воюет с соседним островом. Возможно, Гулливер — единственный, кто может остановить этот длительный конфликт между маленькими жителями разных стран.', 'https://ok.ru/videoembed/6966897936935?autoplay=1&ya=1\n', 'primaryM16.jpg', '4'),
(19, '\"Сказка о мертвой царевне и о семи богатырях\" - А.С.Пушкин', '«Сказка о мёртвой царевне и о семи богатырях» — советский мультипликационный фильм 1951 года по одноимённой сказке Александра Пушкина.  \r\n\r\nСюжет: новая жена царя хочет избавиться от своей падчерицы и отправляет её в лес на верную гибель. Царевна находит приют в лесной чаще у семи богатырей. Царица, узнав об этом, обманом отравляет её.', 'https://rutube.ru/play/embed/cb6f9a111394216c5a4652a4ccbd3688\r\n    ', 'primaryM17.jpg', '4'),
(20, '\"Серебряное копытце\" - П.П.Бажов', '«Серебряное копытце» — советский рисованный мультипликационный фильм 1977 года режиссёра Геннадия Сокольского, экранизация одноимённого сказа Павла Бажова. \r\n\r\n«Серебряное копытце» — сказ Павла Бажова из его сборника «Малахитовая шкатулка», созданный на основе уральского фольклора. Произведение написано в 1938 году.  \r\n\r\nАннотация: «Серебряное копытце» — сказка о доброте, милосердии и душевной щедрости. Это история удивительной дружбы маленькой девочки, её кошки Мурёнки и волшебного козлика Серебряное копытце.', 'https://vk.com/video_ext.php?oid=-225620373&id=456240067&hash=b831238a71174d1e', 'primaryM18.jpg', '4'),
(21, '\"Аленький цветочек\" - С.Т.Аксаков', '«Аленький цветочек» — сказка Сергея Аксакова о любви, которая побеждает зло.  \r\n\r\nСюжет: купец отправляется в деловую поездку и спрашивает у дочек, что им привезти в качестве гостинца. Младшая дочь Настенька просит отца привезти ей аленький цветочек. Когда купец отыскал и сорвал заветный цветок, его настигло ужасное чудовище. Чудище отпустило похитителя цветка лишь с тем условием, что одна из дочек купца по доброй воле приедет жить в его дворец. Настенька вызывается ехать к чудищу, чтобы избавить от гибели любимого отца.', 'https://rutube.ru/play/embed/728d39c0a9797b5b94d74d9b56ebeb13', 'primaryM19.jpg', '4');
INSERT INTO `primary_performances` (`id`, `title`, `description`, `video_url`, `image_url`, `class`) VALUES
(1, '\"Детство\" - Л.Н.Толстой', '«Детство. Отрочество. Юность» — телеспектакль 1973 года из трёх частей, созданный Петром Фоменко по одноимённой трилогии Льва Толстого. \n\nЧасть 1 «Детство»:  \n\nНиколенька Иртеньев — чувствительный и мечтательный мальчик, который, как и многие дети, грезит о той чудесной поре, когда он станет взрослым, когда не нужно будет зубрить уроки и рано ложиться спать. Он счастлив, и все вокруг кажутся ему счастливыми.', 'https://ok.ru/videoembed/1547910056508?autoplay=1', 'primaryPF1.jpg', '4');
INSERT INTO `primary_poems` (`id`, `title`, `description`, `pdf_url`, `epub_url`, `fb2_url`, `txt_url`, `image_url`, `class`) VALUES
(1, '\"Мы играли в хохотушки\" - И.П.Токмакова', 'Книга Ирины Токмаковой «Алёша»: цикл стихов про шестилетнего мальчика Алёшу.\r\n\r\nСодержание: «Скоро в школу», «С добрым утром!», «Бабушкина сказка», «Телефон», «Купите собаку», «Ссора», «Один дома», «Обижалки», «Мы играли в хохотушки», «Новая квартира», «Почитай мне, мама!», «Подарили собаку!», «Фигурное катание», «Песенка, которую Алёша сочинил на прогулке», «Письмо».\r\n\r\nКнига издана в 1986 году издательством «Малыш». К ней приложены цветные иллюстрации М. Беломлинского.', 'Токмакова И.П._Алёша.pdf', '', '', '', 'primaryP1.jpg', '1'),
(2, '\"Разговор лютика и жучка\" - И.П.Токмакова', 'Шуточное стихотворение, которое создаёт весёлое настроение. ', 'Разговор лютика и жучка.pdf', '', '', '', 'primaryP2.jpg', '1'),
(3, '\"Про медведя\" - Г.В.Сапгир', 'Автор делится своими впечатлениями о том, как разговаривает его знакомый медведь.', 'pro-medvedya.pdf', '', '', 'pro-medvedya.txt', 'primaryP3.jpg', '1'),
(4, '\"Автобус номер двадцать шесть\" - С.Я.Маршак ', '«Автобус номер 26» — стихотворение Самуила Маршака, в котором с юмором рассказывается, как в общественном транспорте ведут себя животные, птицы, обитатели морей и даже пресмыкающиеся. \r\n\r\nПроизведение помогает в весёлой форме запомнить название животных и алфавит, ведь каждое животное начинается на следующую букву алфавита. Также стихотворение позволяет запомнить, как не надо себя вести в общественном транспорте.', 'Автобус номер 26.pdf', '', '', '', 'primaryP4.jpg', '1'),
(5, 'Саша Черный', 'Саша Чёрный (настоящее имя Александр Михайлович Гликберг) — русский поэт Серебряного века, писатель, журналист. Получил широкую популярность благодаря сатирическим стихотворным фельетонам.\r\n\r\nРодился 1 (13) октября 1880 года в Одессе, в зажиточной еврейской семье. С ранних лет любил природу, в солнечных лучах, деревьях, цветах и играющих детях видел гармонию.\r\n\r\nК 9 годам родители устроили мальчика в белоцерковную гимназию, но в 15 лет он сбежал из дома и стал жить на улице. Сашу забрала к себе тётя, сестра его отца. Она отправила племянника в Санкт-Петербургскую гимназию, но там мальчик не справился с алгеброй и был отчислен за неуспеваемость.\r\n\r\nВ 20 лет он служил в армии, потом целый год работал в таможне на Австро-Венгерской границе, а после таможни трудился в редакции газеты.\r\n\r\nСаша Чёрный не сразу стал детским поэтом. Вначале он писал стихи и прозу для взрослых читателей.\r\n\r\nВ 1920 году Саша Чёрный эмигрировал из России, много раз перемещался из одной страны в другую: Германия, Италия, Литва, Рим, Париж.\r\n\r\nУмер 5 августа 1932 года на 52-м году жизни — перенапряг сердце, помогая соседям тушить пожар. ', 'PS-Chern.pdf', '', '', '', 'primaryP5.jpg', '1'),
(6, '\"Ласточка примчалась\" - А.Н.Майков ', '«Ласточка примчалась» — стихотворение Аполлона Николаевича Майкова, в котором он передает переменчивый характер весны и уверенность в том, что она непременно наступит.', 'maikov.pdf', '', '', '', 'primaryP6.jpg', '1'),
(7, '\"Травка зеленеет\" - А.Н.Плещеев ', 'Стихотворение А. Н. Плещеева «Травка зеленеет…» — описание картины наступающей весны. В нём встречаются образы зелёной травки, солнца, ласточки, весны, дороги.\r\n\r\nНастроение стихотворения радостное, это ощущается благодаря ласковым словам: травка, солнышко, краше, милей. Всё находится в движении: зеленеет, блестит, летит. Ощущение движения передаётся и благодаря чёткому ритму и рифме (блестит — летит, милей — скорей).', 'Stihi.pdf', '', '', '', 'primaryP7.jpg', '1'),
(8, 'Е.Ф.Трутнева', 'Евгения Фёдоровна Трутнева (1884–1959) — пермская детская поэтесса. 12\r\n\r\nПосле окончания в 1902 году гимназии в Перми работала в конторе Пермской железной дороги, в канцелярии губернского земства, секретарём и классной наставницей в пермской женской гимназии имени И. С. Тургенева. С 1931 года работала в педагогическом институте сначала библиотекарем, затем лаборантом на кафедре.\r\n\r\nСтихи начала писать ещё в гимназические годы. В 1935 году в пермской газете «Звезда» появилось её стихотворение «Я — сын трудового народа». С 1936 года стихи Трутневой стали печатать систематически в уральской пионерской газете «Всходы коммуны» и архангельской молодёжной газете «Северный комсомолец». В 1940 году Евгения Фёдоровна стала постоянной сотрудницей детского журнала «Мурзилка».\r\n\r\nВо время Великой Отечественной войны Евгения Трутнева заведовала литературной частью Пермского театра кукол и написала для него две пьесы: «В тылу врага» и «Сказка». Также она написала ряд песен, положенных на музыку ленинградскими композиторами.\r\n\r\nПри жизни Е. Ф. Трутневой было опубликовано более 40 сборников её стихов: «Подарок», «Снежный город», «Родина», «Победа», «Тропинка», «Грибы», «В родном краю», «Времена года», «У золотого колоса», «Мой календарь» и другие.\r\n\r\nВ 1944 году она стала членом Союза советских писателей. Неоднократно избиралась депутатом Пермского городского Совета.', 'Trutneva_E_F_Zima_vesna_leto_osen_M_1956.pdf', '', '', '', 'primaryP8.jpg', '1'),
(9, '\"РРРЫ\" - Г.М.Кружков ', 'Стихотворение Р. Кружкова \"РРРЫ\" представляет собой яркий пример экспериментальной поэзии, в которой автор использует необычные звуковые сочетания и игру слов для передачи эмоций и создания образов.\r\n\r\nВ этом произведении Кружков обращает внимание на ритм и звук, что позволяет читателю ощутить динамику и энергетику текста. Стихотворение может быть интерпретировано как отражение внутреннего состояния человека, его борьбы с внешним миром и стремления к самовыражению.\r\n\r\nКружков часто использует абсурдные и комические элементы, что создает контраст между серьезностью темы и легкостью подачи. Это стихотворение может вызывать у читателя различные ассоциации и чувства, заставляя задуматься о значении слов и звуков в языке.\r\n\r\nВ целом, \"РРРЫ\" можно рассматривать как попытку исследовать границы поэзии и выразить сложные человеческие переживания через нестандартные формы и приемы.', 'rrry.pdf', 'rrry.epub', 'rrry.fb2', 'rrry.txt', 'primaryP9.jpg', '1'),
(10, '\"Федотка\" - К.И.Чуковский ', '«Федотка» — стихотворение Корнея Чуковского, в котором маленький мальчик Федотка горюет и заливается слезами, что у него никого из близких людей нет.\r\n\r\nОднако на самом деле у него большая семья: «Только мама, да дядя, да тётка, только папа да дедушка с бабушкой». Автор с иронией относится к мальчику и называет его сироткой, потому что Федотка только притворяется несчастным. Скорее всего, он капризничает, хочет, чтобы на него обратили внимание.\r\n\r\nСтихотворение учит беречь членов семьи, проявлять к ним заботу, ценить то, чем наградила судьба, и не взывать попусту к жалости.', 'kornej-chukovskij-fedotka.pdf', 'kornej-chukovskij-fedotka.epub', 'kornej-chukovskij-fedotka.fb2', 'kornej-chukovskij-fedotka.txt', 'primaryP10.jpg', '1'),
(11, '\"Привет\" - О.Дриз ', '«Привет» — стихотворение Овсея Дриза, в котором рассказывается о мальчике, получившем от тёти печенье, конфеты, варежки и шарф, а также горячий привет.\r\n\r\nМальчик находит всё, что прислала тётя, кроме привета, и решает, что это тоже какой-то предмет, вещь, которую можно потрогать, что-то вроде пирога.\r\n\r\n\r\n\"Прислала мне тётя\r\nПеченья,\r\nКонфет,\r\nВарежки,\r\nШарф\r\nИ горячий привет.\r\n\r\nВот варежки,\r\nШарф\r\nИ конфеты\r\nС печеньем…\r\nГде же привет?\r\nЯ ищу с нетерпеньем.\r\n\r\nИ с чем он:\r\nС грибами?\r\nС капустой?\r\nС вареньем?\r\n\r\nНаверное, мама\r\nПривет этот прячет,\r\nЧтобы остыл:\r\nВидно, очень горячий.\"', 'ovsey-driz-privet.pdf', '', '', '', 'primaryP11.jpg', '1'),
(12, '\"Стук\" - О.Е.Григорьев ', '«Стук» — стихотворение Олега Григорьева, в котором герой берёт бумагу и перо, рисует утюг, рвёт листок и швыряет его в ведро, после чего в ведре раздаётся стук.', 'Стук.pdf', '', '', '', 'primaryP12.jpg', '1'),
(13, '\"Кулинаки-пулинаки\" - И.Пивоварова ', '«Кулинаки-пулинаки» — шуточное стихотворение Ирины Пивоваровой, напоминающее небылицу. \r\n\r\nСборник «Тикитак» Ирины Пивоваровой предназначен для детей старшего дошкольного и младшего школьного возраста.\r\n\r\nСодержание:\r\n\r\n«Тикитак»; \r\n«Гостеприимный крот»; \r\n«Ну и что?»; \r\n«Репетиция»; \r\n«Две ящерки»; \r\n«Глиняная корова»; \r\n«Кулинаки-пулинаки» (скороговорка); \r\n«Жила-была собака». \r\nГод издания: 1968, издательство «Малыш».', 'Pivovarova_Tikitak_RuLit_Me_797504.pdf', '', '', '', 'primaryP13.jpg', '1'),
(14, '\"Вежливый ослик\" - И.Пивоварова ', 'Стихотворение И. Пивоваровой «Вежливый ослик» описывает ослика, который показывает себя всем только с положительной стороны — он воспитанный и вежливый. Но стоит его собеседнику отвернуться — как ослик тут же начинает говорить про него всякие гадости.\r\n\r\nАвтор не советует быть похожим на такого ослика, потому что с таким обманщиком и лицемером никому не будет приятно дружить. Если так будет поступать человек, то вряд ли он когда-нибудь найдёт себе друзей.', 'pivovarova-vezhliviy-oslik.pdf', '', '', '', 'primaryP14.jpg', '1'),
(15, '\"Цап Царапыч\" - М.С.Пляцковский ', 'Стихотворение М. Пляцковского «Цап Царапыч» описывает большого и смирного кота, который давно живёт в семье. \r\n\r\nПо сюжету кот получает своё имя: так его прозвали за то, что он может отомстить, если ему сделать плохо. Единственное, что может его потревожить, — когда кто-нибудь наступит на его огромный мохнатый хвост. За реакцию на такую неосмотрительность кот и получит своё имя.\r\n\r\nПри прочтении стихотворения сразу представляется образ Цапа Царапыча: большой, очень пушистый, скорее всего ленивый домашний зверь. Единственное, что может его потревожить, — когда кто-нибудь наступит на его хвост.', 'Цап Царапыч.pdf', '', '', '', 'primaryP15.jpg', '1'),
(16, '\"Подарок\" - Е.Благинина ', 'Стихотворение Елены Благининой «Подарок» рассказывает о девочке, у которой была игрушка, и которую она отдала лучшей подруге. ', 'Подарок.pdf', '', '', '', 'primaryP16.jpg', '1'),
(17, '\"Кто первый\" - В.Орлов ', 'Стихотворение Владимира Орлова «Кто первый?» описывает ситуацию, когда два хороших добрых друга неожиданно подрались. Когда их начинают спрашивать о причинах этой драки, они вдруг с удивлением понимают, что сами не знают этой причины.', 'Кто первый_.pdf', '', '', '', 'primaryP17.jpg', '1'),
(18, '\"Бараны\" - С.Михалков', '«Бараны» — стихотворение Сергея Михалкова, написанное в 1937 году.\r\n\r\nС. Михалков в стилистике фольклорной потешки, аллегорической форме повествует о необходимости бороться с недостатками характера. ', 'Бараны.pdf', '', '', '', 'primaryP18.jpg', '1'),
(19, '\"Трезор\" - С.Михалков ', 'Стихотворение Сергея Михалкова «Трезор» рассказывает о щенке, которого оставили одного в доме. Чтобы он не сбежал, дверь закрыли на массивный замок. \r\n\r\nГлавная мысль стихотворения в том, что хозяева чувствуют вину за то, что оставили щенка дома одного. Это показывает глубокое чувство ответственности за своего питомца.', 'Трезор.pdf', '', '', '', 'primaryP19.jpg', '1'),
(20, '\"Совет\" - Р.Сеф ', '«Совет» — стихотворение детского поэта Романа Семёновича Сефа, в котором описана ссора чашки и блюдца. ', 'Совет.pdf', '', '', '', 'primaryP20.jpg', '1'),
(21, '\"В магазине игрушек\" - В.Берестов ', '«В магазине игрушек» — стихотворение Валентина Берестова, в котором он говорит о том, что друзей не покупают и не продают, их находят люди, а также создают. \r\n\r\nПо сюжету герой стихотворения предлагает в магазине игрушек огромнейший выбор друзей и подружек. ', 'В магазине игрушек.pdf', '', '', '', 'primaryP21.jpg', '1'),
(22, '\"Лягушата\" - В.Берестов ', 'Стихотворение Валентина Берестова «Лягушата» описывает жизненный цикл лягушки от икринки до полноценной особи. Автор указывает звучание лягушачьего голоса и звук от прыжков.', 'Лягушата.pdf', '', '', '', 'primaryP22.jpg', '1'),
(23, '\"Моя родня\" - Я.Аким ', '«Моя родня» — шуточное стихотворение Я. Акима, в котором герой рассказывает о своей семье и друзьях. ', 'Моя родня.pdf', '', '', '', 'primaryP23.jpg', '1'),
(24, '\"Хороший день\" - С. Маршак ', 'Стихотворение Самуила Маршака «Хороший день» рассказывает о необыкновенном дне обычного среднестатистического мальчика из Советского союза. Необыкновенность дня заключается в том, что у родителей выходной и ребёнок сможет провести совместное время со своим папой.', 'Хороший день.pdf', '', '', '', 'primaryP24.jpg', '1'),
(25, '\"Про дружбу\" - Ю.Энтин ', 'Стихотворение Юрия Энтина «Про дружбу» описывает дружбу как близкие, доверительные и ценные отношения, которые жизненно необходимы каждому. Как солнцу нужен ветер, как траве роса, а бабочке цветок — так и друг важен для каждого из нас.\r\n\r\nАвтор наставляет: никогда не ссориться с друзьями, настоящую дружбу нужно беречь и ценить.', 'Про дружбу.pdf', '', '', '', 'primaryP25.jpg', '1'),
(26, '\"Кошка\" - Г.Сапгир ', 'Стихотворение Г. Сапгира «Кошка» представляет собой диалог между кошкой и её хозяином. ', 'Сапгир Г.В. «Кошка».pdf', '', '', '', 'primaryP26.jpg', '1'),
(27, '\"Есть в осени первоначальной\" - Ф.И.Тютчев ', '«Есть в осени первоначальной…» — лирическое стихотворение Фёдора Ивановича Тютчева, написанное 22 августа 1857 года.\r\n\r\nВ стихотворении поэт создаёт образ ранней осени, когда воздух прозрачен, а вечера пронизаны солнечным светом. « Короткая и дивная пора» — это образ бабьего лета в самом начале осени, когда ещё тепло и только-только начали опадать листья. Стоит ясная и спокойная погода, но, кажется, она в любую секунду может перемениться, поэтому поэт называет её «хрустальной».', 'Есть в осени первоначальной....pdf', '', '', '', 'primaryP27.jpg', '2'),
(28, '\"Чародейкою зимою\" - Ф.И.Тютчев', 'Стихотворение Ф. И. Тютчева «Чародейкою зимою…» посвящено описанию зимнего леса. Он будто бы спит волшебным сном, ничто в нём не трепещет, не шевелится. Лес неподвижен под снежной бахромой в полной тишине.', 'Чародейкою зимою….pdf', '', '', '', 'primaryP28.jpg', '2'),
(29, '\"Осень\" - К.Д.Бальмонт', 'В стихотворении «Осень» Константин Бальмонт описывает начало осенней поры. Он отмечает, что в лесу поспевает брусника, дни становятся холоднее, деревья стоят в разноцветном уборе, а стаи птиц улетают. Вся природа готовится к тому, что скоро наступят осенние холода. ', 'konstantin_balmont.pdf', '', '', '', 'primaryP29.jpg', '2'),
(30, '\"Снежинка\" - К.Д.Бальмонт', 'Стихотворение Константина Бальмонта «Снежинка» описывает жизненный путь крохотной снежинки, её хоть и недлинный, но увлекательный полёт. Снежинка спускается с чистого и бескрайнего неба, попадая совершенно в другой для себя мир.', 'Balmont-K.-D.-Snezhinka.pdf', '', '', '', 'primaryP30.jpg', '2'),
(31, '\"Осень наступила\" - А.Н.Плещеев ', 'В стихотворении А. Н. Плещеева «Осень наступила...» описывается поздняя осень, когда уже прошла золотая пора и вся листва с деревьев и кустов облетела. Погода стоит пасмурная, небо серое, идёт моросящий дождь. Грусти добавляет и отсутствие птиц, которые отправились на юг, где и будут зимовать.', 'Plescheev_A._Osen_nastupila.pdf', '', '', '', 'primaryP31.jpg', '2'),
(32, '\"Ласточки пропали\" - А.А.Фет ', 'Стихотворение «Ласточки пропали» Афанасия Фета (1854) создаёт картину осени, которая соотносится с печальным и тревожным состоянием лирического героя.', 'fet_lastochki_propali.pdf', '', '', '', 'primaryP32.jpg', '2'),
(33, '\"Осень\" - А.К.Толстой', 'В стихотворении А. К. Толстого «Осень. Обсыпается весь наш бедный сад...» поэт рисует осенний пейзаж. Урожай собран, сад уже пуст, деревья сбрасывают листья, которые летят, подхваченные ветром. Украшение осени — это ярко-красные кисти рябин, которые виднеются на дне долин. ', 'Осень.pdf', '', '', '', 'primaryP33.jpg', '2'),
(34, 'С.А.Есенин - Том 1. Стихотворения', 'В этом томе Сергея Есенина есть такие стихотворения - \"Закружилась листва золотая\", \"Поет зима-аукает\", \"Береза\", и многие другие.', '', 'Есенин Сергей. Том 1. Стихотворения - royallib.com.epub', 'Есенин Сергей. Том 1. Стихотворения - royallib.ru.fb2', '', 'primaryP34.jpg', '2'),
(35, '\"Сухие листья\" - В.Брюсов ', 'Стихотворение Валерия Брюсова «Сухие листья» воссоздаёт в воображении читателя картины ранней осени — тихой и романтичной. Это время, когда ещё не идут длинные косые дожди. Дни ещё теплы и солнечны, а с деревьев, мягко кружась в воздухе и шурша под ногами, опадают листья.', 'valerij-bryusov-suxie-listya.pdf', 'valerij-bryusov-suxie-listya.epub', 'valerij-bryusov-suxie-listya.fb2', '', 'primaryP35.jpg', '2'),
(36, '\"Хитрые грибы\" - В.Бересков ', 'Стихотворение Валентина Берестова «Хитрые грибы» рассказывает о неудачном походе грибника в лес. Герой уходит в густой лес в надежде собрать полную корзину грибов, в нетерпении осматривает все места, где обычно растут грибы, но ничего не находит. Он считает грибы хитрыми, потому что они искусно спрятались от него.', 'Хитрые грибы.pdf', '', '', '', 'primaryP36.jpg', '2'),
(37, '\"Быль для детей\" - С. Михалков ', '«Быль для детей» — сборник Сергея Михалкова, который содержит стихи о Великой Отечественной войне, мужестве русского народа и долгожданной победе.\r\n\r\nАвтор рассказывает ребятам о борьбе советских солдат с немецко-фашистскими захватчиками и подвиге тружеников тыла, бросивших все силы на помощь фронту. ', 'Быль для детей.pdf', '', '', '', 'primaryP37.jpg', '2'),
(38, '\"У лукоморья дуб зеленый\" - А. С. Пушкин ', '«У лукоморья дуб зелёный» — это отрывок из поэмы А. С. Пушкина «Руслан и Людмила», вступление к ней.\r\n\r\nВ стихотворении описывается волшебный мир сказок, фантастических персонажей и магических мест. Лирический герой, выступающий от первого лица, делится впечатлениями о сказочной стране, где происходит множество чудес.', 'У Лукоморья дуб зеленый.pdf', '', '', '', 'primaryP38.jpg', '2'),
(39, '\"Вот север, тучи нагоняя...\" - А.С.Пушкин', '«Вот север, тучи нагоняя...» — стихотворение Александра Пушкина, картина зимней природы из романа «Евгений Онегин». ', 'pushkin-vot-sever-tuchi-nagonyy.pdf', '', '', '', 'primaryP39.jpg', '2'),
(40, '\"Жила-была собака\" - И.Пивоварова ', '«Жила-была собака» Ирины Михайловны Пивоваровой — история очень доброй собаки монструозного размера. Стихотворение написано не позднее 1968 года. ', 'Жила-была собака.pdf', '', '', '', 'primaryP40.jpg', '2'),
(41, '\"Мороз\" - Е.А.Благинина', 'Стихотворение Елены Александровны Благининой «Мороз» посвящено теме заботы о природе и всём живом в зимние дни. Автор призывает помогать птичкам и собачкам, всему живому в природе, не быть равнодушным человеком.', 'Благинина Е.А._ Огонек. Стихи.(1978).pdf', '', '', '', 'primaryP41.jpg', '2'),
(42, '\"Первый снег\" - Аким Я.Л. ', '«Первый снег» — стихотворение Якова Акима, которое описывает первый снег как лёгкий, новый и имеющий вкус и запах. \r\n\r\nПо сюжету кот приносит на лапах первый снег, который кружится у ребят над головой, успевает расстелить пуховый платок на мостовой и белеет вдоль заборов. Автор делает вывод, что скоро с горок полетят салазки, и можно будет снова строить крепость во дворе.', 'Первый снег.pdf', '', '', '', 'primaryP42.jpg', '2'),
(43, '\"Наши царства\" - М.И.Цветаева', 'В стихотворении Марины Цветаевой «Наши царства» описывается мир детства, воображения и свободы.\r\n\r\nДве девочки представляют себя феями и живут в своём собственном сказочном мире, полном природной красоты и радости. Их «царства» — это ручейки, деревья, поля, скалы и даже прошлогодние вишни во мху. ', 'cvetaeva-nashi-carstva.pdf', '', '', '', 'primaryP43.jpg', '4'),
(44, '\"Бабушкины сказки\" - С.А.Есенин', 'Стихотворение Сергея Есенина «Бабушкины сказки» было написано в 1915 году. В нём поэт обращается к своим детским годам и с любовью вспоминает бабушку, чьи сказки он так любил слушать.', 'esenin-babushkini-skazki.pdf', '', '', '', 'primaryP44.jpg', '4'),
(45, '\"Лебедушка\" - С.А.Есенин', 'Стихотворение Сергея Есенина «Лебедушка» повествуется в жанре древнерусской былины. Простой эпизод гибели лебедя от коршуна превращается в чудесную сказку. ', 'esenin-lebedushka.pdf', '', '', '', 'primaryP45.jpg', '4'),
(46, '\"Русь\" - И.С.Никтин', 'В стихотворении Ивана Саввича Никитина «Русь» описываются бескрайние просторы страны. Здесь есть и пространные степи, и высокие горы, и широкие моря, и шумные леса, и плодородные поля. Страна раскинулась далеко на север и на юг. ', 'Русь.pdf', '', '', '', 'primaryP46.jpg', '4'),
(47, '\"Родине\" - С.Д.Дрожжин', '«Родине» — стихотворение Спиридона Дрожжина, написанное в 1871 году в жанре патриотической лирики. ', 'Родине.pdf', '', '', '', 'primaryP47.jpg', '4'),
(48, 'Ф.И.Тютчев - Полное собрание стихотворений (\"Ещё земли печален вид...\" и др.)', '«Полное собрание стихотворений» Ф. И. Тютчева — издание, в котором собраны все стихотворения поэта. \r\nСтихотворение Ф. И. Тютчева «Ещё земли печален вид…»(стр.156) относится к пейзажно-философской лирике. \r\n', 'Ф. Тютчев Полное собрание стихотворений.pdf', '', '', '', 'primaryP48.jpg', '4'),
(49, 'А.А.Фет - \"Полное собрание стихотворений\"', '«Полное собрание стихотворений» А. А. Фета, издание 1959 года, выпущено Ленинградским отделением издательства «Советский писатель» в серии «Библиотека поэта. Большая серия».  \r\n\r\nОписание: в сборник вошли стихотворения разных лет, в том числе не вошедшие в основное собрание, послания, посвящения, эпиграммы, поэмы, переводы, песни. \r\n\r\n\"Весенний дождь\" - 139 страница.\r\n\"Бабочка\" - 303 страница.', 'fet_polnoe_sobranie_stikhotvoreny_1959__ocr.pdf', '', '', '', 'primaryP49.jpg', '4'),
(50, '\"Весна, весна! Как воздух чист!\" - Е.А.Баратынский', 'Стихотворение Евгения Баратынского «Весна, весна! Как воздух чист!» описывает весеннее пробуждение природы. Автор передаёт запахи весны («Как воздух чист»), краски весны («Как ясен небосклон!»), ощущения тёплого прикосновения весны («Ласкаясь к солнечным лучам…»).', 'baratinskiy-vesna-vesna.pdf', '', '', '', 'primaryP50.jpg', '4'),
(51, '\"Саша\" - Н.А.Некрасов', '«Саша» — поэма Николая Некрасова, написанная в 1855 году. Это произведение о юношеских чувствах, первой девичьей влюблённости и душевном смятении. ', 'nikolaj-nekrasov-sasha.pdf', 'nikolaj-nekrasov-sasha.epub', 'nikolaj-nekrasov-sasha.fb2', 'nikolaj-nekrasov-sasha.txt', 'primaryP50.jpg', '4'),
(52, '\"Листопад\" - И.А.Бунин', '«Листопад» И. А. Бунина — стихотворение, которое относится к пейзажной лирике и описывает осеннюю пору. Автор создаёт картину осеннего леса, но проводит параллели между состоянием природы и жизненными циклами, обращаясь к философским темам жизни и смерти.', 'ivan-bunin-listopad.pdf', '', '', '', 'primaryP51.jpg', '4');
INSERT INTO `senior_audiobooks` (`id`, `title`, `description`, `audio_url`, `audio_url2`, `image_url`, `class`) VALUES
(1, '\"Рождественская песнь в прозе\" - Чарльз Диккенс', '«Рождественская песнь в прозе» Чарльза Диккенса — это история про грубого и жадного Эбинизера Скруджа, для которого Рождество — всего лишь очередной обычный день. 4\n\nНо однажды в канун праздника Эбинизеру является призрак его давно умершего делового партнёра, который утверждает, что тому следует измениться. Грядущее путешествие сквозь время с Духами Рождества должно растопить жестокое сердце Скруджа и превратить его из высокомерного и бесчувственного скряги в сердечного и заботливого человека. 4\n\nКнига заставляет задуматься о ценности человечности в холодном мире материального прогресса. Она напоминает, что даже самые серьёзные проблемы можно решить, изменив свой внутренний мир и восстановив человечность.\nВ основе драмы лежит любовный треугольник. Супруга купца Тихона, Катерина, изменяет ему с племянником богатого купца Дикого Борисом.\n\nТрагедия Катерины показала перелом, происходивший в народном сознании, когда на первое место стала выходить свобода личности и волеизъявления, противостоя существующим традициям и укладу жизни.', 'https://akniga.org/dikkens-rozhdestvenskaya-pesn-v-proze-gvozdicky?ysclid=m6t0ksktlt665775260', 'rozhdestvenskie-povesti-rozhdestvenskaya-pesn-v-proze-section-audio.zip', 'seniorA1.jpg', '10'),
(2, '\"Соборяне\" - Н.С.Лесков', '«Соборяне» Николая Лескова (1831–1895) — роман-хроника, в котором описываются последние годы жизни двух священников и дьякона в вымышленном городе Старгороде.  \n\n\nАннотация: автор сам признавал, что «Соборяне» — одно из лучших его произведений. В центре хроники — судьба смелого проповедника, ревнителя благочестия и патриота, священника Савелия Туберозова.', 'https://akniga.org/leskov-nikolay-soboryane', '', 'seniorA2.jpg', '10'),
(3, '\"Красное и черное\" - Стендаль', '«Красное и чёрное» — роман Фредерика Стендаля, одно из наиболее известных произведений автора.     \n\nАннотация: действие происходит во Франции после Июльской революции 1830 года. Главный герой, Жюльен Сорель, выделяется умом и честолюбием в провинциальном городке Верьер. С детства он мечтает о военной славе Наполеона, его также привлекает и церковная карьера. Внезапная любовь жены мэра госпожи де Реналь смешала его далеко идущие планы, а увлечение светской красавицей маркизой де Ла Моль привело его к гибели. ', 'https://akniga.org/stendal-frederik-krasnoe-i-chernoe-doronin', '', 'seniorA3.jpg', '10'),
(4, '\"Война и мир\" - Л.Н.Толстой', '«Война и мир» — роман-эпопея Льва Толстого, одно из крупнейших произведений мировой литературы.  \n\nАннотация: в романе рассказывается о сложном периоде в истории России и всей Европы — эпохе завоевательных походов императора Наполеона в Восточную Европу и Россию, с 1805 по 1812 год. Автор подробно описывает войну — ход боевых действий от Аустерлица до Бородино и Березины, и мир — жизнь в России в это же время.', 'https://drevbook.org/ln-tolstoy/voyna-i-mir/', '', 'seniorA4.jpg', '10'),
(5, '\"Анна Каренина\" - Л.Н.Толстой', '«Анна Каренина» Льва Толстого — классика мировой литературы, трагическая история о любви и счастье, которое разбилось о суровые стены реальности, общественных устоев и стереотипов.          \n\nАннотация: роман повествует о трагичной любви замужней дамы высшего света к блестящему офицеру Вронскому, о счастливой семейной жизни Константина Левина и Кити Щербацкой на лоне патриархально-усадебного быта. Автор обличает городскую праздную жизнь, буржуазную культуру, даёт масштабную картину нравов и быта дворянской среды Петербурга и Москвы второй половины ХIХ века. ', 'https://predanie.ru/tolstoy-lev-nikolaevich/anna-karenina/slushat/?play=1', '', 'seniorA5.jpg', '10'),
(6, '\"Дуэль\" - А.П.Чехов', '«Дуэль» — повесть Антона Чехова о любви и чести, о способности сохранять чувство собственного достоинства в самых сложных жизненных ситуациях. ', 'https://my.mail.ru/video/embed/7461642160740241224', '', 'seniorA6.jpg', '10'),
(7, '\"Попрыгунья\" - А.П.Чехов', 'Аннотация рассказа А. П. Чехова «Попрыгунья»: Ольга Ивановна всегда считала своего мужа ординарным и скучным человеком, не понимала, как можно всё своё время отдавать медицине и естественным наукам, совершенно не интересоваться искусством и светской жизнью. Постоянно пребывая в поиске общества и внимания знаменитых и талантливых людей, она слишком поздно осознала, что по-настоящему великий и необыкновенный человек всё время был рядом с ней.', 'https://rutube.ru/play/embed/a116524872f054cf059d232c5c3a0086', '', 'seniorA7.jpg', '10'),
(8, '\"Дом с мезонином\" - А.П.Чехов', '«Дом с мезонином» — рассказ Антона Павловича Чехова, написанный в 1896 году. Это история о несбывшейся любви и простом человеческом счастье, за которое влюблённые даже не попытались бороться. ', 'https://knigavuhe.org/book/dom-s-mezoninom-5/', '', 'seniorA8.jpg', '10'),
(9, '\"Человек в футляре\" - А.П.Чехов', '«Человек в футляре» — рассказ Антона Чехова, первая часть цикла «Маленькая трилогия».  \n  \n\n\nАннотация: поучительная история о простом учителе гимназии Беликове, который добровольно загнал себя в футляр. В физическом смысле он в любую погоду носил пальто и галоши, отгораживался от улицы высоким воротником. В духовном — всю жизнь проводил, отгораживаясь от мира, кутаясь и страшась «как бы чего не вышло».', 'https://akniga.org/chehov-anton-chelovek-v-futlyaer', '', 'seniorA9.jpg', '10'),
(10, '\"Крыжовник\" - А.П.Чехов', '«Крыжовник» — рассказ Антона Чехова, опубликованный в 1898 году и входящий в цикл «Маленькая трилогия» (вместе с рассказами «Человек в футляре» и «О любви»).      \n\n\nАннотация: в произведении автор поднимает вопросы поиска смысла жизни и истинного счастья, обнажает конфликт между духовными ценностями и материальными благами. ', 'https://akniga.org/chehov-anton-kryzhovnik-8', '', 'seniorA10.jpg', '10'),
(11, '\"Собачье сердце\" - М.А.Булгаков', '«Собачье сердце» — повесть Михаила Булгакова, написанная в 1925 году. Произведение наполнено иронией и сатирой на новые веяния, распространённые в России начала XX века.    \r\n\r\n\r\n\r\nАннотация: профессор Филипп Филиппович спасает от смерти бездомного пса Шарика, чтобы провести на нём эксперимент по выявлению функций гипофиза. Вопреки ожиданиям, выясняется: гипофиз даёт не ожидаемое учёным омоложение, а полное очеловечивание. В поведении Шарика вскоре проявляются черты донора, трусливого пьяницы и хулигана Клима Чугункина.  ', 'https://akniga.org/bulgakov-mihail-sobache-serdce-4', '', 'seniorA11.jpg', '11'),
(12, '\"Отцы и дети\" - И.С.Тургенев', '«Отцы и дети» — знаменитый роман Ивана Тургенева, который считается одним из самых значительных произведений о взаимоотношениях поколений.     \n\nАннотация: в произведении поднимается вечная тема отцов и детей, актуальная и в наше время. Автор демонстрирует конфликт двух поколений через противостояние молодого нигилиста Базарова и утончённого аристократа Павла Петровича Кирсанова.', 'https://akniga.org/turgenev-is-otcy-i-deti', '', 'seniorA12.jpg', '10'),
(13, '\"Обломов\" - И.А.Гончаров', '«Обломов» — роман Ивана Александровича Гончарова, одно из важнейших произведений русской литературы XIX века.      \n\nАннотация: «Обломов» — история неудавшейся карьеры, несбывшейся любви, упущенных возможностей, но при этом, как ни странно, история жизни вполне счастливого, по собственному мнению, человека.', 'https://my.mail.ru/music/playlists/обломов-и-а-гончаров-1808566250882', '', 'seniorA13.jpg', '10'),
(14, '\"Что делать?\" - Н.Г.Чернышевский', '«Что делать?» — роман русского философа, журналиста и литературного критика Николая Чернышевского, написанный в 1862–1863 годах во время заключения в Петропавловской крепости Санкт-Петербурга.    \r\n\r\nАннотация: в романе нет прямых ответов на вопрос, как улучшить или радикально изменить существующее общество, но отражена целая палитра истинно человеческого общения. Произведение формирует понимание, какими должны быть настоящие отношения между людьми. ', 'https://akniga.org/chernyshevskiy-nikolay-chto-delat-sushkov', '', 'seniorA14.jpg', '10'),
(15, '\"Гроза\" - А.Н.Островский', '«Гроза» — драма А. Н. Островского, написанная в 1859 году. Произведение относится к направлению реализма и затрагивает такие темы, как семья и брак, любовь, счастье, нравственность, жизнь и смерть, надежда, власть (над человеком). ', 'https://my.mail.ru/video/embed/1961646056689202512', '', 'seniorA15.jpg', '10'),
(16, '\"Кому на Руси жить хорошо\" - Н.А.Некрасов', 'Поэма «Кому на Руси жить хорошо» — главное произведение Н. А. Некрасова, вершина его творчества. Задумав поэму в 1863 году, вскоре после отмены крепостного права, поэт работал над ней с большими перерывами до последних дней.  ', 'https://akniga.org/nekrasov-na-komu-na-rusi-zhit-horosho', '', 'seniorA16.jpg', '10'),
(17, '\"Детство. Отрочество. Юность\" - Л.Н.Толстой', '«Детство. Отрочество. Юность» — псевдо-автобиографическая трилогия Льва Толстого, основанная на дневниках молодого писателя.     \r\n\r\n\r\nАннотация: трилогия повествует о становлении личности ребёнка, проблемах самоопределения и установления взаимоотношений с внешним миром.', 'https://akniga.org/tolstoy-lev-detstvo-otrochestvo-yunost', '', 'seniorA17.jpg', '10'),
(18, '\"История одного города\" - М.Е.Салтыков-Щедрин', '«История одного города» — сатирический роман-хроника Михаила Евграфовича Салтыкова-Щедрина, написанный в 1869–1870 годах.     \r\n\r\nАннотация: под видом «летописи» вымышленного города Глупова автор обличает типичные пороки российского общества: глупость, самодурство и взяточничество власть имущих, казнокрадство и низкопоклонничество чиновников, лень, необразованность и мракобесие.', 'https://akniga.org/saltykov-schedrin-mihail-evgrafovich-istoriya-odnogo-goroda', '', 'seniorA18.jpg', '10'),
(19, '\"Пармская обитель\" - Стендаль', '«Пармская обитель» — второй после «Красного и чёрного» роман французского писателя Стендаля об эпохе Реставрации. \r\n\r\n\r\n\r\nАннотация: действие происходит в Италии, стране сильных страстей и великой культуры. Темой становится судьба поколения итальянцев, которое вступило в жизнь в конце XVIII века, страстно мечтая о независимости родины, однако битва при Ватерлоо ознаменовала крах восторженных иллюзий. ', 'https://akniga.org/stendal-frederik-parmskaya-obitel', '', 'seniorA19.jpg', '10'),
(20, '\"Человеческая комедия\" - Оноре де Бальзак', '«Человеческая комедия» — цикл произведений французского писателя Оноре де Бальзака, который включает все романы, написанные им за двадцатилетнюю творческую карьеру.  \r\n\r\n\r\n\r\nАннотация: Бальзак исследовал нравы французского общества XIX века, период с 1815 по 1848 годы. Он назвал свой труд «Человеческой комедией» по аналогии с «Божественной комедией» Данте, делая акцент на реалистическом методе трактовки современной действительности. ', 'https://uknig.com/series/2356', '', 'seniorA20.jpg', '10'),
(21, '\"Евгения Гранде\" - Оноре де Бальзак', '«Евгения Гранде» — роман Оноре де Бальзака, написанный в 1833 году и входящий в цикл «Сцены провинциальной жизни».     \r\n\r\n\r\n\r\nАннотация: в основе сюжета лежит противостояние духовного и материального, которое не ослабевает на протяжении всей книги. Это история о несбыточных мечтах и утраченных иллюзиях. Главная проблема романа — противопоставление любви к человеку и любви к деньгам, выраженная в образах Евгении Гранде и её отца соответственно.', 'https://akniga.org/balzak-onore-de-evgeniya-grande', '', 'seniorA21.jpg', '10'),
(22, '\"Преступление и наказание\" - Ф.М.Достоевский', '«Преступление и наказание» (1866) Фёдора Михайловича Достоевского — социально-психологический роман с ярко выраженным философским подтекстом.  \r\n\r\n\r\n\r\n\r\nАннотация: главный герой романа, студент Родион Раскольников, «задавлен бедностью». Он поддался популярным в то время идеям о том, что все люди делятся на «тварей дрожащих» и «наполеонов». Так он решается на убийство злой и жадной старухи-процентщицы, чтобы разом изменить свою судьбу и поправить положение своей семьи. Философия вступает в конфликт с совестью и душевным состоянием героя: он не в силах справиться с тяжким бременем преступления. ', 'https://akniga.org/dostoevskiy-fedor-prestuplenie-i-nakazanie-2', '', 'seniorA22.jpg', '10'),
(23, '\"Очаровательный странник\" - Н.С.Лесков', '«Очарованный странник» — повесть Николая Семёновича Лескова, написанная в 1872–1873 годах, входит в цикл «Праведники».  \r\n\r\n\r\nАннотация: «Очарованный странник» — история жизни русского мужика, настоящего богатыря, который после долгих скитаний нашёл утешение в вере. Повесть вошла в цикл легенд о русских праведниках.', 'https://akniga.org/leskov-nikolay-ocharovannyy-strannik-3', '', 'seniorA23.jpg', '10'),
(24, '\"Леди Макбет Мценского уезда\" - Н.С.Лесков', 'Аннотация повести Николая Лескова «Леди Макбет Мценского уезда»:   \r\n\r\n\r\nКатерина Измайлова, молодая красавица, по расчёту выданная за нелюбимого, годами томится в удушливой чопорной атмосфере богатого купеческого дома, изнывая от скуки. Внезапно она загорается пагубной, непреодолимой страстью к дерзкому приказчику Сергею. Отныне она уже не способна думать ни о чём, кроме возлюбленного, и готова ради него на любое безумство и преступление.', 'https://akniga.org/leskov-nikolay-ledi-makbet-mcenskogo-uezda', '', 'seniorA24.jpg', '10'),
(25, '\"О любви\" - А.П.Чехов', '«О любви» — рассказ Антона Чехова, созданный в 1898 году. Это произведение представляет собой завершение так называемой «Маленькой трилогии» автора, в которую также включены рассказы «Человек в футляре» и «Крыжовник».     \r\n\r\n\r\nАннотация: любовь — это не всегда страсть и буря эмоций, не всегда ухаживания и свадьба. Иногда любовь — это тихо смотреть на любимого человека, вздыхать украдкой в одиночестве, не смея и боясь порушить счастье любимой.', 'https://vk.com/video_ext.php?oid=-220378519&id=456239043&hash=2c9c65cd08d0ddc0', '', 'seniorA25.jpg', '10'),
(26, '\"Деревня\" - И.А.Бунин', '«Деревня» — повесть Ивана Алексеевича Бунина, написанная в 1909–1910 годах и впервые опубликованная в 1910 году.  \r\n\r\n\r\n\r\nАннотация: произведение представляет собой описание русской деревни на стыке XIX–XX веков. В основе серой и унылой жизни русских крестьян лежат патриархальные устои, неизменность устаревших традиций и несгибаемая воля к изменениям. ', 'https://akniga.org/bunin-ivan-derevnya', '', 'seniorA26.jpg', '11'),
(27, '\"Иоанн Рыдалец\" - И.А.Бунин', '«Иоанн Рыдалец» — рассказ Ивана Бунина, написанный в 1913 году.  \r\n\r\n\r\n\r\nАннотация: история сумасшедшего Ивана, от которого не было покоя окружающим в округе села Грешного. Его бы так и забыли, если бы не причуда князя, велевшего похоронить себя рядом с Иваном.', 'https://my.mail.ru/music/songs/иван-бунин-иоанн-рыдалец-чит-м-глузский-441d7877a6f2edd76c286b3f7f896275', '', 'seniorA27.jpg', '11'),
(28, '\"Господин из Сан-Франциско\" - И.А.Бунин', '«Господин из Сан-Франциско» — рассказ Ивана Алексеевича Бунина, написанный в 1915 году. Произведение представляет собой притчу, в которой автор размышляет о смысле жизни, тщетности материальных благ и неизбежности смерти.   \r\n\r\n\r\n\r\nАннотация: главный герой рассказа, состоятельный американец, всю жизнь посвятил накоплению богатства, но в итоге умирает, так и не успев насладиться плодами своего труда.', 'https://knigavuhe.org/book/gospodin-iz-san-francisko-2/', '', 'seniorA28.jpg', '11'),
(29, '\"Олеся\" - А.И.Куприн', '«Олеся» — повесть Александра Куприна, написанная в 1898 году. Главная тема произведения — трагическая любовь городского барина Ивана Тимофеевича и молодой девушки Олеси, считавшейся ведьмой и живущей в одинокой избушке на болоте в чаще леса со своей бабкой Мануйлихой.', 'https://akniga.org/kuprin-aleksandr-olesya-2', '', 'seniorA29.jpg', '11'),
(30, '\"Поединок\" - А.И.Куприн', '«Поединок» — повесть Александра Ивановича Куприна, опубликованная в 1905 году. В произведении описывается история конфликта молодого подпоручика Ромашова со старшим офицером.    \r\n\r\n\r\n\r\nАннотация: повесть рассказывает о буднях русской армии в мирное время, но в первую очередь о любви, которая, по мнению писателя, — созидающая сила, помогающая открыть лучшие свойства человеческой души.', 'https://akniga.org/kuprin-poedinok', '', 'seniorA30.jpg', '11'),
(31, '\"Гранатовый браслет\" - А.И.Куприн', '«Гранатовый браслет» Александра Куприна — повесть о безответной любви мужчины к замужней женщине. Название книги связано с сюжетом: княгиня Вера Шеина получает на именины гранатовый браслет от тайного поклонника и хочет отослать его обратно, но для этого нужно узнать, кто же является отправителем.  ', 'https://akniga.org/kuprin-aleksandr-granatovyy-braslet-4', '', 'seniorA31.jpg', '11'),
(32, '\"На дне\" - М.Горький', '«На дне» — пьеса Максима Горького, написанная в конце 1901 — начале 1902 года. В январе 1904 года произведение получило Грибоедовскую премию.     \r\n\r\n\r\n\r\nАннотация: действие разворачивается в ночлежном доме, где живут люди с самого дна социума: бедняки, воры, проститутки, чернорабочие. Некоторые из них отчаянно пытаются выбраться, другие опускают руки. Отношения между персонажами сложные, постоянно вспыхивают склоки.', 'https://akniga.org/gorkiy-maksim-na-dne', '', 'seniorA32.jpg', '11'),
(33, '\"Разгром\" - А.А.Фадеев', '«Разгром» (1926) — автобиографический роман Александра Александровича Фадеева (1901–1956), одно из самых известных произведений, посвящённых Гражданской войне в России. ', 'https://akniga.org/fadeev-aleksandr-razgrom', '', 'seniorA33.jpg', '11'),
(34, '\"Мы\" - Е.И.Замятин', '«Мы» — роман-антиутопия Евгения Замятина, написанный в 1920 году. В СССР не печатался до 1988 года, потому что считался «идеологически враждебным» и «клеветническим».       \r\n\r\n\r\n\r\nАннотация: роман об «обществе равных», в котором человеческая личность сведена к «нумеру». В нём унифицировано всё — одежда и квартиры, мысли и чувства. Нет ни семьи, ни прочных привязанностей.', 'https://akniga.org/zamyatin-evgeniy-my', '', 'seniorA34.jpg', '11'),
(35, '\"Мастер и Маргарита\" - М.А.Булгаков', '«Мастер и Маргарита» Михаила Булгакова — многоплановое произведение, которое сочетает в себе сатиру и философскую условность, фэнтези, библейские образы, мифологические архетипы, элементы детектива, авантюрного романа и другие.  ', 'https://smotrim.ru/audio/2571247', '', 'seniorA35.jpg', '11'),
(36, '\"Доктор Живаго\" - Б.Л.Пастернак', '«Доктор Живаго» Бориса Пастернака — роман о жизни российской интеллигенции в период от начала XX века до Великой Отечественной войны.      \r\n\r\n\r\n\r\nАннотация: академик Д. Лихачёв назвал «Доктора Живаго» духовной автобиографией Пастернака. ', 'https://knigavuhe.org/book/doktor-zhivago/', '', 'seniorA36.jpg', '11'),
(37, '\"Тихий Дон\" - М.А.Шолохов', '«Тихий Дон» — социально-психологический роман-эпопея Михаила Шолохова, одно из главных произведений отечественной литературы XX века.     \r\n\r\n\r\n\r\nАннотация: «Тихий Дон» — масштабное эпическое полотно, которое повествует о донском казачестве в переломный период истории России — годы Первой мировой и Гражданской войны. По словам самого Шолохова, в это время «в смертной драке брат идёт на брата, сын — на отца».', 'https://rutube.ru/play/embed/998d40f79b6c678633a85e558a62654d', '', 'seniorA37.jpg', '11'),
(38, '\"Страна Муравия\" - А.Т.Твардовский', '«Страна Муравия» А. Т. Твардовского — поэтическое повествование о становлении нового, социалистического сознания крестьянина-середняка, о крушении индивидуалистического, собственнического начала в душе трудового человека. ', 'https://ok.ru/videoembed/3940856040012?autoplay=1&ya=1', '', 'seniorA38.jpg', '11'),
(39, '\"Василий Тёркин\" - А.Т.Твардовский', '«Василий Тёркин. Книга про бойца» — поэма Александра Твардовского, одно из главных произведений в творчестве поэта, получившее всенародное признание.     \r\n\r\n\r\n\r\nАннотация: герой поэмы, Василий Тёркин, — собирательный образ русского солдата, олицетворяющего воина-победителя, которому присущи воинская отвага, умение воевать и не унывать ни при каких обстоятельствах. ', 'https://mishka-knizhka.ru/audio-stihi-dlya-detej/sbornik-audio-stihov/vasilij-terkin-audio/', '', 'seniorA39.jpg', '11'),
(40, '\"Один день Ивана Денисовича\" - А.Солженицын', '«Один день Ивана Денисовича» — это повесть Александра Солженицына, которая описывает важный факт из жизни целой страны. Главный герой — Иван Денисович Шухов, заключённый Щ-854. Его образ близок обобщённым образам русского крестьянина и солдата: он жизнелюбив, умеет находить счастье в труде и в простых вещах. В то же время образ неоднозначен, ведь Шухов хитрит и изворачивается, прислуживает Цезарю. Но Иван Денисович не приспособленец, в борьбе за выживание ему важно не потерять человеческого достоинства.', 'https://akniga.org/solzhenicyn-aleksandr-odin-den-ivana-denisovicha-1', '', 'seniorA40.jpg', '11');
INSERT INTO `senior_movies` (`id`, `title`, `description`, `video_url`, `image_url`, `class`) VALUES
(1, '\"Гроза\"(1933г.)- Островский А.Н.', '«Гроза» — драма А. Н. Островского, написанная в 1859 году. Произведение относится к направлению реализма и затрагивает такие темы, как семья и брак, любовь, счастье, нравственность, жизнь и смерть, надежда, власть (над человеком). ', 'https://rutube.ru/play/embed/479e6a6735242cebb88faa3d2478239c', 'seniorM1.jpg', '10'),
(2, '\"Оно\"(1989г.)(\"История одного города\") - М.Е.Салтыков-Щедрин', '«Оно» — двухсерийный фильм 1989 года режиссёра Сергея Овчарова, основанный на романе Салтыкова-Щедрина «История одного города» и некоторых других его произведениях.      \n\nАннотация: в метафорической и гротескной форме в фильме передаётся история России с призвания варягов до конца XX века.', 'https://ok.ru/videoembed/591043758665?autoplay=1&ya=1', 'seniorM2.jpg', '10'),
(3, '\"Красное и черное\"(1997г.) - Стендаль', '«Красное и чёрное» (1997) — экранизация одноимённого романа Стендаля, жанр — драма.      \n\nАннотация: Франция, XIX век. В центре событий — судьба молодого и амбициозного человека Жюльена Сореля, тонкий ум и честолюбие которого помогли ему покинуть унылую провинцию. « Красное» и «чёрное» символизируют возможные карьеры Сореля — красный мундир офицера и чёрную сутану священника.', 'https://rutube.ru/play/embed/487c856cadbf7830d2fbb5c675b6bb2e', 'seniorM3.jpg', '10'),
(4, '\"Война и мир\"(1956г.) - Л.Н.Толстой', '«Война и мир» (англ. War and Peace) — кинофильм режиссёра Кинга Видора, вышедший на экраны в 1956 году. Экранизация романа Льва Толстого.   \n\nАннотация: эпическая драма о международном конфликте, опасных приключениях, захватывающей интриге и трагической любви на фоне широкомасштабного вторжения Наполеона в Россию.  ', 'https://rutube.ru/play/embed/6ed5fea82d2f2662be22a5a75b3badea\n    ', 'seniorM4.jpg', '10'),
(5, '\"Анна Каренина\"(1967г.) - Л.Н.Толстой', '«Анна Каренина» (1967) — двухсерийная драма, снятая по мотивам одноимённого романа Льва Толстого.           \n\nАннотация: в основе мелодрамы — трагическая история любви и измены, судьба женщины, ради страсти решившейся бесповоротно изменить свою жизнь. Анна Каренина, не в силах отказаться от своих чувств к избраннику, отвергает мораль светского общества, пытаясь защитить своё право на любовь. Муж разлучает Анну с сыном, её знакомые отворачиваются от неё.', 'https://vk.com/video_ext.php?oid=326139589&id=456239536&hash=fecc646a247f387e', 'seniorM5.jpg', '10'),
(6, '\"Плохой хороший человек\"(\"Дуэль\")(1973г.) - А.П.Чехов', '«Плохой хороший человек» — фильм 1973 года, вольная экранизация повести А. П. Чехова «Дуэль». Режиссёр — Иосиф Хейфиц. ', 'https://vk.com/video_ext.php?oid=-6018165&id=168958924&hash=baad6ed0249508d9', 'seniorM6.jpg', '10'),
(7, '\"Попрыгунья\"(1955г.) - А.П.Чехов', '«Попрыгунья» (1955) — советская мелодрама, экранизация одноимённого рассказа А. П. Чехова.  \n\n\nАннотация: Ольга Ивановна Дымова увлечена поисками своего идеала. Она ищет в каждом встреченном ею мужчине нечто уникальное, выдающееся и отличительное от всех прочих. Зачастую критерии этого поиска крайне размыты и нестабильны, зависимы от ветреного характера их носителя. Тем более, что Ольга Ивановна не желает замечать массу выдающихся качеств личности своего мужа, Осипа Степановича Дымова. Но очевидное обнаруживается тогда, когда уже поздно. ', 'https://vk.com/video_ext.php?oid=238710450&id=170321343&hash=55d829e9af6fd0ed', 'seniorM7.jpg', '10'),
(8, '\"Дом с мезонином\"(1960г.) - А.П.Чехов', '«Дом с мезонином» — художественный фильм 1960 года по одноимённому рассказу А. П. Чехова. ', 'https://vk.com/video_ext.php?oid=-34261602&id=456244704&hash=9e90ee5490fe558c', 'seniorM8.jpg', '10'),
(9, '\"Человек в футляре\"(1939г.) - А.П.Чехов', '«Человек в футляре» (1939) — советский художественный фильм режиссёра Исидора Анненского по одноимённому рассказу Антона Чехова.  \n\n\nАннотация: тихий сельский учитель обретает надежду устроить личную жизнь. ', 'https://ok.ru/videoembed/5106961102?autoplay=1', 'seniorM9.jpg', '10'),
(10, '\"Крыжовник\"(1967г.) - А.П.Чехов', '«Крыжовник» (1967) — экранизация одноимённого рассказа Антона Чехова, социальная драма.      \n\n\n\nАннотация: на примере судьбы человека автор показывает, как крыжовник и усадьба могут стать смыслом жизни и сделать человека счастливым. ', 'https://ok.ru/videoembed/2059060513520?autoplay=1&ya=1', 'seniorM10.jpg', '10'),
(11, '\"Собачье сердце\"(1988г.) - М.А.Булгаков', '«Собачье сердце» (1988) — советский комедийно-драматический научно-фантастический фильм режиссёра Владимира Бортко, основанный на романе Михаила Булгакова «Собачье сердце».      \r\n\r\n\r\n\r\nАннотация: профессор Преображенский, светило медицинской науки, проводит эксперимент по пересадке человеческих органов собаке, в результате чего создаётся новый человек. Однако у него остаются повадки животного и характер донора — алкоголика и дебошира. Тем не менее новый человек, взявший себе имя Полиграф Полиграфович Шариков, вписывается в нарождающуюся советскую действительность и даже занимает должность небольшого начальника. ', 'https://vk.com/video_ext.php?oid=-227148217&id=456239542&hash=87f51a11cd8e60e6', 'seniorM11.jpg', '11'),
(12, '\"Отцы и дети\"(2008г.) - И.С.Тургенев', '«Отцы и дети» — знаменитый роман Ивана Тургенева, который считается одним из самых значительных произведений о взаимоотношениях поколений.      \n\nАннотация: в произведении поднимается вечная тема отцов и детей, актуальная и в наше время. Автор демонстрирует конфликт двух поколений через противостояние молодого нигилиста Базарова и утончённого аристократа Павла Петровича Кирсанова.', 'https://vk.com/video_ext.php?oid=-224297244&id=456239350&hash=85af867964304394', 'seniorM12.jpg', '10'),
(13, '\"Несколько дней из жизни И.И.Обломова\"(1979г.)', '«Несколько дней из жизни И. И. Обломова» (1979) — советский художественный полнометражный двухсерийный цветной фильм, снятый Никитой Михалковым на киностудии «Мосфильм» по мотивам романа И. А. Гончарова «Обломов».      \n\nАннотация: события фильма происходят в середине XIX века, когда жизнь большинства помещиков протекала по веками заведённым обычаям, как будто во сне. Главный герой, Илья Ильич Обломов, — владелец небольшого имения, который вечно проводит время в праздности, преимущественно предаваясь сну и безделью. ', 'https://my.mail.ru/video/embed/4362993483410064292', 'seniorM13.jpg', '10'),
(14, '\"Евгения Гранде\"(1960г.) - Оноре де Бальзак', '«Евгения Гранде» — советский драматический фильм 1960 года, поставленный режиссёром Сергеем Алексеевым на основе спектакля Малого театра по одноимённому роману Оноре де Бальзака.    \n\nАннотация и краткое описание сюжета:\n\nСобытия фильма разворачиваются во французской провинции XIX века. Глава зажиточной семьи — Феликс Гранде, скряга и деспот, тайно копит деньги, ни на кого не тратит, экономит на всём. Жена Гранде страдает, она слабая женщина, не смеет возражать мужу. Красивая дочь — Евгения Гранде, знакомится со своим кузеном Шарлем, приехавшим из Парижа. Между ними возникает любовь, Шарль называет Евгению своей женой, но вынужден уехать за границу. Евгения обещает ждать его. ', 'https://my.mail.ru/video/embed/1210712441353145678', 'seniorM14.jpg', '10'),
(15, '\"Преступление и наказание\"(2007г.) - Ф.М.Достоевский', 'Это социально-психологический роман с ярко выраженным философским подтекстом. \n\nГлавный герой — Родион Раскольников — студент, который «задавлен бедностью». Он поддался популярным в то время идеям о том, что все люди делятся на «тварей дрожащих» и «наполеонов». Так он решается на убийство злой и жадной старухи-процентщицы, чтобы разом изменить свою судьбу и поправить положение своей семьи. \n\nФилософия вступает в конфликт с совестью и душевным состоянием героя: он не в силах справиться с тяжким бременем преступления. \n\nРоман ставит перед читателем важнейшие нравственно-мировоззренческие вопросы — о вере, совести, грехе и об искуплении через страдание.', 'https://vk.com/video_ext.php?oid=113971814&id=167601343&hash=42fa5802a38e15e5', 'seniorM15.jpg', '10'),
(16, '\"Очарованный странник\"(1990г.) - Н.С.Лесков', '«Очарованный странник» (1990) — советский художественный фильм, экранизация одноимённой повести Н. С. Лескова.             \n\nАннотация: на пароходе между пассажирами завязался спор о том, что такое праведник и есть ли он на Руси. Никто из спорщиков не хочет представлять собственную судьбу на суд общественности, чтобы определить степень праведности. Один лишь православный батюшка решается на исповедь. Он признаётся, что, прежде всего, грешен, ведь были в его жизни и попытки самоубийства, и тяжкий плен у татар и даже несчастная любовь. ', 'https://my.mail.ru/video/embed/563944152451038742', 'seniorM16.jpg', '10'),
(17, '\"Леди Макбет мценского уезда\"(1989г.) - Н.С.Лесков', '«Леди Макбет Мценского уезда» (1989) — драма режиссёра Романа Балаяна по мотивам одноимённой повести Николая Лескова.  \n\n\n\nАннотация: молодая жена богатого купца Катерина Измайлова томится в несчастливом браке. Будучи по натуре женщиной страстной и чувствительной, Катерина больше всего страдала от нехватки ощущения полноты жизни и любви. Поэтому она потянулась к работнику мужа Сергею.', 'https://ok.ru/videoembed/7784753203870?autoplay=1&ya=1', 'seniorM17.jpg', '10'),
(18, '\"Пармская обитель\"(1948 г.) - Стендаль', '«Пармская обитель» (La Chartreuse de Parme, La Certosa di Parma) — полнометражный фильм 1948 года, драма, мелодрама.  13\r\n\r\nАннотация: действие разворачивается в начале XIX века в Италии, в герцогстве Пармском. Молодой маркиз Фабрицио возвращается на родину из Неаполя, где он учился, чтобы стать священником. В Парме дорогого племянника с нетерпением ждёт герцогиня Джина Сансеверина, которая воспитывала Фабрицио с шестилетнего возраста.', 'https://my.mail.ru/video/embed/3506992095996940669', 'seniorM18.jpg', '10'),
(19, '\"Духи рождества\"(1999 г.) (На основе \"Рождественская песнь в прозе\" - Чарльз Диккенс)', '«Духи Рождества» (A Christmas Carol, 1999). Это американская сказочная драма в жанрах фантастики и драмы.  \r\n\r\nСюжет: угрюмый ростовщик Эбенезер Скрудж накануне Рождества особенно подвержен плохому настроению. Ночью в сочельник к нему являются Духи Рождества, чтобы указать Скруджу на то, насколько он неправильно живёт. Каждый из трёх Духов покажет Скруджу последствия его поведения, которые отразились на его прошлом, настоящем и будущем.  \r\n\r\nВ основу фильма легла новелла Чарльза Диккенса «Рождественская песнь в прозе».', 'https://vk.com/video_ext.php?oid=-224743475&id=456240505&hash=8ea90dd47f08a903', 'seniorM19.jpg', '10'),
(20, '\"О любви\" - А.П.Чехов', '«О любви» (2003) — российская мелодрама, снятая по мотивам рассказов А. П. Чехова «Доктор», «Медведь», «Володя».     \r\n\r\nАннотация: герой фильма, которого играет Александр Абдулов, пытается ответить на вопрос, что побудило расстаться с жизнью молодого человека 15 лет. Он рассказывает историю своей жизни, в которой были и счастливые, и горькие минуты. Измена любимой жены и предательство лучшего друга заставили его покинуть семью, когда Володе, которого он ранее считал своим сыном, было всего около пяти. Последовали годы одиночества, разрубить которые смогло лишь случайное знакомство и несостоявшаяся дуэль. ', 'https://vk.com/video_ext.php?oid=-43671558&id=163779435&hash=8d68b3486ea81d41', 'seniorM20.jpg', '10'),
(21, '\"Олеся\" - А.И.Куприн', '«Олеся» (1971) — советский драматический фильм режиссёра Бориса Ивченко, экранизация одноимённой повести Александра Куприна.  \r\n\r\n\r\n\r\nАннотация: история любви русского интеллигента и неграмотной девушки-колдуньи.', 'https://vk.com/video_ext.php?oid=67005410&id=168116040&hash=db6e3833810da3fd', 'seniorM21.jpg', '11'),
(22, '\"Поединок\" - А.И.Куприн', '«Куприн. Поединок» — мини-сериал 2014 года в жанрах мелодрама и драма.      \r\n\r\n\r\n\r\nАннотация: молодой и перспективный подпоручик Ромашов поступает в полк, где видит творящуюся здесь несправедливость. Он один пытается хоть как-то исправить ситуацию, быть честным по отношению к себе и людям, но это не всегда получается.', 'https://ok.ru/videoembed/7374570130011?autoplay=1&ya=1', 'seniorM22.jpg', '11'),
(23, '\"Гранатовый браслет\" - А.И.Куприн', '«Гранатовый браслет» (1964) — художественный фильм режиссёра Абрама Роома, снятый по мотивам одноимённой повести Александра Ивановича Куприна.     \r\n\r\n\r\n\r\nАннотация: история возвышенной неразделённой любви мелкого чиновника к молодой светской даме.', 'https://rutube.ru/play/embed/6ba1558b5de969f60829c8db523eb4c8', 'seniorM23.jpg', '11'),
(24, '\"Двенадцать\" - А.А.Блок', '«Двенадцать» — поэма Александра Блока, написанная в январе 1918 года. Произведение стало откликом на две революции: Февральскую и Октябрьскую.      \r\n\r\n\r\n\r\nАннотация: в поэме отражён революционный Петроград, смешение слов, образов и идей — от мельчайшего до великого, от пошлого и кабацкого до божественного. ', 'https://vk.com/video_ext.php?oid=-218236787&id=456239245&hash=dec5890f012db2d1', 'seniorM24.jpg', '11'),
(25, '\"Юность наших отцов\" (экр. \"Разгром\" - А.А.Фадеев)', '\"Юность наших отцов\" — советская героическая киноповесть 1958 года, экранизация романа А. Фадеева «Разгром». Режиссёры: Михаил Калик, Борис Рыцарев. ', 'https://ok.ru/videoembed/6368628443684?autoplay=1', 'seniorM25.jpg', '11'),
(26, '\"Мы\" - Е.И.Замятин', '«Мы» (нем. Wir) — немецкий научно-фантастический фильм 1982 года по мотивам романа «Мы» (1920) русского писателя Евгения Замятина.', 'https://vk.com/video_ext.php?oid=-86085064&id=456240807&hash=776fdc81e82c652a', 'seniorM26.jpg', '11'),
(27, '\"Мастер и Маргарита\" - М.А.Булгаков', '«Мастер и Маргарита» (1994) — российский фильм режиссёра Юрия Кары, экранизация одноимённого романа Михаила Булгакова.      \r\n\r\n\r\n\r\nАннотация: действие происходит в 1920–1930-е годы в сталинской Москве и в годы жизни Иисуса Христа в Ершалаиме. Мастер с гениальным озарением описал последние дни жизни Спасителя в своём романе о Понтии Пилате, за который поплатился свободой. Его тайная возлюбленная Маргарита готова на сделку с дьяволом, лишь бы спасти Мастера. В это время некто Воланд, посетивший Москву со своей свитой, наводит ужас на обывателей.', 'https://vk.com/video_ext.php?oid=-51077316&id=456239165&hash=19b6845c2a77debe', 'seniorM27.jpg', '11'),
(28, '\"Доктор Живаго\" - Б.Л.Пастернак', '«Доктор Живаго» (1965) — эпическая драма режиссёра Дэвида Лина, созданная по мотивам одноимённого романа Бориса Пастернака.  \r\n\r\n\r\n\r\nАннотация: история о любовном треугольнике во времена политических катаклизмов. ', 'https://rutube.ru/play/embed/b95e83f55630f9f26936de43400afc32', 'seniorM28.jpg', '11'),
(29, '\"Тихий Дон\" - М.А.Шолохов', '«Тихий Дон» (1957) — советский трёхсерийный драматический художественный фильм-эпопея, снятый режиссёром Сергеем Герасимовым по одноимённому роману Михаила Шолохова.     \r\n\r\n\r\n\r\nАннотация: фильм рассказывает о судьбах людей во время Первой мировой войны (1914–1918), Октябрьской революции 1917 года и Гражданской войны в России (1917–1922), о крушении устоев и идеалов донского казачества России в начале XX века, о личной трагедии главного героя — Григория Мелехова. ', 'https://vk.com/video_ext.php?oid=-189355686&id=456242923&hash=baedcc4e28783746', 'seniorM29.jpg', '11'),
(30, '\"Василий Тёркин\" - А.Т.Твардовский', '«Василий Тёркин. Книга про бойца» — поэма Александра Твардовского, одно из главных произведений в творчестве поэта, получившее всенародное признание.     \r\n\r\n\r\n\r\nАннотация: герой поэмы, Василий Тёркин, — собирательный образ русского солдата, олицетворяющего воина-победителя, которому присущи воинская отвага, умение воевать и не унывать ни при каких обстоятельствах. ', 'https://ok.ru/videoembed/1298386193034?autoplay=1&ya=1', 'seniorM30.jpg', '11');
INSERT INTO `senior_performances` (`id`, `title`, `description`, `video_url`, `image_url`, `class`) VALUES
(1, '\"Что делать?\"(1971г.) - Н.Г.Чернышевский', '«Что делать?» (1971) — трёхсерийный телеспектакль по мотивам одноимённого романа Н. Г. Чернышевского.  ', 'https://vk.com/video_ext.php?oid=144539554&id=167383926&hash=19a871da78feecaa', 'seniorPF1.jpg', '10'),
(2, '\"Гроза\" - А.Н.Островский', '«Гроза» (1977) — телеспектакль по пьесе А. Н. Островского, телевариант спектакля Малого театра.', 'https://vk.com/video_ext.php?oid=67654076&id=160959577&hash=aaa9f3a9ecc658fc', 'seniorPF2.jpg', '10'),
(3, '\"Детство. Отрочество. Юность\" - Л.Н.Толстой', '«Детство. Отрочество. Юность» — телеспектакль 1973 года из трёх частей, созданный Петром Фоменко по одноименному произведению Льва Толстого. ', 'https://vk.com/video_ext.php?oid=268538838&id=456246290&hash=6d40791ceeea6972', 'seniorPF3.jpg', '10'),
(4, '\"На дне\" - М.Горький', '«На дне» (1972) — телеверсия спектакля по мотивам пьесы Максима Горького «На дне» в постановке Московского театра «Современник».       \n\n\n\nАннотация: в ночлежном доме собрались те, кто оказался на дне жизни — бездомные, преступники, падшие женщины. Между постоянными обитателями этого грустного места возникают отношения — романтические, напряжённые и враждебные. Когда в ночлежке появляется таинственный странник по имени Лука, обстановка заметно меняется.', 'https://ok.ru/videoembed/309799881456?autoplay=1&ya=1', 'seniorPF4.jpg', '11');
INSERT INTO `senior_poems` (`id`, `title`, `description`, `pdf_url`, `epub_url`, `fb2_url`, `txt_url`, `image_url`, `class`) VALUES
(1, '\"Кому на Руси жить хорошо\" - Н.А.Некрасов', 'Поэма «Кому на Руси жить хорошо» — главное произведение Н. А. Некрасова, вершина его творчества. Задумав поэму в 1863 году, вскоре после отмены крепостного права, поэт работал над ней с большими перерывами до последних дней.  ', 'Н. Некрасов Кому на Руси жить хорошо (1).pdf', '', '', '', 'seniorP1.jpg', '10'),
(2, '\"Двенадцать\" - А.А.Блок', '«Двенадцать» — поэма Александра Блока, написанная в январе 1918 года. Произведение стало откликом на две революции: Февральскую и Октябрьскую.      \r\n\r\n\r\n\r\nАннотация: в поэме отражён революционный Петроград, смешение слов, образов и идей — от мельчайшего до великого, от пошлого и кабацкого до божественного. ', '13.pdf', '', '', '', 'seniorP2.jpg', '11'),
(3, '\"Погорельщина\" - Н.А.Клюев', '«Погорельщина» (1928) — поэма Николая Алексеевича Клюева, одно из самых значительных произведений русской новокрестьянской поэзии.      \r\n\r\n\r\n\r\nАннотация: Клюев считал «Погорельщину» вместе с поэмой «Песнь о Великой Матери» (не законченной им из-за смерти) своими главными произведениями, говоря о них: «то, для чего я родился». ', 'Lib.ru_Классика_ Клюев Николай Алексеевич. Погорельщина.pdf', '', '', '', 'seniorP3.jpg', '11'),
(4, '\"Песнь о Великой Матери\" - Н.А.Клюев', '«Песнь о Великой Матери» — незавершённая поэма Николая Клюева, написанная между 1929 и 1934 годами.      \r\n\r\n\r\n\r\nАннотация: произведение имеет таинственный характер, в нём автор торжественно говорит о русской культуре, о своих неприятных предчувствиях, делится пророчествами о будущем страны и самом себе.  ', 'Lib.ru_Классика_ Клюев Николай Алексеевич. Песнь о великой матери.pdf', '', '', '', 'seniorP4.jpg', '11'),
(5, '\"Анна Снегина\" - С.А.Есенин', '«Анна Снегина» — автобиографическая поэма Сергея Есенина, написанная в декабре 1924 года. В основу легли воспоминания поэта о том, как он посетил родное село, о революции, о безответной любви в юности.    \r\n\r\n\r\n\r\nАннотация: поэма представляет собой не только историю любви и страданий героев, но и сложное исследование человеческой природы, социальных проблем и трагического состояния человека в современном обществе. Есенин размышляет о том, насколько сложно и противоречиво быть человеком в мире, где часто доминируют страдания и боли. ', 'Esenin_Anna_Snegina.pdf', '', '', '', 'seniorP5.jpg', '11'),
(6, '\"О дряни\" - В.В.Маяковский', '«О дряни» — стихотворение Владимира Маяковского, написанное в 1921 году. Произведение является реакцией писателя на лицемерие определённой категории граждан, не принявших идеи революции, но сумевших приспособиться к новым реалиям. ', '4b2628cd.pdf', '', '', '', 'seniorP6.jpg', '11'),
(7, '\"Реквием\" - А.А.Ахматова', '«Реквием» Анны Ахматовой — поэма, в которой описываются чувства тех, кто ждал известий о своих близких во время репрессий. Лирическая героиня говорит от имени всех женщин, стоявших вместе с ней семнадцать месяцев в тюремных очередях у ленинградских «Крестов». ', 'anna-ahmatova_rekviem.pdf', '', '', '', 'seniorP7.jpg', '11'),
(8, '\"Поэма без героя\" - А.А.Ахматова', '«Поэма без героя» Анны Ахматовой — размышления поэтессы о своём времени, о месте человека в этом мире, о его предназначении и о смысле жизни.     \r\n\r\n\r\n\r\nАннотация: в поэме автор вспоминает давно минувшую эпоху — «серебряный век», время своей молодости и литературного дебюта.', 'akhmatova_poema_bez_geroya_1978__ocr.pdf', '', '', '', 'seniorP8.jpg', '11'),
(9, '\"Страна Муравия\" - А.Т.Твардовский', '«Страна Муравия» А. Т. Твардовского — поэтическое повествование о становлении нового, социалистического сознания крестьянина-середняка, о крушении индивидуалистического, собственнического начала в душе трудового человека. ', '', 'Твардовский Александр. Страна Муравия (поэма и стихотворения) - royallib.com.epub', 'Твардовский Александр. Страна Муравия (поэма и стихотворения) - royallib.com.fb2.zip', 'Твардовский Александр. Страна Муравия (поэма и стихотворения) - royallib.com.txt.zip', 'seniorP9.jpg', '11'),
(10, '\"Василий Тёркин\" - А.Т.Твардовский', '«Василий Тёркин. Книга про бойца» — поэма Александра Твардовского, одно из главных произведений в творчестве поэта, получившее всенародное признание.     \r\n\r\n\r\n\r\nАннотация: герой поэмы, Василий Тёркин, — собирательный образ русского солдата, олицетворяющего воина-победителя, которому присущи воинская отвага, умение воевать и не унывать ни при каких обстоятельствах. ', 'Tvardovsky_Vasilij_Terkin (1).pdf', '', '', '', 'seniorP10.jpg', '11'),
(11, 'Марина Цветаева - Поэмы', 'Автор: Цветаева Марина.\r\nНазвание: Поэмы.\r\nЖанр: Поэзия: прочее.', 'poemy-marina-ivanovna-cvetaeva.pdf', 'Цветаева Марина. Поэмы - royallib.com.epub', 'Цветаева Марина. Поэмы - royallib.com.fb2.zip', '', 'seniorP11.jpg', '11');
